#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/I3DSettings.h"

//Displayanti-aliasingsupport
voidShowAntiAliasingSupport(IADLX3DAntiAliasing*antiAliasing);

//Displaycurrentanti-aliasingstate
voidGetAntiAliasingState(IADLX3DAntiAliasing*antiAliasing);

//Setanti-aliasingmode
voidSetAntiAliasingMode(IADLX3DAntiAliasing*antiAliasing,intindex);

//Setanti-aliasingmethod
voidSetAntiAliasingMethod(IADLX3DAntiAliasing*antiAliasing);

//Setanti-aliasinglevel
voidSetAntiAliasingLevel(IADLX3DAntiAliasing*antiAliasing);

//Menu
voidMainMenu();

//Menucontrol
voidMenuControl(IADLX3DAntiAliasing*antiAliasing);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//GetGPUs
IADLXGPUList*gpus=NULL;
res=sys->pVtbl->GetGPUs(sys,&gpus);

//Get3DSettingsservice
IADLX3DSettingsServices*d3dSettingSrv=NULL;
res=sys->pVtbl->Get3DSettingsServices(sys,&d3dSettingSrv);

if(ADLX_SUCCEEDED(res))
{
//GetGPUinterface
IADLXGPU*gpu=NULL;
adlx_uintindex=0;
res=gpus->pVtbl->At_GPUList(gpus,index,&gpu);

//GetAntiAliasinginterface
IADLX3DAntiAliasing*antiAliasing=NULL;
res=d3dSettingSrv->pVtbl->GetAntiAliasing(d3dSettingSrv,gpu,&antiAliasing);
if(ADLX_SUCCEEDED(res))
{
MainMenu();
MenuControl(antiAliasing);
}

//ReleasetheAntiAliasinginterface
if(antiAliasing!=NULL)
{
antiAliasing->pVtbl->Release(antiAliasing);
antiAliasing=NULL;
}

//ReleasetheGPUinterface
if(gpu!=NULL)
{
gpu->pVtbl->Release(gpu);
gpu=NULL;
}
}
else
{
printf("Failedtoget3DSettingsservices\n");
}

//Releasethe3DSettingsServiceinterface
if(d3dSettingSrv!=NULL)
{
d3dSettingSrv->pVtbl->Release(d3dSettingSrv);
d3dSettingSrv=NULL;
}

//ReleasetheGPUslistinterface
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
}
else
{
printf("ADLXinitializationfailed\n");
return0;
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

staticchar*getModeStr(ADLX_ANTI_ALIASING_MODEmode)
{
switch(mode)
{
caseAA_MODE_USE_APP_SETTINGS:
return"Useapplicationsettings";
caseAA_MODE_ENHANCE_APP_SETTINGS:
return"Enhanceapplicationsettings";
caseAA_MODE_OVERRIDE_APP_SETTINGS:
return"Overrideapplicationsettings";
default:
return"UNKNOWN";
}
};

staticchar*getMethodStr(ADLX_ANTI_ALIASING_METHODmethod)
{
switch(method)
{
caseAA_METHOD_MULTISAMPLING:
return"Muti-Sampling";
caseAA_METHOD_ADAPTIVE_MULTISAMPLING:
return"AdaptiveMuti-Sampling";
caseAA_METHOD_SUPERSAMPLING:
return"SuperSampling";
default:
return"UNKNOWN";
}
};

staticchar*getLevelStr(ADLX_ANTI_ALIASING_LEVELlevel)
{
switch(level)
{
caseAA_LEVEL_INVALID:
return"LevelInvalid";
caseAA_LEVEL_2X:
return"2X";
caseAA_LEVEL_2XEQ:
return"2XEQ";
caseAA_LEVEL_4X:
return"4X";
caseAA_LEVEL_4XEQ:
return"4XEQ";
caseAA_LEVEL_8X:
return"8X";
caseAA_LEVEL_8XEQ:
return"8XEQ";
default:
return"UNKNOWN";
}
};

voidShowAntiAliasingSupport(IADLX3DAntiAliasing*antiAliasing)
{
adlx_boolsupported=false;
ADLX_RESULTres=antiAliasing->pVtbl->IsSupported(antiAliasing,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupported:%d\n",supported);
}

voidGetAntiAliasingState(IADLX3DAntiAliasing*antiAliasing)
{
ADLX_ANTI_ALIASING_MODEmode;
ADLX_RESULTres=antiAliasing->pVtbl->GetMode(antiAliasing,&mode);
if(ADLX_SUCCEEDED(res))
printf("\tMode:%s\n",getModeStr(mode));
ADLX_ANTI_ALIASING_METHODmethod;
res=antiAliasing->pVtbl->GetMethod(antiAliasing,&method);
if(ADLX_SUCCEEDED(res))
printf("\tMethod:%s\n",getMethodStr(method));
ADLX_ANTI_ALIASING_LEVELlevel;
res=antiAliasing->pVtbl->GetLevel(antiAliasing,&level);
if(ADLX_SUCCEEDED(res))
printf("\tLevel:%s\n",getLevelStr(level));
}

voidSetAntiAliasingMode(IADLX3DAntiAliasing*antiAliasing,intindex)
{
ADLX_RESULTres=ADLX_FAIL;
ADLX_ANTI_ALIASING_MODEmode=AA_MODE_USE_APP_SETTINGS;
mode=(ADLX_ANTI_ALIASING_MODE)(mode+index);
res=antiAliasing->pVtbl->SetMode(antiAliasing,mode);
printf("\tReturncodeis:%d(0meanssuccess)\n",res);
}

voidSetAntiAliasingMethod(IADLX3DAntiAliasing*antiAliasing)
{
ADLX_ANTI_ALIASING_METHODmethod=AA_METHOD_MULTISAMPLING;
ADLX_RESULTres=antiAliasing->pVtbl->GetMethod(antiAliasing,&method);
if(ADLX_SUCCEEDED(res))
{
method=(ADLX_ANTI_ALIASING_METHOD)((method+1)%3);
res=antiAliasing->pVtbl->SetMethod(antiAliasing,method);
printf("\tSetmethod:%s,resis:%d\n",getMethodStr(method),res);
}
}

voidSetAntiAliasingLevel(IADLX3DAntiAliasing*antiAliasing)
{
ADLX_ANTI_ALIASING_LEVELlevel;
ADLX_RESULTres=antiAliasing->pVtbl->GetLevel(antiAliasing,&level);
if(ADLX_SUCCEEDED(res))
{
level=(level==AA_LEVEL_2X)?AA_LEVEL_4X:AA_LEVEL_2X;
res=antiAliasing->pVtbl->SetLevel(antiAliasing,level);
printf("\tSetlevel:%s,resis:%d\n",getLevelStr(level),res);
}
}

voidMainMenu()
{
printf("\tChoosefromthefollowingoptions:\n");

printf("\t->Press1todisplayanti-aliasingsupport\n");
printf("\t->Press2todisplaycurrentanti-aliasingstate\n");
printf("\t->Press3tosetanti-aliasingmode:Useapplicationsettings\n");
printf("\t->Press4tosetanti-aliasingmode:Enhanceapplicationsettings\n");
printf("\t->Press5tosetanti-aliasingmode:Overrideapplicationsettings\n");

printf("\t->Press6tochangeanti-aliasingmethod\n");
printf("\t->Press7tochangeanti-aliasinglevel\n");

printf("\t->PressQ/qtoquittheapplication\n");
printf("\t->PressM/mtodisplaymenuoptions\n");
}

voidMenuControl(IADLX3DAntiAliasing*antiAliasing)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displayanti-aliasingsupport
case'1':
ShowAntiAliasingSupport(antiAliasing);
break;

//Displaycurrentanti-aliasingstatus
case'2':
GetAntiAliasingState(antiAliasing);
break;

//Setanti-aliasingmode
case'3':
case'4':
case'5':
SetAntiAliasingMode(antiAliasing,num-'3');
break;

//Setanti-aliasingmethod
case'6':
SetAntiAliasingMethod(antiAliasing);
break;

//Setanti-aliasinglevel
case'7':
SetAntiAliasingLevel(antiAliasing);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}
