#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IGPUAutoTuning.h"
#include"SDK/Include/IGPUTuning.h"

//ADLXEventListener
staticHANDLEg_ADLXEvent=NULL;

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus);

//CheckGPUTuningisatfactorystatus
voidCheckGPUTuningIsAtFactoryStatus(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus);

//RestorethegivenGPUTuningtofactorystatus.
voidRestoreGPUTuningToFactoryStatus(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus);

//ShowGPUautotuningsupport
voidShowGPUAutoTuningSupport(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus);

//ShowcurrentGPUstatus
voidShowCurrentGPUStatus(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus);

//DerivefromIADLXGPUAutoTuningCompleteListener
adlx_boolADLX_STD_CALLonGPUAutoTuningCompleteEvents(IADLXGPUAutoTuningCompleteListener*pGPUAutoTuningCompleteListener,
IADLXGPUAutoTuningCompleteEvent*pGPUAutoTuningCompleteEvents);
//StartGPUautotuning
voidStartGPUAutoTuning(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus,intnum);

staticIADLXGPUAutoTuningCompleteListenerVtblvtable={&onGPUAutoTuningCompleteEvents};
staticIADLXGPUAutoTuningCompleteListenercompleteListener={&vtable};

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
//Createevent
g_ADLXEvent=CreateEvent(NULL,TRUE,FALSE,TEXT("ADLXEvent"));

//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

IADLXGPUTuningServices*gpuTuningService=NULL;
IADLXGPUList*gpus=NULL;

res=sys->pVtbl->GetGPUTuningServices(sys,&gpuTuningService);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUtuningservicesfailed",0,gpuTuningService,gpus);

res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUlistfailed",0,gpuTuningService,gpus);

//Showamenuwithoptionstochosefrom
MainMenu();

//Getthechoiceandinterpetit
MenuControl(gpuTuningService,gpus);

//Free
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
}
else
{
printf("\tg_ADLXHelpinitializefailed\n");
system("pause");
return0;
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Closeevent
if(g_ADLXEvent)
CloseHandle(g_ADLXEvent);

//Pauseforuserseetheprintout
system("pause");

return0;
}

//mainmenu
voidMainMenu()
{
printf("\tChooseonefromthefollowingoptions\n");

printf("\t->Press1tocheckGPUTuningisatfactorystatus\n");
printf("\t->Press2torestorethegivenGPUTuningtofactorystatus\n");
printf("\t->Press3toshowGPUautotuningsupport\n");
printf("\t->Press4toshowcurrentGPUstatus\n");
printf("\t->Press5tostartUndervoltGPUtuning\n");
printf("\t->Press6tostartOverclockGPUtuning\n");
printf("\t->Press7tostartOverclockVRAMtuning\n");

printf("\t->PressQ/qtoquit\n");
printf("\t->PressM/mtoshowtheoptionsagain\n");
}

//menuactioncontrol
voidMenuControl(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//CheckGPUTuningisatfactorystatus
case'1':
CheckGPUTuningIsAtFactoryStatus(gpuTuningService,gpus);
break;

//RestorethegivenGPUTuningtofactorystatus.
case'2':
RestoreGPUTuningToFactoryStatus(gpuTuningService,gpus);
break;

//ShowGPUautotuningsupport
case'3':
ShowGPUAutoTuningSupport(gpuTuningService,gpus);
break;

//ShowcurrentGPUstatus
case'4':
ShowCurrentGPUStatus(gpuTuningService,gpus);
break;

//StartUndervoltGPUtuning
case'5':
//StartOverclockGPUtuning
case'6':
//StartOverclockVRAMtuning
case'7':
StartGPUAutoTuning(gpuTuningService,gpus,num-5);
break;

//Showoptionsagain
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus)
{
//Printoutthemessageandpauseforusertoseeitthenreturnthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}

//DestroyADLX
ADLX_RESULTres=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

system("pause");
returnretCode;
}

//CheckGPUTuningisatfactorystatus
voidCheckGPUTuningIsAtFactoryStatus(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus)
{
IADLXGPU*oneGPU=NULL;
for(adlx_uintcrt=gpus->pVtbl->Begin(gpus);crt!=gpus->pVtbl->End(gpus);++crt)
{
ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,crt,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
adlx_boolisFactory=false;
res=gpuTuningService->pVtbl->IsAtFactory(gpuTuningService,oneGPU,&isFactory);
if(ADLX_SUCCEEDED(res))
{
printf("\tThe%dthGPUisatfactorystatus:%d\n",crt,isFactory);
}
else
{
printf("\tCheckthe%dthGPUTuningisatfactorystatusfailed\n",crt);
}
}
else
{
printf("\tGet%dthGPUfailed\n",crt);
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
}

//RestorethegivenGPUTuningtofactorystatus.
voidRestoreGPUTuningToFactoryStatus(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus)
{
IADLXGPU*oneGPU=NULL;
for(adlx_uintcrt=gpus->pVtbl->Begin(gpus);crt!=gpus->pVtbl->End(gpus);++crt)
{
ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,crt,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
adlx_boolisFactory=false;
res=gpuTuningService->pVtbl->ResetToFactory(gpuTuningService,oneGPU);
if(ADLX_FAILED(res))
printf("\tResetthe%dthGPUTuningtofactorystatusfailed\n",crt);
else
printf("\tResetthe%dthGPUTuningtofactorystatussuccessfully\n",crt);
}
else
{
printf("\tGet%dthGPUfailed\n",crt);
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
}

//ShowGPUautotuningsupport
voidShowGPUAutoTuningSupport(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus)
{
IADLXGPU*oneGPU=NULL;
IADLXInterface*autoTuningIfc=NULL;
IADLXGPUAutoTuning*autoTuning=NULL;
for(adlx_uintcrt=gpus->pVtbl->Begin(gpus);crt!=gpus->pVtbl->End(gpus);++crt)
{
ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,crt,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
adlx_boolsupported=false;
res=gpuTuningService->pVtbl->IsSupportedAutoTuning(gpuTuningService,oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
printf("\tThe%dthGPUdoesn'tsupportedautotuning\n",crt);
}
else
{
res=gpuTuningService->pVtbl->GetAutoTuning(gpuTuningService,oneGPU,&autoTuningIfc);
if(ADLX_FAILED(res)||autoTuningIfc==NULL)
{
printf("\tGetautotuningof%dthGPUfailed\n",crt);
}
else
{
res=autoTuningIfc->pVtbl->QueryInterface(autoTuningIfc,IID_IADLXGPUAutoTuning(),(void**)(&autoTuning));
if(ADLX_SUCCEEDED(res)&&autoTuning)
{
supported=false;
res=autoTuning->pVtbl->IsSupportedUndervoltGPU(autoTuning,&supported);
printf("\tIsthe%dthGPUsupportedundervolt:%d\n",crt,supported);
supported=false;
res=autoTuning->pVtbl->IsSupportedOverclockGPU(autoTuning,&supported);
printf("\tIsthe%dthGPUsupportedoverclock:%d\n",crt,supported);
supported=false;
res=autoTuning->pVtbl->IsSupportedOverclockVRAM(autoTuning,&supported);
printf("\tIsthe%dthGPUsupportedoverclockVRAM:%d\n",crt,supported);
}

if(autoTuning!=NULL)
{
autoTuning->pVtbl->Release(autoTuning);
autoTuning=NULL;
}
}

if(autoTuningIfc!=NULL)
{
autoTuningIfc->pVtbl->Release(autoTuningIfc);
autoTuningIfc=NULL;
}
}
}
else
{
printf("\tGet%dthGPUfailed\n",crt);
}

if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
}

//ShowcurrentGPUstatus
voidShowCurrentGPUStatus(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus)
{
IADLXGPU*oneGPU;
IADLXInterface*autoTuningIfc=NULL;
IADLXGPUAutoTuning*autoTuning=NULL;
for(adlx_uintcrt=gpus->pVtbl->Begin(gpus);crt!=gpus->pVtbl->End(gpus);++crt)
{
ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,crt,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
adlx_boolsupported=false;
res=gpuTuningService->pVtbl->IsSupportedAutoTuning(gpuTuningService,oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
printf("\tThe%dthGPUdoesn'tsupportedautotuning\n",crt);
}
else
{
res=gpuTuningService->pVtbl->GetAutoTuning(gpuTuningService,oneGPU,&autoTuningIfc);
if(ADLX_FAILED(res)||autoTuningIfc==NULL)
{
printf("\tGetautotuningof%dthGPUfailed\n",crt);
}
else
{
res=autoTuningIfc->pVtbl->QueryInterface(autoTuningIfc,IID_IADLXGPUAutoTuning(),(void**)(&autoTuning));
adlx_boolapplied=false;
if(ADLX_SUCCEEDED(res)&&autoTuning)
{
applied=false;
res=autoTuning->pVtbl->IsCurrentUndervoltGPU(autoTuning,&applied);
printf("\tIsthe%dthGPUundervoltapplied:%d\n",crt,applied);
applied=false;
res=autoTuning->pVtbl->IsCurrentOverclockGPU(autoTuning,&applied);
printf("\tIsthe%dthGPUoverclockapplied:%d\n",crt,applied);
applied=false;
res=autoTuning->pVtbl->IsCurrentOverclockVRAM(autoTuning,&applied);
printf("\tIsthe%dthGPUoverclockVRAMapplied:%d\n",crt,applied);
}

if(autoTuning!=NULL)
{
autoTuning->pVtbl->Release(autoTuning);
autoTuning=NULL;
}
}

if(autoTuningIfc!=NULL)
{
autoTuningIfc->pVtbl->Release(autoTuningIfc);
autoTuningIfc=NULL;
}
}
}
else
{
printf("\tGet%dthGPUfailed\n",crt);
}

if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
}

adlx_boolADLX_STD_CALLonGPUAutoTuningCompleteEvents(IADLXGPUAutoTuningCompleteListener*pGPUAutoTuningCompleteListener,
IADLXGPUAutoTuningCompleteEvent*pGPUAutoTuningCompleteEvent)
{
adlx_boolchange=false;
change=pGPUAutoTuningCompleteEvent->pVtbl->IsOverclockGPUCompleted(pGPUAutoTuningCompleteEvent);
printf("\tIsoverclockGPUCompleted:%d\n",change);
change=pGPUAutoTuningCompleteEvent->pVtbl->IsUndervoltGPUCompleted(pGPUAutoTuningCompleteEvent);
printf("\tIsundervoltGPUCompleted:%d\n",change);
change=pGPUAutoTuningCompleteEvent->pVtbl->IsOverclockVRAMCompleted(pGPUAutoTuningCompleteEvent);
printf("\tIsoverclockVRAMCompleted:%d\n",change);
SetEvent(g_ADLXEvent);
returntrue;
};

voidStartGPUAutoTuning(IADLXGPUTuningServices*gpuTuningService,IADLXGPUList*gpus,intnum)
{
IADLXGPU*oneGPU=NULL;
//SetthefirstGPUautotuning
ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,0,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
adlx_boolsupported=false;
res=gpuTuningService->pVtbl->IsSupportedAutoTuning(gpuTuningService,oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
printf("\tTheGPUdoesn'tsupportedautotuning\n");
}
else
{
IADLXInterface*autoTuningIfc=NULL;
res=gpuTuningService->pVtbl->GetAutoTuning(gpuTuningService,oneGPU,&autoTuningIfc);
if(ADLX_FAILED(res)||autoTuningIfc==NULL)
{
printf("\tGetautotuningofGPUfailed\n");
}
else
{
IADLXGPUAutoTuning*autoTuning=NULL;
res=autoTuningIfc->pVtbl->QueryInterface(autoTuningIfc,IID_IADLXGPUAutoTuning(),(void**)(&autoTuning));
if(ADLX_SUCCEEDED(res)&&autoTuning)
{
switch(num)
{
case'0':

res=autoTuning->pVtbl->IsSupportedUndervoltGPU(autoTuning,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
res=autoTuning->pVtbl->StartUndervoltGPU(autoTuning,&completeListener);
WaitForSingleObject(g_ADLXEvent,1000);
ResetEvent(g_ADLXEvent);
if(ADLX_SUCCEEDED(res))
printf("\tStartundervoltGPUsuccessfully\n");
else
printf("\tStartundervoltGPUfailed\n");
}
else
{
printf("\tUndervoltGPUisnotsupported\n");
}
break;
case'1':
res=autoTuning->pVtbl->IsSupportedOverclockGPU(autoTuning,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
res=autoTuning->pVtbl->StartOverclockGPU(autoTuning,&completeListener);
WaitForSingleObject(g_ADLXEvent,1000);
ResetEvent(g_ADLXEvent);
if(ADLX_SUCCEEDED(res))
printf("\tStartoverclockGPUsuccessfully\n");
else
printf("\tStartoverclockGPUfailed\n");
}
else
{
printf("\tOverclockGPUisnotsupported\n");
}
break;
case'2':
res=autoTuning->pVtbl->IsSupportedOverclockVRAM(autoTuning,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
res=autoTuning->pVtbl->StartOverclockVRAM(autoTuning,&completeListener);
WaitForSingleObject(g_ADLXEvent,1000);
ResetEvent(g_ADLXEvent);
if(ADLX_SUCCEEDED(res))
printf("\tStartoverclockVRAMsuccessfully\n");
else
printf("\tStartoverclockVRAMfailed\n");
}
else
{
printf("\tOverclockVRAMisnotsupported\n");
}
break;
default:
break;
}
}

if(autoTuning!=NULL)
{
autoTuning->pVtbl->Release(autoTuning);
autoTuning=NULL;
}
}

if(autoTuningIfc!=NULL)
{
autoTuningIfc->pVtbl->Release(autoTuningIfc);
autoTuningIfc=NULL;
}
}
}
else
{
printf("\tGetGPUfailed\n");
}

if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
