#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IGPUPresetTuning.h"
#include"SDK/Include/IGPUTuning.h"

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXGPUPresetTuning*gpuPresetTuning);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,
IADLXGPUTuningServices*gpuTuningService,
IADLXGPUList*gpus,
IADLXGPU*oneGPU,
IADLXInterface*gpuPresetTuningIfc,
IADLXGPUPresetTuning*gpuPresetTuning);

//DisplayGPUtuningsupportstates
voidShowIsSupported(IADLXGPUPresetTuning*gpuPresetTuning);

//DisplaycurrentGPUtuningstates
voidGetCurrentStates(IADLXGPUPresetTuning*gpuPresetTuning);

//SetGPUstates
voidSetGPUStates(IADLXGPUPresetTuning*gpuPresetTuning);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXGPUTuningServices*gpuTuningService=NULL;
IADLXGPUList*gpus=NULL;
IADLXGPU*oneGPU=NULL;
IADLXInterface*gpuPresetTuningIfc=NULL;
IADLXGPUPresetTuning*gpuPresetTuning=NULL;

res=sys->pVtbl->GetGPUTuningServices(sys,&gpuTuningService);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUtuningservicesfailed",0,gpuTuningService,gpus,oneGPU,
gpuPresetTuningIfc,gpuPresetTuning);

res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUlistfailed",0,gpuTuningService,gpus,oneGPU,
gpuPresetTuningIfc,gpuPresetTuning);

ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,0,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==NULL)
returnWaitAndExit("\tGetGPUfailed",0,gpuTuningService,gpus,oneGPU,
gpuPresetTuningIfc,gpuPresetTuning);

adlx_boolsupported=false;
res=gpuTuningService->pVtbl->IsSupportedPresetTuning(gpuTuningService,oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
returnWaitAndExit("\tGPUpresettuningisnotsupportedbythisGPU",0,gpuTuningService,gpus,oneGPU,
gpuPresetTuningIfc,gpuPresetTuning);

res=gpuTuningService->pVtbl->GetPresetTuning(gpuTuningService,oneGPU,&gpuPresetTuningIfc);
if(ADLX_FAILED(res)||gpuPresetTuningIfc==NULL)
returnWaitAndExit("\tGetGPUpresettuninginterfacefailed",0,gpuTuningService,gpus,oneGPU,
gpuPresetTuningIfc,gpuPresetTuning);

gpuPresetTuningIfc->pVtbl->QueryInterface(gpuPresetTuningIfc,IID_IADLXGPUPresetTuning(),(void**)&gpuPresetTuning);
if(gpuPresetTuning==NULL)
returnWaitAndExit("\tGetGPUpresettuningfailed",0,gpuTuningService,gpus,oneGPU,
gpuPresetTuningIfc,gpuPresetTuning);
//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(gpuPresetTuning);

//ReleasethegpuPresetTuninginterface
if(gpuPresetTuning!=NULL)
{
gpuPresetTuning->pVtbl->Release(gpuPresetTuning);
gpuPresetTuning=NULL;
}
if(gpuPresetTuningIfc!=NULL)
{
gpuPresetTuningIfc->pVtbl->Release(gpuPresetTuningIfc);
gpuPresetTuningIfc=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
}
else
{
printf("\tg_ADLXHelpinitializefailed\n");
system("pause");
return0;
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
printf("\tChoosefromthefollowingoptions:\n");

printf("\t->Press1todisplayGPUtuningsupportstates\n");
printf("\t->Press2todisplaycurrentGPUtuningstates\n");
printf("\t->Press3tosetGPUstates\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

voidMenuControl(IADLXGPUPresetTuning*gpuPresetTuning)
{
charnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplayGPUtuningsupportstates
case'1':
ShowIsSupported(gpuPresetTuning);
break;

//DisplaycurrentGPUtuningstates
case'2':
GetCurrentStates(gpuPresetTuning);
break;

//SetGPUstates
case'3':
SetGPUStates(gpuPresetTuning);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,
IADLXGPUTuningServices*gpuTuningService,
IADLXGPUList*gpus,
IADLXGPU*oneGPU,
IADLXInterface*gpuPresetTuningIfc,
IADLXGPUPresetTuning*gpuPresetTuning)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

if(gpuPresetTuning!=NULL)
{
gpuPresetTuning->pVtbl->Release(gpuPresetTuning);
gpuPresetTuning=NULL;
}
if(gpuPresetTuningIfc!=NULL)
{
gpuPresetTuningIfc->pVtbl->Release(gpuPresetTuningIfc);
gpuPresetTuningIfc=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
//DestroyADLX
ADLX_RESULTres=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

system("pause");
returnretCode;
}

//DisplayGPUtuningsupportstates
voidShowIsSupported(IADLXGPUPresetTuning*gpuPresetTuning)
{
adlx_boolsupported=false;
gpuPresetTuning->pVtbl->IsSupportedPowerSaver(gpuPresetTuning,&supported);
printf("\tIsPowerSavesupportedbytheGPU:%d\n",supported);
supported=false;
gpuPresetTuning->pVtbl->IsSupportedQuiet(gpuPresetTuning,&supported);
printf("\tIsQuietsupportedbytheGPU:%d\n",supported);
supported=false;
gpuPresetTuning->pVtbl->IsSupportedBalanced(gpuPresetTuning,&supported);
printf("\tIsBalancedsupportedbytheGPU:%d\n",supported);
supported=false;
gpuPresetTuning->pVtbl->IsSupportedTurbo(gpuPresetTuning,&supported);
printf("\tIsTurbosupportedbytheGPU:%d\n",supported);
supported=false;
gpuPresetTuning->pVtbl->IsSupportedRage(gpuPresetTuning,&supported);
printf("\tIsRagesupportedbytheGPU:%d\n",supported);
}

//DisplaycurrentGPUtuningstates
voidGetCurrentStates(IADLXGPUPresetTuning*gpuPresetTuning)
{
adlx_boolapplied=false;
gpuPresetTuning->pVtbl->IsCurrentPowerSaver(gpuPresetTuning,&applied);
printf("\tIsPowerSaverapplied:%d\n",applied);
applied=false;
gpuPresetTuning->pVtbl->IsCurrentQuiet(gpuPresetTuning,&applied);
printf("\tIsQuietapplied:%d\n",applied);
applied=false;
gpuPresetTuning->pVtbl->IsCurrentBalanced(gpuPresetTuning,&applied);
printf("\tIsBalancedapplied:%d\n",applied);
applied=false;
gpuPresetTuning->pVtbl->IsCurrentTurbo(gpuPresetTuning,&applied);
printf("\tIsTurboapplied:%d\n",applied);
applied=false;
gpuPresetTuning->pVtbl->IsCurrentRage(gpuPresetTuning,&applied);
printf("\tIsRageapplied:%d\n",applied);
}

//SetGPUstates
voidSetGPUStates(IADLXGPUPresetTuning*gpuPresetTuning)
{
adlx_boolapplied=false;
gpuPresetTuning->pVtbl->SetPowerSaver(gpuPresetTuning);
gpuPresetTuning->pVtbl->IsCurrentPowerSaver(gpuPresetTuning,&applied);
printf("\tSetPowerSaverpresettuning%s\n",(applied?"Successful":"failed"));
applied=false;
gpuPresetTuning->pVtbl->SetQuiet(gpuPresetTuning);
gpuPresetTuning->pVtbl->IsCurrentQuiet(gpuPresetTuning,&applied);
printf("\tSetQuietpresettuning%s\n",(applied?"Successful":"failed"));
applied=false;
gpuPresetTuning->pVtbl->SetBalanced(gpuPresetTuning);
gpuPresetTuning->pVtbl->IsCurrentBalanced(gpuPresetTuning,&applied);
printf("\tSetBalancedpresettuning%s\n",(applied?"Successful":"failed"));
applied=false;
gpuPresetTuning->pVtbl->SetTurbo(gpuPresetTuning);
gpuPresetTuning->pVtbl->IsCurrentTurbo(gpuPresetTuning,&applied);
printf("\tSetTurbopresettuning%s\n",(applied?"Successful":"failed"));
applied=false;
gpuPresetTuning->pVtbl->SetRage(gpuPresetTuning);
gpuPresetTuning->pVtbl->IsCurrentRage(gpuPresetTuning,&applied);
printf("\tSetRagepresettuning%s\n",(applied?"Successful":"failed"));
}
