#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IGPUManualFanTuning.h"
#include"SDK/Include/IGPUTuning.h"

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXManualFanTuning*manualFanTuning);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,
IADLXGPUTuningServices*gpuTuningService,
IADLXGPUList*gpus,
IADLXGPU*oneGPU,
IADLXInterface*manualFanTuningIfc,
IADLXManualFanTuning*manualFanTuning);

//Displayandsetfansettings
voidShowGetAndSetFan(IADLXManualFanTuning*manualFanTuning);

//DisplayandsetZeroRPMsettings
voidShowGetAndSetZeroRPM(IADLXManualFanTuning*manualFanTuning);

//DisplayandsetMinAcousticsettings
voidShowGetAndSetMinAcoustic(IADLXManualFanTuning*manualFanTuning);

//DisplayandsetMinFanSpeedsettings
voidShowGetAndSetMinFanSpeed(IADLXManualFanTuning*manualFanTuning);

//DisplayandsetTargetFanSpeedsettings
voidShowGetAndSetTargetFanSpeed(IADLXManualFanTuning*manualFanTuning);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXGPUTuningServices*gpuTuningService=NULL;
IADLXGPUList*gpus=NULL;
IADLXGPU*oneGPU=NULL;
IADLXInterface*manualFanTuningIfc=NULL;
IADLXManualFanTuning*manualFanTuning=NULL;

res=sys->pVtbl->GetGPUTuningServices(sys,&gpuTuningService);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUtuningservicesfailed",0,gpuTuningService,gpus,oneGPU,manualFanTuningIfc,manualFanTuning);

res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUlistfailed",0,gpuTuningService,gpus,oneGPU,manualFanTuningIfc,manualFanTuning);

res=gpus->pVtbl->At_GPUList(gpus,0,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==NULL)
returnWaitAndExit("\tGetGPUfailed",0,gpuTuningService,gpus,oneGPU,manualFanTuningIfc,manualFanTuning);

adlx_boolsupported=false;
res=gpuTuningService->pVtbl->IsSupportedManualFanTuning(gpuTuningService,oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
returnWaitAndExit("\tThisGPUdoesnotsupportmanualfantuning",0,gpuTuningService,gpus,oneGPU,manualFanTuningIfc,manualFanTuning);

res=gpuTuningService->pVtbl->GetManualFanTuning(gpuTuningService,oneGPU,&manualFanTuningIfc);
if(ADLX_FAILED(res)||manualFanTuningIfc==NULL)
returnWaitAndExit("\tGetmanualfantuninginterfacefailed",0,gpuTuningService,gpus,oneGPU,manualFanTuningIfc,manualFanTuning);

manualFanTuningIfc->pVtbl->QueryInterface(manualFanTuningIfc,IID_IADLXManualFanTuning(),(void**)&manualFanTuning);
if(manualFanTuning==NULL)
returnWaitAndExit("\tGetmanualfantuningfailed",0,gpuTuningService,gpus,oneGPU,manualFanTuningIfc,manualFanTuning);

//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(manualFanTuning);

//ReleasethemanualFanTuninginterface
if(manualFanTuning!=NULL)
{
manualFanTuning->pVtbl->Release(manualFanTuning);
manualFanTuning=NULL;
}
if(manualFanTuningIfc!=NULL)
{
manualFanTuningIfc->pVtbl->Release(manualFanTuningIfc);
manualFanTuningIfc=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
}
else
{
printf("\tg_ADLXHelpinitializefailed\n");
system("pause");
return0;
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
printf("\tChoosefromthefollowingoptions:\n");

printf("\t->Press1todisplayandsetfansetting\n");
printf("\t->Press2todisplayandsetZeroRPMsetting\n");
printf("\t->Press3todisplayandsetMinAcousticsetting\n");
printf("\t->Press4todisplayandsetMinFanSpeedsetting\n");
printf("\t->Press5todisplayandsetTargetFanSpeedsetting\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXManualFanTuning*manualFanTuning)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displayandsetfansettings
case'1':
ShowGetAndSetFan(manualFanTuning);
break;

//DisplayandsetZeroRPMsettings
case'2':
ShowGetAndSetZeroRPM(manualFanTuning);
break;

//DisplayandsetMinAcousticsettings
case'3':
ShowGetAndSetMinAcoustic(manualFanTuning);
break;

//DisplayandsetMinFanSpeedsettings
case'4':
ShowGetAndSetMinFanSpeed(manualFanTuning);
break;

//DisplayandsetTargetFanSpeedsettings
case'5':
ShowGetAndSetTargetFanSpeed(manualFanTuning);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,
IADLXGPUTuningServices*gpuTuningService,
IADLXGPUList*gpus,
IADLXGPU*oneGPU,
IADLXInterface*manualFanTuningIfc,
IADLXManualFanTuning*manualFanTuning)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

//ReleasethemanualFanTuninginterface
if(manualFanTuning!=NULL)
{
manualFanTuning->pVtbl->Release(manualFanTuning);
manualFanTuning=NULL;
}
if(manualFanTuningIfc!=NULL)
{
manualFanTuningIfc->pVtbl->Release(manualFanTuningIfc);
manualFanTuningIfc=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
//DestroyADLX
ADLX_RESULTres=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

system("pause");
returnretCode;
}

//Displayandsetfansettings
voidShowGetAndSetFan(IADLXManualFanTuning*manualFanTuning)
{
//Displaymanualfantuningranges(speed,temperature)
ADLX_IntRangefanSpeedRange,fanTemperatureRange;
ADLX_RESULTres=manualFanTuning->pVtbl->GetFanTuningRanges(manualFanTuning,&fanSpeedRange,&fanTemperatureRange);
printf("\tFanspeedrange:(%d,%d)\n",fanSpeedRange.minValue,fanSpeedRange.maxValue);
printf("\tFantemperaturerange:(%d,%d)\n",fanTemperatureRange.minValue,fanTemperatureRange.maxValue);
printf("\tReturncodeis:%d(0meanssuccess)\n",res);

//Displaycurrentfantuningstates
IADLXManualFanTuningStateList*states;
IADLXManualFanTuningState*oneState;
res=manualFanTuning->pVtbl->GetFanTuningStates(manualFanTuning,&states);
if(ADLX_SUCCEEDED(res))
{
for(adlx_uintcrt=states->pVtbl->Begin(states);crt!=states->pVtbl->End(states);++crt)
{
res=states->pVtbl->At_ManualFanTuningStateList(states,crt,&oneState);
adlx_intspeed=0,temperature=0;
oneState->pVtbl->GetFanSpeed(oneState,&speed);
oneState->pVtbl->GetTemperature(oneState,&temperature);
printf("\tThecurrent%dstate:speedis%dtemperatureis%d\n",crt,speed,temperature);
if(oneState!=NULL)
{
oneState->pVtbl->Release(oneState);
oneState=NULL;
}
}
if(states!=NULL)
{
states->pVtbl->Release(states);
states=NULL;
}
}
//Displaydefaultfantuningstates
IADLXManualFanTuning1*manualFanTuning1;
manualFanTuning->pVtbl->QueryInterface(manualFanTuning,IID_IADLXManualFanTuning1(),(void**)&manualFanTuning1);
if(manualFanTuning1)
{
res=manualFanTuning1->pVtbl->GetDefaultFanTuningStates(manualFanTuning1,&states);
if(ADLX_SUCCEEDED(res))
{
for(adlx_uintcrt=states->pVtbl->Begin(states);crt!=states->pVtbl->End(states);++crt)
{
res=states->pVtbl->At_ManualFanTuningStateList(states,crt,&oneState);
adlx_intspeed=0,temperature=0;
oneState->pVtbl->GetFanSpeed(oneState,&speed);
oneState->pVtbl->GetTemperature(oneState,&temperature);
printf("\tThedefault%dstate:speedis%dtemperatureis%d\n",crt,speed,temperature);
if(oneState!=NULL)
{
oneState->pVtbl->Release(oneState);
oneState=NULL;
}
}
if(states!=NULL)
{
states->pVtbl->Release(states);
states=NULL;
}
}
manualFanTuning1->pVtbl->Release(manualFanTuning1);
manualFanTuning1=NULL;
}

//Setemptyfantuningstates
res=manualFanTuning->pVtbl->GetEmptyFanTuningStates(manualFanTuning,&states);
if(ADLX_SUCCEEDED(res))
{
for(adlx_uintcrt=states->pVtbl->Begin(states);crt!=states->pVtbl->End(states);++crt)
{
res=states->pVtbl->At_ManualFanTuningStateList(states,crt,&oneState);
adlx_intspeed=0,temperature=0;
intfanSpeedStep=(fanSpeedRange.maxValue-fanSpeedRange.minValue)/states->pVtbl->Size(states);
intfanTemperatureStep=(fanTemperatureRange.maxValue-fanTemperatureRange.minValue)/states->pVtbl->Size(states);
oneState->pVtbl->SetFanSpeed(oneState,fanSpeedRange.minValue+fanSpeedStep*crt);
oneState->pVtbl->GetFanSpeed(oneState,&speed);
oneState->pVtbl->SetTemperature(oneState,fanTemperatureRange.minValue+fanTemperatureStep*crt);
oneState->pVtbl->GetTemperature(oneState,&temperature);
printf("\tSetempty%dstate:speedis%dtemperatureis%d\n",crt,speed,temperature);
if(oneState!=NULL)
{
oneState->pVtbl->Release(oneState);
oneState=NULL;
}
}
}

//Setemptyfantuningstatestocurrentfantuningstates
adlx_interrorIndex;
res=manualFanTuning->pVtbl->IsValidFanTuningStates(manualFanTuning,states,&errorIndex);
printf("\tIsValidGPUTuningStates,errorIndexis:%d\n",errorIndex);
if(ADLX_SUCCEEDED(res))
{
manualFanTuning->pVtbl->SetFanTuningStates(manualFanTuning,states);
}
if(states!=NULL)
{
states->pVtbl->Release(states);
states=NULL;
}
res=manualFanTuning->pVtbl->GetFanTuningStates(manualFanTuning,&states);
if(ADLX_SUCCEEDED(res))
{
printf("\tAftersetting:\n");
for(adlx_uintcrt=states->pVtbl->Begin(states);crt!=states->pVtbl->End(states);++crt)
{
res=states->pVtbl->At_ManualFanTuningStateList(states,crt,&oneState);
adlx_intspeed=0,temperature=0;
oneState->pVtbl->GetFanSpeed(oneState,&speed);
oneState->pVtbl->GetTemperature(oneState,&temperature);
printf("\tThecurrent%dstate:speedis%dtemperatureis%d\n",crt,speed,temperature);
if(oneState!=NULL)
{
oneState->pVtbl->Release(oneState);
oneState=NULL;
}
}
if(states!=NULL)
{
states->pVtbl->Release(states);
states=NULL;
}
}
}

//DisplayandsetZeroRPMsettings
voidShowGetAndSetZeroRPM(IADLXManualFanTuning*manualFanTuning)
{
adlx_boolsupported=false;
ADLX_RESULTres=manualFanTuning->pVtbl->IsSupportedZeroRPM(manualFanTuning,&supported);
printf("\tIssupportZeroRPM:%d\n",supported);
if(ADLX_FAILED(res)||!supported)
return;
adlx_boolisZeroRPMStateSet=false;
res=manualFanTuning->pVtbl->SetZeroRPMState(manualFanTuning,true);
printf("\tSetZeroRPMstate,returncodeis:%d(0meanssuccess)\n",res);
res=manualFanTuning->pVtbl->GetZeroRPMState(manualFanTuning,&isZeroRPMStateSet);
printf("\tIsZeroRPMstateset:%d,returncodeis:%d(0meanssuccess)\n",isZeroRPMStateSet,res);
res=manualFanTuning->pVtbl->SetZeroRPMState(manualFanTuning,false);
printf("\tResetZeroRPMstate,returncodeis:%d(0meanssuccess)\n",res);
res=manualFanTuning->pVtbl->GetZeroRPMState(manualFanTuning,&isZeroRPMStateSet);
printf("\tIsZeroRPMstateset:%d,returncodeis:%d(0meanssuccess)\n",isZeroRPMStateSet,res);
IADLXManualFanTuning1*manualFanTuning1;
manualFanTuning->pVtbl->QueryInterface(manualFanTuning,IID_IADLXManualFanTuning1(),(void**)&manualFanTuning1);
if(manualFanTuning1)
{
res=manualFanTuning1->pVtbl->GetDefaultZeroRPMState(manualFanTuning1,&isZeroRPMStateSet);
printf("\tDefaultZeroRPMstateis:%d,returncodeis:%d(0meanssuccess)\n",isZeroRPMStateSet,res);
//releasemanualFanTuning1
manualFanTuning1->pVtbl->Release(manualFanTuning1);
manualFanTuning1=NULL;
}
}

//DisplayandsetMinAcousticsettings
voidShowGetAndSetMinAcoustic(IADLXManualFanTuning*manualFanTuning)
{
adlx_boolsupported=false;
ADLX_RESULTres=manualFanTuning->pVtbl->IsSupportedMinAcousticLimit(manualFanTuning,&supported);
printf("\tIsMinAcousticLimitsupported:%d\n",supported);
if(ADLX_FAILED(res)||!supported)
return;

ADLX_IntRangetuningRange;
res=manualFanTuning->pVtbl->GetMinAcousticLimitRange(manualFanTuning,&tuningRange);
printf("\tMinAcousticlimitrange:(%d,%d),returncodeis:%d(0meanssuccess)\n",tuningRange.minValue,tuningRange.maxValue,res);

adlx_intminAcousticLimit;
res=manualFanTuning->pVtbl->GetMinAcousticLimit(manualFanTuning,&minAcousticLimit);
printf("\tCurrentminacousticlimit:%d,returncodeis:%d(0meanssuccess)\n",minAcousticLimit,res);
res=manualFanTuning->pVtbl->SetMinAcousticLimit(manualFanTuning,tuningRange.minValue+(tuningRange.maxValue-tuningRange.minValue)/2);
res=manualFanTuning->pVtbl->GetMinAcousticLimit(manualFanTuning,&minAcousticLimit);
printf("\tSetcurrentminacousticlimitto:%d,returncodeis:%d(0meanssuccess)\n",minAcousticLimit,res);

IADLXManualFanTuning1*manualFanTuning1;
manualFanTuning->pVtbl->QueryInterface(manualFanTuning,IID_IADLXManualFanTuning1(),(void**)&manualFanTuning1);
if(manualFanTuning1)
{
res=manualFanTuning1->pVtbl->GetMinAcousticLimitDefault(manualFanTuning1,&minAcousticLimit);
printf("\tDefaultminacousticlimitis:%d,returncodeis:%d(0meanssuccess)\n",minAcousticLimit,res);
//releasemanualFanTuning1
manualFanTuning1->pVtbl->Release(manualFanTuning1);
manualFanTuning1=NULL;
}
}

//DisplayandsetMinFanSpeedsettings
voidShowGetAndSetMinFanSpeed(IADLXManualFanTuning*manualFanTuning)
{
adlx_boolsupported=false;
ADLX_RESULTres=manualFanTuning->pVtbl->IsSupportedMinFanSpeed(manualFanTuning,&supported);
printf("\tIsMinFanSpeedsupported:%d\n",supported);
if(ADLX_FAILED(res)||!supported)
return;

ADLX_IntRangetuningRange;
res=manualFanTuning->pVtbl->GetMinFanSpeedRange(manualFanTuning,&tuningRange);
printf("\tMinFanSpeedrange:(%d,%d),returncodeis:%d(0meanssuccess)\n",tuningRange.minValue,tuningRange.maxValue,res);

adlx_intminFanSpeed=0;
res=manualFanTuning->pVtbl->GetMinFanSpeed(manualFanTuning,&minFanSpeed);
printf("\tCurrentMinFanSpeed:%d,returncodeis:%d(0meanssuccess)\n",minFanSpeed,res);
res=manualFanTuning->pVtbl->SetMinFanSpeed(manualFanTuning,tuningRange.minValue+(tuningRange.maxValue-tuningRange.minValue)/2);
res=manualFanTuning->pVtbl->GetMinFanSpeed(manualFanTuning,&minFanSpeed);
printf("\tSetcurrentMinFanSpeedto:%d,returncodeis:%d(0meanssuccess)\n",minFanSpeed,res);

IADLXManualFanTuning1*manualFanTuning1;
manualFanTuning->pVtbl->QueryInterface(manualFanTuning,IID_IADLXManualFanTuning1(),(void**)&manualFanTuning1);
if(manualFanTuning1)
{
res=manualFanTuning1->pVtbl->GetMinFanSpeedDefault(manualFanTuning1,&minFanSpeed);
printf("\tDefaultMinFanSpeedis:%d,returncodeis:%d(0meanssuccess)\n",minFanSpeed,res);
//releasemanualFanTuning1
manualFanTuning1->pVtbl->Release(manualFanTuning1);
manualFanTuning1=NULL;
}
}

//DisplayandsetTargetFanSpeedsettings
