#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IGPUManualGFXTuning.h"
#include"SDK/Include/IGPUTuning.h"

//Mainmenu
voidMainMenu(IADLXManualGraphicsTuning1*manualGFXTuning1,IADLXManualGraphicsTuning2*manualGFXTuning2);

//Menuactioncontrol
voidMenuControl(IADLXManualGraphicsTuning1*manualGFXTuning1,IADLXManualGraphicsTuning2*manualGFXTuning2);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,
IADLXGPUTuningServices*gpuTuningService,
IADLXGPUList*gpus,
IADLXGPU*oneGPU,
IADLXInterface*manualGFXTuningIfc,
IADLXManualGraphicsTuning1*manualGFXTuning1,
IADLXManualGraphicsTuning2*manualGFXTuning2);

/*****Thefirstispre-Navi,thesecondispost-Navi*****/
//DisplayGPUfrequencyandvoltagerange
voidShowFrequencyAndVoltageRange1(IADLXManualGraphicsTuning1*manualGFXTuning1);
voidShowFrequencyAndVoltageRange2(IADLXManualGraphicsTuning2*manualGFXTuning2);
//DisplaycurrentGPUtuningstates
voidGetCurrentStates1(IADLXManualGraphicsTuning1*manualGFXTuning1);
voidGetCurrentStates2(IADLXManualGraphicsTuning2*manualGFXTuning2);
//SetGPUstates
voidSetGPUStates1(IADLXManualGraphicsTuning1*manualGFXTuning1);
voidSetGPUStates2(IADLXManualGraphicsTuning2*manualGFXTuning2);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXGPUTuningServices*gpuTuningService=NULL;
IADLXGPUList*gpus=NULL;
IADLXGPU*oneGPU=NULL;
IADLXInterface*manualGFXTuningIfc=NULL;
IADLXManualGraphicsTuning1*manualGFXTuning1=NULL;
IADLXManualGraphicsTuning2*manualGFXTuning2=NULL;

res=sys->pVtbl->GetGPUTuningServices(sys,&gpuTuningService);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUtuningservicesfailed",0,gpuTuningService,gpus,oneGPU,
manualGFXTuningIfc,manualGFXTuning1,manualGFXTuning2);

res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUlistfailed",0,gpuTuningService,gpus,oneGPU,
manualGFXTuningIfc,manualGFXTuning1,manualGFXTuning2);

ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,0,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==NULL)
returnWaitAndExit("\tGetGPUfailed",0,gpuTuningService,gpus,oneGPU,
manualGFXTuningIfc,manualGFXTuning1,manualGFXTuning2);

adlx_boolsupported=false;
res=gpuTuningService->pVtbl->IsSupportedManualGFXTuning(gpuTuningService,oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
returnWaitAndExit("\tThisGPUdoesnotsupportmanualgraphicstuning",0,gpuTuningService,gpus,oneGPU,
manualGFXTuningIfc,manualGFXTuning1,manualGFXTuning2);

res=gpuTuningService->pVtbl->GetManualGFXTuning(gpuTuningService,oneGPU,&manualGFXTuningIfc);
if(ADLX_FAILED(res)||manualGFXTuningIfc==NULL)
returnWaitAndExit("\tGetmanualgraphicstuninginterfacefailed",0,gpuTuningService,gpus,oneGPU,
manualGFXTuningIfc,manualGFXTuning1,manualGFXTuning2);

//Pre-NaviASIC
manualGFXTuningIfc->pVtbl->QueryInterface(manualGFXTuningIfc,IID_IADLXManualGraphicsTuning1(),(void**)&manualGFXTuning1);
//Post-NaviASIC
manualGFXTuningIfc->pVtbl->QueryInterface(manualGFXTuningIfc,IID_IADLXManualGraphicsTuning2(),(void**)&manualGFXTuning2);

if(manualGFXTuning1==NULL&&manualGFXTuning2==NULL)
returnWaitAndExit("\tGetmanualgraphicstuningfailed",0,gpuTuningService,gpus,oneGPU,
manualGFXTuningIfc,manualGFXTuning1,manualGFXTuning2);
//Displaymainmenuoptions
MainMenu(manualGFXTuning1,manualGFXTuning2);

//Getandexecutethechoice
MenuControl(manualGFXTuning1,manualGFXTuning2);

//ReleasethemanualGFXTuning1interface
if(manualGFXTuning1!=NULL)
{
manualGFXTuning1->pVtbl->Release(manualGFXTuning1);
manualGFXTuning1=NULL;
}
if(manualGFXTuning2!=NULL)
{
manualGFXTuning2->pVtbl->Release(manualGFXTuning2);
manualGFXTuning2=NULL;
}
if(manualGFXTuningIfc!=NULL)
{
manualGFXTuningIfc->pVtbl->Release(manualGFXTuningIfc);
manualGFXTuningIfc=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
}
else
{
printf("\tg_ADLXHelpinitializefailed\n");
system("pause");
return0;
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu(IADLXManualGraphicsTuning1*manualGFXTuning1,IADLXManualGraphicsTuning2*manualGFXTuning2)
{
if(manualGFXTuning1)
printf("\tThecurrentGPUispre-Navi.Choosefromthefollowingoptions:\n");
elseif(manualGFXTuning2)
printf("\tThecurrentGPUispost-Navi.Choosefromthefollowingoptions:\n");
printf("\t->Press1todisplayGPUfrequencyandvoltage\n");
printf("\t->Press2todisplaycurrentGPUstates\n");
printf("\t->Press3tosetGPUstates\n");
printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

voidMenuControl(IADLXManualGraphicsTuning1*manualGFXTuning1,IADLXManualGraphicsTuning2*manualGFXTuning2)
{
charnum=0;
while((num=getchar())!='q'&&num!='Q')
{
//Pre-NaviMenuactioncontrol
if(manualGFXTuning1)
{
switch(num)
{
//DisplayGPUfrequencyandvoltagerange
case'1':
ShowFrequencyAndVoltageRange1(manualGFXTuning1);
break;

//DisplaycurrentGPUtuningstates
case'2':
GetCurrentStates1(manualGFXTuning1);
break;

//SetGPUstates
case'3':
SetGPUStates1(manualGFXTuning1);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu(manualGFXTuning1,manualGFXTuning2);
break;
default:
break;
}
}
//Post-NaviMenuactioncontrol
else
{
switch(num)
{
//DisplayGPUfrequencyandvoltagerange
case'1':
ShowFrequencyAndVoltageRange2(manualGFXTuning2);
break;

//DisplaycurrentGPUtuningstates
case'2':
GetCurrentStates2(manualGFXTuning2);
break;

//SetGPUstates
case'3':
SetGPUStates2(manualGFXTuning2);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu(manualGFXTuning1,manualGFXTuning2);
break;
default:
break;
}
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,
IADLXGPUTuningServices*gpuTuningService,
IADLXGPUList*gpus,
IADLXGPU*oneGPU,
IADLXInterface*manualGFXTuningIfc,
IADLXManualGraphicsTuning1*manualGFXTuning1,
IADLXManualGraphicsTuning2*manualGFXTuning2)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

//ReleasethemanualGFXTuninginterface
if(manualGFXTuning1!=NULL)
{
manualGFXTuning1->pVtbl->Release(manualGFXTuning1);
manualGFXTuning1=NULL;
}
if(manualGFXTuning2!=NULL)
{
manualGFXTuning2->pVtbl->Release(manualGFXTuning2);
manualGFXTuning2=NULL;
}
if(manualGFXTuningIfc!=NULL)
{
manualGFXTuningIfc->pVtbl->Release(manualGFXTuningIfc);
manualGFXTuningIfc=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
//DestroyADLX
ADLX_RESULTres=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

system("pause");
returnretCode;
}

//DisplayGPUfrequencyandvoltagerange
voidShowFrequencyAndVoltageRange1(IADLXManualGraphicsTuning1*manualGFXTuning1)
{
ADLX_IntRangefreqRange,voltRange;
ADLX_RESULTres=manualGFXTuning1->pVtbl->GetGPUTuningRanges(manualGFXTuning1,&freqRange,&voltRange);
printf("\tFrequencyrange:(%d,%d),returncodeis:%d(0meanssuccess)\n",freqRange.minValue,freqRange.maxValue,res);
printf("\tVoltagerange:(%d,%d),returncodeis:%d(0meanssuccess)\n",voltRange.minValue,voltRange.maxValue,res);
}

//DisplaycurrentGPUtuningstates
voidGetCurrentStates1(IADLXManualGraphicsTuning1*manualGFXTuning1)
{
IADLXManualTuningStateList*states;
IADLXManualTuningState*oneState;
ADLX_RESULTres=manualGFXTuning1->pVtbl->GetGPUTuningStates(manualGFXTuning1,&states);
if(ADLX_SUCCEEDED(res))
{
for(adlx_uintcrt=states->pVtbl->Begin(states);crt!=states->pVtbl->End(states);++crt)
{
states->pVtbl->At_ManualTuningStateList(states,crt,&oneState);
adlx_intfreq=0,volt=0;
res=oneState->pVtbl->GetFrequency(oneState,&freq);
printf("\tThecurrentstate%d:frequencyis%d,returncodeis:%d(0meanssuccess)\n",crt,freq,res);
res=oneState->pVtbl->GetVoltage(oneState,&volt);
printf("\tThecurrentstate%d:voltageis%d,returncodeis:%d(0meanssuccess)\n",crt,volt,res);
if(oneState!=NULL)
{
oneState->pVtbl->Release(oneState);
oneState=NULL;
}
}
}
if(states!=NULL)
{
states->pVtbl->Release(states);
states=NULL;
}
}

//SetGPUstates
voidSetGPUStates1(IADLXManualGraphicsTuning1*manualGFXTuning1)
{
IADLXManualTuningStateList*states;
IADLXManualTuningState*oneState;
ADLX_RESULTres1=manualGFXTuning1->pVtbl->GetEmptyGPUTuningStates(manualGFXTuning1,&states);
ADLX_IntRangefreqRange,voltRange;
ADLX_RESULTres2=manualGFXTuning1->pVtbl->GetGPUTuningRanges(manualGFXTuning1,&freqRange,&voltRange);
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
for(adlx_uintcrt=states->pVtbl->Begin(states);crt!=states->pVtbl->End(states);++crt)
{
states->pVtbl->At_ManualTuningStateList(states,crt,&oneState);
adlx_intfreq=0,volt=0;
intfreqStep=(freqRange.maxValue-freqRange.minValue)/(states->pVtbl->Size(states));
//TheStepshouldnotbetoolarge
if(freqStep>=60)
freqStep=60;
intvoltStep=(voltRange.maxValue-voltRange.minValue)/(states->pVtbl->Size(states));
//TheStepshouldnotbetoolarge
if(voltStep>=20)
voltStep=20;
ADLX_RESULTres=oneState->pVtbl->SetFrequency(oneState,freqRange.minValue+freqStep*crt);
res=oneState->pVtbl->GetFrequency(oneState,&freq);
res=oneState->pVtbl->SetVoltage(oneState,voltRange.minValue+voltStep*crt);
res=oneState->pVtbl->GetVoltage(oneState,&volt);
printf("\tSetemptystate%d:frequencyis%d,voltageis%d,returncodeis:%d(0meanssuccess)\n",crt,freq,volt,res);
if(oneState!=NULL)
{
oneState->pVtbl->Release(oneState);
oneState=NULL;
}
}
}
adlx_interrorIndex;
ADLX_RESULTres=manualGFXTuning1->pVtbl->IsValidGPUTuningStates(manualGFXTuning1,states,&errorIndex);
printf("\tIsValidGPUTuningStates,errorIndexis:%d,returncodeis:%d(0meanssuccess)\n",errorIndex,res);
if(ADLX_SUCCEEDED(res))
{
res=manualGFXTuning1->pVtbl->SetGPUTuningStates(manualGFXTuning1,states);
printf("\tSetGPUtuningstates%s\n",(ADLX_SUCCEEDED(res)?"succeeded":"failed"));
}
if(states!=NULL)
{
states->pVtbl->Release(states);
states=NULL;
}

res=manualGFXTuning1->pVtbl->GetGPUTuningStates(manualGFXTuning1,&states);
printf("\tAftersetting:\n");
if(ADLX_SUCCEEDED(res))
{
for(adlx_uintcrt=states->pVtbl->Begin(states);crt!=states->pVtbl->End(states);++crt)
{
states->pVtbl->At_ManualTuningStateList(states,crt,&oneState);
adlx_intfreq=0,volt=0;
res=oneState->pVtbl->GetFrequency(oneState,&freq);
res=oneState->pVtbl->GetVoltage(oneState,&volt);
printf("\tThecurrentstate%d:frequencyis%d,voltageis%d,returncodeis:%d(0meanssuccess)\n",crt,freq,volt,res);
if(oneState!=NULL)
{
oneState->pVtbl->Release(oneState);
oneState=NULL;
}
}
}
if(states!=NULL)
{
states->pVtbl->Release(states);
states=NULL;
}
}

//DisplayGPUfrequencyandvoltagerange
voidShowFrequencyAndVoltageRange2(IADLXManualGraphicsTuning2*manualGFXTuning2)
{
ADLX_IntRangefreqRange,voltRange;
ADLX_RESULTres=manualGFXTuning2->pVtbl->GetGPUMinFrequencyRange(manualGFXTuning2,&freqRange);
printf("\tGPUminimumfrequencyrange:(%d,%d),returncodeis:%d(0meanssuccess)\n",freqRange.minValue,freqRange.maxValue,res);
res=manualGFXTuning2->pVtbl->GetGPUMaxFrequencyRange(manualGFXTuning2,&freqRange);
printf("\tGPUmaximumfrequencyrange:(%d,%d),returncodeis:%d(0meanssuccess)\n",freqRange.minValue,freqRange.maxValue,res);
res=manualGFXTuning2->pVtbl->GetGPUVoltageRange(manualGFXTuning2,&voltRange);
printf("\tVoltagerange:(%d,%d),returncodeis:%d(0meanssuccess)\n",voltRange.minValue,voltRange.maxValue,res);
}

//DisplaycurrentGPUtuningstates
voidGetCurrentStates2(IADLXManualGraphicsTuning2*manualGFXTuning2)
{
adlx_intminFreq=0,maxFreq=0,volt=0;
ADLX_RESULTres=manualGFXTuning2->pVtbl->GetGPUMinFrequency(manualGFXTuning2,&minFreq);
printf("\tCurrentGPUminimumfrequency:%d,returncodeis:%d(0meanssuccess)\n",minFreq,res);
res=manualGFXTuning2->pVtbl->GetGPUMaxFrequency(manualGFXTuning2,&maxFreq);
printf("\tCurrentGPUmaximumfrequency:%d,returncodeis:%d(0meanssuccess)\n",maxFreq,res);
res=manualGFXTuning2->pVtbl->GetGPUVoltage(manualGFXTuning2,&volt);
printf("\tCurrentGPUclockvoltage:%d,returncodeis:%d(0meanssuccess)\n",volt,res);
}

//SetGPUstates
voidSetGPUStates2(IADLXManualGraphicsTuning2*manualGFXTuning2)
{
ADLX_IntRangefreqRange,voltRange;
ADLX_RESULTres=manualGFXTuning2->pVtbl->GetGPUMinFrequencyRange(manualGFXTuning2,&freqRange);
res=manualGFXTuning2->pVtbl->GetGPUVoltageRange(manualGFXTuning2,&voltRange);
res=manualGFXTuning2->pVtbl->SetGPUMinFrequency(manualGFXTuning2,freqRange.minValue);
printf("\tSetGPUminfrequency%s\n",(ADLX_SUCCEEDED(res)?"succeeded":"failed"));
res=manualGFXTuning2->pVtbl->SetGPUMaxFrequency(manualGFXTuning2,freqRange.minValue+100);
printf("\tSetGPUmaxfrequency%s\n",(ADLX_SUCCEEDED(res)?"succeeded":"failed"));
res=manualGFXTuning2->pVtbl->SetGPUVoltage(manualGFXTuning2,voltRange.minValue+(voltRange.maxValue-voltRange.minValue)/2);
printf("\tSetGPUvoltage%s\n",(ADLX_SUCCEEDED(res)?"succeeded":"failed"));
printf("\tAftersetting:\n");
adlx_intminFreq=0,maxFreq=0,volt=0;
res=manualGFXTuning2->pVtbl->GetGPUMinFrequency(manualGFXTuning2,&minFreq);
printf("\tCurrentGPUminfrequency:%d,returncodeis:%d(0meanssuccess)\n",minFreq,res);
res=manualGFXTuning2->pVtbl->GetGPUMaxFrequency(manualGFXTuning2,&maxFreq);
printf("\tCurrentGPUmaxfrequency:%d,returncodeis:%d(0meanssuccess)\n",maxFreq,res);
res=manualGFXTuning2->pVtbl->GetGPUVoltage(manualGFXTuning2,&volt);
printf("\tCurrentGPUclockvoltage:%d,returncodeis:%d(0meanssuccess)\n",volt,res);
}
