#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IGPUManualPowerTuning.h"
#include"SDK/Include/IGPUTuning.h"

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXManualPowerTuning*manualPowerTuning);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,
IADLXGPUTuningServices*gpuTuningService,
IADLXGPUList*gpus,
IADLXGPU*oneGPU,
IADLXInterface*manualPowerTuningIfc,
IADLXManualPowerTuning*manualPowerTuning);

//Displaypowerlimitrange
voidShowGetPowerLimitRange(IADLXManualPowerTuning*manualPowerTuning);

//Displaycurrentpowerlimit
voidShowGetPowerLimit(IADLXManualPowerTuning*manualPowerTuning);

//Setpowerlimit
voidShowSetPowerLimit(IADLXManualPowerTuning*manualPowerTuning);

//ShowtocheckTDClimitissupported
voidShowTDCLimitSupported(IADLXManualPowerTuning*manualPowerTuning);

//ShowhowtogetTDClimitrange.
voidShowGetTDCLimitRange(IADLXManualPowerTuning*manualPowerTuning);

//ShowhowtogetcurrentTDClimit.
voidShowGetTDCLimit(IADLXManualPowerTuning*manualPowerTuning);

//ShowhowtosetTDClimit.
voidShowSetTDCLimit(IADLXManualPowerTuning*manualPowerTuning);


intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXGPUTuningServices*gpuTuningService=NULL;
IADLXGPUList*gpus=NULL;
IADLXGPU*oneGPU=NULL;
IADLXInterface*manualPowerTuningIfc=NULL;
IADLXManualPowerTuning*manualPowerTuning=NULL;

res=sys->pVtbl->GetGPUTuningServices(sys,&gpuTuningService);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUtuningservicesfailed",0,gpuTuningService,gpus,oneGPU,manualPowerTuningIfc,manualPowerTuning);

res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_FAILED(res))
returnWaitAndExit("\tGetGPUlistfailed",0,gpuTuningService,gpus,oneGPU,manualPowerTuningIfc,manualPowerTuning);

res=gpus->pVtbl->At_GPUList(gpus,0,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==NULL)
returnWaitAndExit("\tGetGPUfailed",0,gpuTuningService,gpus,oneGPU,manualPowerTuningIfc,manualPowerTuning);

adlx_boolsupported=false;
res=gpuTuningService->pVtbl->IsSupportedManualPowerTuning(gpuTuningService,oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
returnWaitAndExit("\tThisGPUdoesn'tsupportedmanualpowertuning",0,gpuTuningService,gpus,oneGPU,manualPowerTuningIfc,manualPowerTuning);

res=gpuTuningService->pVtbl->GetManualPowerTuning(gpuTuningService,oneGPU,&manualPowerTuningIfc);
if(ADLX_FAILED(res)||manualPowerTuningIfc==NULL)
returnWaitAndExit("\tGetmanualpowertuninginterfacefailed",0,gpuTuningService,gpus,oneGPU,manualPowerTuningIfc,manualPowerTuning);

res=manualPowerTuningIfc->pVtbl->QueryInterface(manualPowerTuningIfc,IID_IADLXManualPowerTuning(),(void**)&manualPowerTuning);
if(manualPowerTuning==NULL)
returnWaitAndExit("\tGetmanualpowertuningfailed",0,gpuTuningService,gpus,oneGPU,manualPowerTuningIfc,manualPowerTuning);

//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(manualPowerTuning);

//ReleasethemanualPowerTuninginterface
if(manualPowerTuning!=NULL)
{
manualPowerTuning->pVtbl->Release(manualPowerTuning);
manualPowerTuning=NULL;
}
if(manualPowerTuningIfc!=NULL)
{
manualPowerTuningIfc->pVtbl->Release(manualPowerTuningIfc);
manualPowerTuningIfc=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
}
else
{
printf("\tg_ADLXHelpinitializefailed\n");
system("pause");
return0;
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
printf("\tChoosefromthefollowingoptions:\n");
printf("\t->Press1todisplaypowerlimitrange\n");
printf("\t->Press2todisplaycurrentpowerlimit\n");
printf("\t->Press3tosetpowerlimit\n");
printf("\t->Press4tocheckifTDCLimitissupported\n");
printf("\t->Press5todisplayTDClimitrange\n");
printf("\t->Press6todisplaycurrentTDClimit\n");
printf("\t->Press7tosetTDClimit\n");
printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXManualPowerTuning*manualPowerTuning)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displaypowerlimitrange
case'1':
ShowGetPowerLimitRange(manualPowerTuning);
break;

//Displaycurrentpowerlimit
case'2':
ShowGetPowerLimit(manualPowerTuning);
break;

//Setpowerlimit
case'3':
ShowSetPowerLimit(manualPowerTuning);
break;
//ShowhowtocheckTDClimitissupported.
case'4':
ShowTDCLimitSupported(manualPowerTuning);
break;
//ShowhowtogetTDClimitrange.
case'5':
ShowGetTDCLimitRange(manualPowerTuning);
break;
//ShowhowtogetcurrentTDClimit.
case'6':
ShowGetTDCLimit(manualPowerTuning);
break;
//ShowhowtosetTDClimit.
case'7':
ShowSetTDCLimit(manualPowerTuning);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode,
IADLXGPUTuningServices*gpuTuningService,
IADLXGPUList*gpus,
IADLXGPU*oneGPU,
IADLXInterface*manualPowerTuningIfc,
IADLXManualPowerTuning*manualPowerTuning)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

//ReleasethemanualPowerTuninginterface
if(manualPowerTuning!=NULL)
{
manualPowerTuning->pVtbl->Release(manualPowerTuning);
manualPowerTuning=NULL;
}
if(manualPowerTuningIfc!=NULL)
{
manualPowerTuningIfc->pVtbl->Release(manualPowerTuningIfc);
manualPowerTuningIfc=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}

//DestroyADLX
ADLX_RESULTres=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

system("pause");
returnretCode;
}

//Displaypowerlimitrange
voidShowGetPowerLimitRange(IADLXManualPowerTuning*manualPowerTuning)
{
ADLX_IntRangepowerRange;
ADLX_RESULTres=manualPowerTuning->pVtbl->GetPowerLimitRange(manualPowerTuning,&powerRange);
printf("\tPowerlimitrange:(%d,%d),returncodeis:%d(0meanssuccess)\n",powerRange.minValue,powerRange.maxValue,res);
}

//Displaycurrentpowerlimit
voidShowGetPowerLimit(IADLXManualPowerTuning*manualPowerTuning)
{
adlx_intpowerLimit;
ADLX_RESULTres=manualPowerTuning->pVtbl->GetPowerLimit(manualPowerTuning,&powerLimit);
printf("\tCurrentpowerlimit:%d,returncodeis:%d(0meanssuccess)\n",powerLimit,res);
}

//Setpowerlimit
voidShowSetPowerLimit(IADLXManualPowerTuning*manualPowerTuning)
{
ADLX_IntRangepowerRange;
ADLX_RESULTres=manualPowerTuning->pVtbl->GetPowerLimitRange(manualPowerTuning,&powerRange);
res=manualPowerTuning->pVtbl->SetPowerLimit(manualPowerTuning,powerRange.minValue+(powerRange.maxValue-powerRange.minValue)/2);
printf("\tSetpowerlimit%s\n",(ADLX_SUCCEEDED(res)?"succeeded":"failed"));
adlx_intpowerLimit;
res=manualPowerTuning->pVtbl->GetPowerLimit(manualPowerTuning,&powerLimit);
printf("\tSetcurrentpowerlimitto:%d,returncodeis:%d(0meanssuccess)\n",powerLimit,res);
}


//ShowhowtocheckTDCLimitissupported.
voidShowTDCLimitSupported(IADLXManualPowerTuning*manualPowerTuning)
{
adlx_booltdcSupported;
ADLX_RESULTres=manualPowerTuning->pVtbl->IsSupportedTDCLimit(manualPowerTuning,&tdcSupported);
printf("\tIsTDCLimitsupported:%d,returncodeis:%d(0meanssuccess)\n",tdcSupported,res);
}

//ShowhowtogetTDClimitrange.
voidShowGetTDCLimitRange(IADLXManualPowerTuning*manualPowerTuning)
{
ADLX_IntRangetdcRange;
ADLX_RESULTres=manualPowerTuning->pVtbl->GetTDCLimitRange(manualPowerTuning,&tdcRange);
printf("\tGetTDClimitrangeis:(%d,%d),returncodeis:%d(0meanssuccess)\n",tdcRange.minValue,tdcRange.maxValue,res);
}

//ShowhowtogetcurrentTDClimit.
voidShowGetTDCLimit(IADLXManualPowerTuning*manualPowerTuning)
{
adlx_inttdcLimit;
ADLX_RESULTres=manualPowerTuning->pVtbl->GetTDCLimit(manualPowerTuning,&tdcLimit);
printf("\tThecurrentTDClimitis:%d,returncodeis:%d(0meanssuccess)\n",tdcLimit,res);
}

//ShowhowtosetTDClimit.
voidShowSetTDCLimit(IADLXManualPowerTuning*manualPowerTuning)
{
ADLX_IntRangetdcRange;
ADLX_RESULTres=manualPowerTuning->pVtbl->GetTDCLimitRange(manualPowerTuning,&tdcRange);
res=manualPowerTuning->pVtbl->SetTDCLimit(manualPowerTuning,tdcRange.minValue+(tdcRange.maxValue-tdcRange.minValue)/2);
adlx_inttdcLimit;
res=manualPowerTuning->pVtbl->GetTDCLimit(manualPowerTuning,&tdcLimit);
printf("\tSetcurrentTDClimitto:%d,returncodeis:%d(0meanssuccess)\n",tdcLimit,res);
}
