#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IGPUManualVRAMTuning.h"
#include"SDK/Include/IGPUTuning.h"

//DisplayMemoryTimingsupport
voidShowSupport(IADLXInterface*vramTuningIfc);

//GetVRAMstate
voidGetState(IADLXInterface*vramTuningIfc);

//SetMemoryTimingLevel
voidSetTimingLevel(IADLXInterface*vramTuningIfc);

//SetVRAMstate
voidSetState(IADLXInterface*vramTuningIfc);

//Mainmenu
voidMainMenu(IADLXInterface*vramTuningIfc);

//Menuactioncontrol
voidMenuControl(IADLXInterface*vramTuningIfc);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
//GetSystemServices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//GetGPUs
IADLXGPUList*gpus=NULL;
res=sys->pVtbl->GetGPUs(sys,&gpus);

//GetGPUtuningservice
IADLXGPUTuningServices*gpuTuningService=NULL;
res=sys->pVtbl->GetGPUTuningServices(sys,&gpuTuningService);

if(ADLX_SUCCEEDED(res))
{
//GetGPUinterface
IADLXGPU*gpu=NULL;
adlx_uintindex=0;
res=gpus->pVtbl->At_GPUList(gpus,index,&gpu);

//GetManualVRAMTuningsupport
adlx_boolsupported;
gpuTuningService->pVtbl->IsSupportedManualVRAMTuning(gpuTuningService,gpu,&supported);
printf("\tTheGPUsupportformanualVRAMtuningis:%d\n",supported);

//GetManualVRAMTuninginterface
IADLXInterface*vramTuningIfc=NULL;
res=gpuTuningService->pVtbl->GetManualVRAMTuning(gpuTuningService,gpu,&vramTuningIfc);
if(ADLX_SUCCEEDED(res))
{
MainMenu(vramTuningIfc);
MenuControl(vramTuningIfc);
}

//ReleasethevramTuningIfcinterface
if(vramTuningIfc!=NULL)
{
vramTuningIfc->pVtbl->Release(vramTuningIfc);
vramTuningIfc=NULL;
}

//ReleasetheGPUinterface
if(gpu!=NULL)
{
gpu->pVtbl->Release(gpu);
gpu=NULL;
}
}
else
{
printf("FailedtogetGPUtuningservice\n");
}

//ReleasethegpuTuningServiceinterface
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}

//ReleasetheGPUslistinterface
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
}
else
{
printf("ADLXinitializationfailed\n");
return0;
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

staticconstchar*getDescStr(ADLX_MEMORYTIMING_DESCRIPTIONdesc)
{
switch(desc)
{
caseMEMORYTIMING_DEFAULT:
return"Default";
caseMEMORYTIMING_FAST_TIMING:
return"FastTiming";
caseMEMORYTIMING_FAST_TIMING_LEVEL_2:
return"FastTimingLevel2";
caseMEMORYTIMING_AUTOMATIC:
return"Automatic";
caseMEMORYTIMING_MEMORYTIMING_LEVEL_1:
return"MemoryTimingLevel1";
caseMEMORYTIMING_MEMORYTIMING_LEVEL_2:
return"MemoryTimingLevel2";
}
return"Default";
};

voidShowSupport(IADLXInterface*vramTuningIfc)
{
adlx_boolsupported;
IADLXManualVRAMTuning2*vramTuning2=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning2(),&vramTuning2);
IADLXManualVRAMTuning1*vramTuning1=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning1(),&vramTuning1);
if(vramTuning2)
{
vramTuning2->pVtbl->IsSupportedMemoryTiming(vramTuning2,&supported);
}
elseif(vramTuning1)
{
vramTuning1->pVtbl->IsSupportedMemoryTiming(vramTuning1,&supported);
}

printf("\tIsSupported:%d\n",supported);

//ReleasetheManualVRAMTuning2interface
if(vramTuning2!=NULL)
{
vramTuning2->pVtbl->Release(vramTuning2);
vramTuning2=NULL;
}

//ReleasetheManualVRAMTuning1interface
if(vramTuning1!=NULL)
{
vramTuning1->pVtbl->Release(vramTuning1);
vramTuning1=NULL;
}
}

voidGetState(IADLXInterface*vramTuningIfc)
{
IADLXManualVRAMTuning2*vramTuning2=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning2(),&vramTuning2);
IADLXManualVRAMTuning1*vramTuning1=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning1(),&vramTuning1);
ADLX_MEMORYTIMING_DESCRIPTIONdesc;
IADLXMemoryTimingDescriptionList*descList=NULL;
if(vramTuning2)
{
vramTuning2->pVtbl->GetSupportedMemoryTimingDescriptionList(vramTuning2,&descList);
vramTuning2->pVtbl->GetMemoryTimingDescription(vramTuning2,&desc);

adlx_intfreq;
ADLX_IntRangerang;
vramTuning2->pVtbl->GetMaxVRAMFrequency(vramTuning2,&freq);
vramTuning2->pVtbl->GetMaxVRAMFrequencyRange(vramTuning2,&rang);

printf("\tMaxVRAMFrequencyis:%d\n\tMaxVRAMFrequencyRange:[%d,%d],step:%d\n",freq,rang.minValue,rang.maxValue,rang.step);
}
elseif(vramTuning1)
{
vramTuning1->pVtbl->GetSupportedMemoryTimingDescriptionList(vramTuning1,&descList);
vramTuning1->pVtbl->GetMemoryTimingDescription(vramTuning1,&desc);

IADLXManualTuningStateList*states=NULL;
vramTuning1->pVtbl->GetVRAMTuningStates(vramTuning1,&states);
adlx_uints=states->pVtbl->Begin(states);
printf("\tStateis:\n");
for(;s!=states->pVtbl->End(states);s++)
{
IADLXManualTuningState*state=NULL;
adlx_intfrequency;
adlx_intvoltage;
states->pVtbl->At_ManualTuningStateList(states,s,&state);
state->pVtbl->GetFrequency(state,&frequency);
state->pVtbl->GetVoltage(state,&voltage);
printf("\t\tfrequency:%d,voltage:%d\n",frequency,voltage);
}

ADLX_IntRangefrequencyRange;
ADLX_IntRangevoltageRange;
vramTuning1->pVtbl->GetVRAMTuningRanges(vramTuning1,&frequencyRange,&voltageRange);
printf("\tFrequencyRange:[%d,%d],step:%d\n",frequencyRange.minValue,frequencyRange.maxValue,frequencyRange.step);
printf("\tVoltageRange:[%d,%d],step:%d\n",voltageRange.minValue,voltageRange.maxValue,voltageRange.step);
}

if(descList)
{
printf("\tCurrentTimingdescis:%s\n",getDescStr(desc));
printf("\tSupportedmemorytimingdesclist:\n");
for(adlx_uints=0;s!=descList->pVtbl->End(descList);s++)
{
IADLXMemoryTimingDescription*item=NULL;
descList->pVtbl->At_MemoryTimingDescriptionList(descList,s,&item);
item->pVtbl->GetDescription(item,&desc);
printf("\t%s\n",getDescStr(desc));

//ReleasetheIADLXMemoryTimingDescriptioninterface
if(item)
{
item->pVtbl->Release(item);
item=NULL;
}
}
}
printf("\n");

//ReleasetheIADLXMemoryTimingDescriptionListinterface
if(descList!=NULL)
{
descList->pVtbl->Release(descList);
descList=NULL;
}

//ReleasetheManualVRAMTuning2interface
if(vramTuning2!=NULL)
{
vramTuning2->pVtbl->Release(vramTuning2);
vramTuning2=NULL;
}

//ReleasetheManualVRAMTuning1interface
if(vramTuning1!=NULL)
{
vramTuning1->pVtbl->Release(vramTuning1);
vramTuning1=NULL;
}
}

voidSetTimingLevel(IADLXInterface*vramTuningIfc)
{
ADLX_RESULTres;
IADLXManualVRAMTuning2*vramTuning2=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning2(),&vramTuning2);
IADLXManualVRAMTuning1*vramTuning1=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning1(),&vramTuning1);
ADLX_MEMORYTIMING_DESCRIPTIONdesc,currentDesc;
IADLXMemoryTimingDescriptionList*descList=NULL;
IADLXMemoryTimingDescription*item=NULL;
if(vramTuning2)
{
res=vramTuning2->pVtbl->GetSupportedMemoryTimingDescriptionList(vramTuning2,&descList);
if(descList)
{
vramTuning2->pVtbl->GetMemoryTimingDescription(vramTuning2,&currentDesc);
for(adlx_uints=0;s!=descList->pVtbl->End(descList);s++)
{
descList->pVtbl->At_MemoryTimingDescriptionList(descList,s,&item);
item->pVtbl->GetDescription(item,&desc);
if(desc!=currentDesc)
{
res=vramTuning2->pVtbl->SetMemoryTimingDescription(vramTuning2,desc);
printf("\tSetMemoryTimingdescriptionto%s:returncodeis:%d(0meanssuccess)\n",getDescStr(desc),res);
break;
}
}
}
else
{
printf("\tFailedtogettheSupportedMemoryTimingDescriptionList.\n");
}
}
elseif(vramTuning1)
{
res=vramTuning1->pVtbl->GetSupportedMemoryTimingDescriptionList(vramTuning1,&descList);
if(descList)
{
vramTuning1->pVtbl->GetMemoryTimingDescription(vramTuning1,&currentDesc);
for(adlx_uints=0;s!=descList->pVtbl->End(descList);s++)
{
descList->pVtbl->At_MemoryTimingDescriptionList(descList,s,&item);
item->pVtbl->GetDescription(item,&desc);
if(desc!=currentDesc)
{
res=vramTuning1->pVtbl->SetMemoryTimingDescription(vramTuning1,desc);
printf("\tSetMemoryTimingdescriptionto%s:returncodeis:%d(0meanssuccess)\n",getDescStr(desc),res);
break;
}
}
}
else
{
printf("\tFailedtogettheSupportedMemoryTimingDescriptionList.\n");
}
}

//ReleasetheIADLXMemoryTimingDescriptioninterface
if(item!=NULL)
{
item->pVtbl->Release(item);
item=NULL;
}

//ReleasetheIADLXMemoryTimingDescriptionListinterface
if(descList!=NULL)
{
descList->pVtbl->Release(descList);
descList=NULL;
}

//ReleasetheManualVRAMTuning2interface
if(vramTuning2!=NULL)
{
vramTuning2->pVtbl->Release(vramTuning2);
vramTuning2=NULL;
}

//ReleasetheManualVRAMTuning1interface
if(vramTuning1!=NULL)
{
vramTuning1->pVtbl->Release(vramTuning1);
vramTuning1=NULL;
}
}

voidSetState(IADLXInterface*vramTuningIfc)
{
ADLX_RESULTres;
IADLXManualVRAMTuning2*vramTuning2=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning2(),&vramTuning2);
IADLXManualVRAMTuning1*vramTuning1=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning1(),&vramTuning1);
if(vramTuning2)
{
adlx_intfreq;
ADLX_IntRangerang;
vramTuning2->pVtbl->GetMaxVRAMFrequency(vramTuning2,&freq);
vramTuning2->pVtbl->GetMaxVRAMFrequencyRange(vramTuning2,&rang);
if(freq!=rang.minValue)
{
res=vramTuning2->pVtbl->SetMaxVRAMFrequency(vramTuning2,rang.minValue);
printf("\tSetminimumfrequency:returncodeis:%d(0meanssuccess)\n",res);
}
else
{
res=vramTuning2->pVtbl->SetMaxVRAMFrequency(vramTuning2,rang.minValue+rang.step*2);
printf("\tSetmaximumfrequency:returncodeis:%d(0meanssuccess)\n",res);
}
}
elseif(vramTuning1)
{
ADLX_IntRangefrequencyRange;
ADLX_IntRangevoltageRange;
vramTuning1->pVtbl->GetVRAMTuningRanges(vramTuning1,&frequencyRange,&voltageRange);
IADLXManualTuningStateList*states=NULL;
vramTuning1->pVtbl->GetVRAMTuningStates(vramTuning1,&states);
adlx_uints=states->pVtbl->Begin(states);
for(;s!=states->pVtbl->End(states);s++)
{
IADLXManualTuningState*state=NULL;
adlx_intfrequency;
adlx_intvoltage;
states->pVtbl->At_ManualTuningStateList(states,s,&state);
state->pVtbl->GetFrequency(state,&frequency);
state->pVtbl->GetVoltage(state,&voltage);
//Onlychangethefirst
res=state->pVtbl->SetFrequency(state,frequency!=frequencyRange.minValue?frequencyRange.minValue:frequencyRange.maxValue);
res=state->pVtbl->SetVoltage(state,voltage!=voltageRange.minValue?voltageRange.minValue:voltageRange.maxValue);
break;
}
res=vramTuning1->pVtbl->SetVRAMTuningStates(vramTuning1,states);
printf("\tChangeVRAMtuningstates:returncodeis:%d(0meanssuccess)\n",res);
}

//ReleasetheManualVRAMTuning2interface
if(vramTuning2!=NULL)
{
vramTuning2->pVtbl->Release(vramTuning2);
vramTuning2=NULL;
}

//ReleasetheManualVRAMTuning1interface
if(vramTuning1!=NULL)
{
vramTuning1->pVtbl->Release(vramTuning1);
vramTuning1=NULL;
}
}

voidMainMenu(IADLXInterface*vramTuningIfc)
{
IADLXManualVRAMTuning2*vramTuning2=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning2(),&vramTuning2);
IADLXManualVRAMTuning1*vramTuning1=NULL;
vramTuningIfc->pVtbl->QueryInterface(vramTuningIfc,IID_IADLXManualVRAMTuning1(),&vramTuning1);
printf("\tChoosefromthefollowingoptions:\n");

printf("\t->Press1todisplayMemoryTimingsupport\n");
if(vramTuning2)
printf("\t->Press2todisplayMemoryTiminglevelandfrequencyrange\n");
elseif(vramTuning1)
printf("\t->Press2todisplayMemoryTiminglevel,VRAMfrequency,andthevoltageglobalrangeforallstates\n");

printf("\t->Press3tochangecurrentMemoryTimingLevel\n");
if(vramTuning2)
printf("\t->Press4tochangecurrentVRAMmaximumfrequencyvalue\n");
elseif(vramTuning1)
printf("\t->Press4tochangeVRAMstates\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");

//ReleasetheManualVRAMTuning2interface
if(vramTuning2!=NULL)
{
vramTuning2->pVtbl->Release(vramTuning2);
vramTuning2=NULL;
}

//ReleasetheManualVRAMTuning1interface
if(vramTuning1!=NULL)
{
vramTuning1->pVtbl->Release(vramTuning1);
vramTuning1=NULL;
}
}

voidMenuControl(IADLXInterface*vramTuningIfc)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//GetMemoryTimingsupport
case'1':
ShowSupport(vramTuningIfc);
break;

//GetVRAMstate
case'2':
GetState(vramTuningIfc);
break;

//Settiminglevel
case'3':
SetTimingLevel(vramTuningIfc);
break;

//SetVRAMstate
case'4':
SetState(vramTuningIfc);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu(vramTuningIfc);
break;
default:
break;
}
}
}
