//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

/*
*Iftheapponlyrequiresthecurrentmetricset,metricstrackingcanbeomittedinfavorofcallingthemoreefficientGetCurrentfunction.
*Iftheappalsorequiresmetricshistory,GetHistoryretrievestherequiredaccumulatedhistorybuffer,thatmayalsoincludethecurrentmetricset.
*/
//Displayallcurrentmetrics
voidShowCurrentAllMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU);

//Displayallhistoricalmetrics
voidShowHistoricalAllMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU);

//ASCII°
staticconstsignedcharg_degree=248;

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
//GetPerformanceMonitoringservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXPerformanceMonitoringServices*perfMonitoringService=NULL;
res=sys->pVtbl->GetPerformanceMonitoringServices(sys,&perfMonitoringService);
if(ADLX_SUCCEEDED(res))
{
IADLXGPUList*gpus=NULL;
//GetGPUlist
res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_SUCCEEDED(res))
{
//UseinitialGPU
IADLXGPU*oneGPU=NULL;
res=gpus->pVtbl->At_GPUList(gpus,gpus->pVtbl->Begin(gpus),&oneGPU);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(perfMonitoringService,oneGPU);
}
else
printf("\tGetparticularGPUfailed\n");
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
else
printf("\tGetGPUlistfailed\n");
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
}
else
printf("\tGetperformancemonitoringservicesfailed\n");
if(perfMonitoringService!=NULL)
{
perfMonitoringService->pVtbl->Release(perfMonitoringService);
perfMonitoringService=NULL;
}
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
printf("\tChooseoneofthefollowingoptions\n");

printf("\t->Press1todisplayallcurrentmetrics\n");
printf("\t->Press2todisplayallhistoricalmetrics\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displayallcurrentmetrics
case'1':
ShowCurrentAllMetrics(perfMonitoringServices,oneGPU);
break;

//Displayallhistoricalmetrics
case'2':
ShowHistoricalAllMetrics(perfMonitoringServices,oneGPU);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbefore returningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}

//Displaythesystemtimestamp(inms)
