//DisplayGPUmemorytemperature(in°C)
voidShowGPUMemoryTemperature(IADLXGPUMetricsSupport*gpuMetricsSupport,IADLXGPUMetrics*gpuMetrics)
{
adlx_boolsupported=false;

IADLXGPUMetricsSupport1*gpuMetricsSupport1=NULL;
IADLXGPUMetrics1*gpuMetrics1=NULL;
ADLX_RESULTsupportRes=gpuMetricsSupport->pVtbl->QueryInterface(gpuMetricsSupport,IID_IADLXGPUMetricsSupport1(),&gpuMetricsSupport1);
ADLX_RESULTmetricsRes=gpuMetrics->pVtbl->QueryInterface(gpuMetrics,IID_IADLXGPUMetrics1(),&gpuMetrics1);

if(ADLX_SUCCEEDED(supportRes)&&ADLX_SUCCEEDED(metricsRes))
{
//DisplaytheGPUmemorytemperaturesupportstatus
ADLX_RESULTres=gpuMetricsSupport1->pVtbl->IsSupportedGPUMemoryTemperature(gpuMetricsSupport1,&supported);
if(ADLX_SUCCEEDED(res))
{
printf("GPUmemorytemperaturesupportstatus:%d\n",supported);
if(supported)
{
adlx_doubletemperature=0;
res=gpuMetrics1->pVtbl->GPUMemoryTemperature(gpuMetrics1,&temperature);
if(ADLX_SUCCEEDED(res))
printf("TheGPUmemorytemperatureis:%f%cC\n",temperature,g_degree);
}
}
}

if(gpuMetricsSupport1)
{
gpuMetricsSupport1->pVtbl->Release(gpuMetricsSupport1);
gpuMetricsSupport1=NULL;
}

if(gpuMetrics1)
{
gpuMetrics1->pVtbl->Release(gpuMetrics1);
gpuMetrics1=NULL;
}
}

