//Displayallcurrentmetrics
voidShowCurrentAllMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU)
{
//Getsystemmetricssupport
IADLXSystemMetricsSupport*systemMetricsSupport=NULL;
ADLX_RESULTres2=perfMonitoringServices->pVtbl->GetSupportedSystemMetrics(perfMonitoringServices,&systemMetricsSupport);
//GetGPUmetricssupport
IADLXGPUMetricsSupport*gpuMetricsSupport=NULL;
res2=perfMonitoringServices->pVtbl->GetSupportedGPUMetrics(perfMonitoringServices,oneGPU,&gpuMetricsSupport);

//Accumulateanddisplaycurrentmetricsforeachof10loops
IADLXAllMetrics*allMetrics=NULL;
IADLXSystemMetrics*systemMetrics=NULL;
IADLXGPUMetrics*gpuMetrics=NULL;
IADLXFPS*oneFPS=NULL;
for(inti=0;i<10;++i)
{
//Clearscreen
system("cls");
//Getallcurrentmetrics
ADLX_RESULTres1=perfMonitoringServices->pVtbl->GetCurrentAllMetrics(perfMonitoringServices,&allMetrics);
if(ADLX_SUCCEEDED(res1))
{
printf("Thecurrentallmetrics:\n");
//Getcurrentsystemmetrics
res1=allMetrics->pVtbl->GetSystemMetrics(allMetrics,&systemMetrics);
//Displaytimestamp,CPUusage,systemRAM,andSmartShift
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
GetTimeStampBySystem(systemMetrics);
ShowCPUUsage(systemMetricsSupport,systemMetrics);
ShowSystemRAM(systemMetricsSupport,systemMetrics);
ShowSmartShift(systemMetricsSupport,systemMetrics);
}
//Free
if(systemMetrics!=NULL)
{
systemMetrics->pVtbl->Release(systemMetrics);
systemMetrics=NULL;
}
//GetcurrentGPUmetrics
res1=allMetrics->pVtbl->GetGPUMetrics(allMetrics,oneGPU,&gpuMetrics);
//DisplaytimestampandGPUmetrics
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
GetTimeStampByGPU(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUMemoryTemperature(gpuMetricsSupport,gpuMetrics);
ShowNPUActivityLevel(gpuMetricsSupport,gpuMetrics);
ShowNPUFrequency(gpuMetricsSupport,gpuMetrics);
}
//Free
if(gpuMetrics!=NULL)
{
gpuMetrics->pVtbl->Release(gpuMetrics);
gpuMetrics=NULL;
}
//GetcurrentFPSmetrics
res1=allMetrics->pVtbl->GetFPS(allMetrics,&oneFPS);
if(ADLX_SUCCEEDED(res1))
{
adlx_int64timeStamp=0;
res1=oneFPS->pVtbl->TimeStamp(oneFPS,&timeStamp);
if(ADLX_SUCCEEDED(res1))
printf("Thecurrentmetrictimestampis:%lldms\n",timeStamp);
adlx_intfpsData=0;
res1=oneFPS->pVtbl->FPS(oneFPS,&fpsData);
if(ADLX_SUCCEEDED(res1))
printf("ThecurrentmetricFPSis:%d\n",fpsData);
elseif(res1==ADLX_NOT_SUPPORTED)
printf("FPSnotsupported\n");
}
//Free
if(oneFPS!=NULL)
{
oneFPS->pVtbl->Release(oneFPS);
oneFPS=NULL;
}
}
Sleep(1000);
//Free
if(allMetrics!=NULL)
{
allMetrics->pVtbl->Release(allMetrics);
allMetrics=NULL;
}
}
//Free
if(systemMetricsSupport!=NULL)
{
systemMetricsSupport->pVtbl->Release(systemMetricsSupport);
systemMetricsSupport=NULL;
}
if(gpuMetricsSupport!=NULL)
{
gpuMetricsSupport->pVtbl->Release(gpuMetricsSupport);
gpuMetricsSupport=NULL;
}

MainMenu();
}

//Showallhistoricalmetrics
voidShowHistoricalAllMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU)
{
//Clearhistoricalperformancemetricsdata
ADLX_RESULTres=perfMonitoringServices->pVtbl->ClearPerformanceMetricsHistory(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Clearhistoricaldatafailed\n");
return;
}

//Starttrackingperformancemetrics
res=perfMonitoringServices->pVtbl->StartPerformanceMetricsTracking(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Starttrackingperformancemetricsfailed\n");
return;
}

//Waitfor10secondstoaccumulatemetrics
printf("Waitfor10secondstoaccumulatemetrics...\n");
Sleep(10000);

//Getallmetricshistoryfrom10secondsago(10000ms:thesecondparameter)tothepresenttime(0ms:thethirdparameter)
IADLXAllMetricsList*allMetricsList=NULL;
res=perfMonitoringServices->pVtbl->GetAllMetricsHistory(perfMonitoringServices,10000,0,&allMetricsList);
if(ADLX_SUCCEEDED(res))
{
//Getsystemmetricssupport
IADLXSystemMetricsSupport*systemMetricsSupport=NULL;
ADLX_RESULTsystemRes=perfMonitoringServices->pVtbl->GetSupportedSystemMetrics(perfMonitoringServices,&systemMetricsSupport);

//GetGPUmetricssupport
IADLXGPUMetricsSupport*gpuMetricsSupport=NULL;
ADLX_RESULTgpuRes=perfMonitoringServices->pVtbl->GetSupportedGPUMetrics(perfMonitoringServices,oneGPU,&gpuMetricsSupport);

//Displayallthemetricsinthelist
IADLXAllMetrics*allMetrics=NULL;
IADLXSystemMetrics*systemMetrics=NULL;
IADLXGPUMetrics*gpuMetrics=NULL;
IADLXFPS*oneFPS=NULL;
for(inti=allMetricsList->pVtbl->Begin(allMetricsList);i!=allMetricsList->pVtbl->End(allMetricsList);++i)
{
printf("**********historicalsystemmetrics%d:**********\n",i+1);
res=allMetricsList->pVtbl->At_AllMetricsList(allMetricsList,i,&allMetrics);
if(ADLX_SUCCEEDED(res))
{
res=allMetrics->pVtbl->GetSystemMetrics(allMetrics,&systemMetrics);
//Displayfirstsystemmetricstimestamp,CPUusage,systemRAM,andSmartShift
if(ADLX_SUCCEEDED(systemRes)&&ADLX_SUCCEEDED(res))
{
GetTimeStampBySystem(systemMetrics);
ShowCPUUsage(systemMetricsSupport,systemMetrics);
ShowSystemRAM(systemMetricsSupport,systemMetrics);
ShowSmartShift(systemMetricsSupport,systemMetrics);
}
//Free
if(systemMetrics!=NULL)
{
systemMetrics->pVtbl->Release(systemMetrics);
systemMetrics=NULL;
}
res=allMetrics->pVtbl->GetGPUMetrics(allMetrics,oneGPU,&gpuMetrics);
//DisplaytimestampandGPUmetrics
if(ADLX_SUCCEEDED(gpuRes)&&ADLX_SUCCEEDED(res))
{
GetTimeStampByGPU(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUMemoryTemperature(gpuMetricsSupport,gpuMetrics);
ShowNPUActivityLevel(gpuMetricsSupport,gpuMetrics);
ShowNPUFrequency(gpuMetricsSupport,gpuMetrics);
}
//Free
if(gpuMetrics!=NULL)
{
gpuMetrics->pVtbl->Release(gpuMetrics);
gpuMetrics=NULL;
}
res=allMetrics->pVtbl->GetFPS(allMetrics,&oneFPS);
if(ADLX_SUCCEEDED(res))
{
adlx_int64timeStamp=0;
res=oneFPS->pVtbl->TimeStamp(oneFPS,&timeStamp);
if(ADLX_SUCCEEDED(res))
printf("Thetimestampis:%lldms\n",timeStamp);
adlx_intfpsData=0;
res=oneFPS->pVtbl->FPS(oneFPS,&fpsData);
if(ADLX_SUCCEEDED(res))
printf("TheFPSis:%d\n",fpsData);
elseif(res==ADLX_NOT_SUPPORTED)
printf("Don'tsupportFPS\n");
}
//Free
if(oneFPS!=NULL)
{
oneFPS->pVtbl->Release(oneFPS);
oneFPS=NULL;
}
printf("\n");
}
//Free
if(allMetrics!=NULL)
{
allMetrics->pVtbl->Release(allMetrics);
allMetrics=NULL;
}
}
//Free
if(systemMetricsSupport!=NULL)
{
systemMetricsSupport->pVtbl->Release(systemMetricsSupport);
systemMetricsSupport=NULL;
}
if(gpuMetricsSupport!=NULL)
{
gpuMetricsSupport->pVtbl->Release(gpuMetricsSupport);
gpuMetricsSupport=NULL;
}
}
//Free
if(allMetricsList!=NULL)
{
allMetricsList->pVtbl->Release(allMetricsList);
allMetricsList=NULL;
}

//Stoptrackingperformancemetrics
res=perfMonitoringServices->pVtbl->StopPerformanceMetricsTracking(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Stoptrackingperformancemetricsfailed\n");
return;
}
}
