voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//ShowGPUmetricsrange
voidShowGPUMetricsRange(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU);

/*
*Iftheapponlyrequiresthecurrentmetricset,metricstrackingcanbeomittedinfavorofcallingthemoreefficientGetCurrentfunction.
*Iftheappalsorequiresmetricshistory,GetHistoryretrievestherequiredaccumulatedhistorybuffer,thatmayalsoincludethecurrentmetricset.
*/
//ShowcurrentGPUmetrics
voidShowCurrentGPUMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU);
voidShowCurrentGPUMetricsFromHistorical(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU);

//ShowhistoricalGPUmetrics
voidShowHistoricalGPUMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU);

//ASCIIÂ°
staticconstsignedcharg_degree=248;

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
//GetPerformanceMonitoringservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXPerformanceMonitoringServices*perfMonitoringService=NULL;
res=sys->pVtbl->GetPerformanceMonitoringServices(sys,&perfMonitoringService);
if(ADLX_SUCCEEDED(res))
{
IADLXGPUList*gpus=NULL;
//GetGPUlist
res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_SUCCEEDED(res))
{
//UseinitialGPU
IADLXGPU*oneGPU=NULL;
res=gpus->pVtbl->At_GPUList(gpus,gpus->pVtbl->Begin(gpus),&oneGPU);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(perfMonitoringService,oneGPU);
}
else
printf("\tGetparticularGPUfailed\n");
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
else
printf("\tGetGPUlistfailed\n");
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
}
else
printf("\tGetPerformanceMonitoringservicesfailed\n");
if(perfMonitoringService!=NULL)
{
perfMonitoringService->pVtbl->Release(perfMonitoringService);
perfMonitoringService=NULL;
}
}
else
returnWaitAndExit("\tg_ADLXHelpinitializationfailed",0);

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
printf("\tChooseonefromthefollowingoptions\n");

printf("\t->Press1todisplaytheGPUmetricsrange\n");
printf("\t->Press2todisplaythecurrentGPUmetrics\n");
printf("\t->Press3todisplaythecurrentGPUmetricsfromhistoricaldata\n");
printf("\t->Press4todisplaythehistoricalGPUmetrics\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaythemainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//ShowGPUmetricsrange
case'1':
ShowGPUMetricsRange(perfMonitoringServices,oneGPU);
break;

//DisplaycurrentGPUmetrics
case'2':
ShowCurrentGPUMetrics(perfMonitoringServices,oneGPU);
break;

//DisplaycurrentGPUmetricsfromhistoricaldata
case'3':
ShowCurrentGPUMetricsFromHistorical(perfMonitoringServices,oneGPU);
break;

//DisplayhistoricalGPUmetrics
case'4':
ShowHistoricalGPUMetrics(perfMonitoringServices,oneGPU);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}

//DisplayGPUmetricsrange
voidShowGPUMetricsRange(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU)
{
//GetGPUmetricssupport
IADLXGPUMetricsSupport*gpuMetricsSupport=NULL;
ADLX_RESULTres=perfMonitoringServices->pVtbl->GetSupportedGPUMetrics(perfMonitoringServices,oneGPU,&gpuMetricsSupport);
if(ADLX_SUCCEEDED(res))
{
adlx_intminValue=0,maxValue=0;

//GetGPUusagerange
res=gpuMetricsSupport->pVtbl->GetGPUUsageRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUusagerangebetween%d%%and%d%%\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUusagerangenotsupported\n");

//GetGPUclockspeedrange
res=gpuMetricsSupport->pVtbl->GetGPUClockSpeedRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUclockspeedrangebetween%dMHzand%dMHz\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUclockspeedrangenotsupported\n");

//GetGPUVRAMclockspeedrange
res=gpuMetricsSupport->pVtbl->GetGPUVRAMClockSpeedRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUVRAMclockspeedrangebetween%dMHzand%dMHz\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUVRAMclockspeedrangenotsupported\n");

//GetGPUtemperaturerange
res=gpuMetricsSupport->pVtbl->GetGPUTemperatureRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUtemperaturerangebetween%d%cCand%d%cC\n",minValue,g_degree,maxValue,g_degree);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUtemperaturerangenotsupported\n");

//GetGPUhotspottemperaturerange
res=gpuMetricsSupport->pVtbl->GetGPUHotspotTemperatureRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUhotspottemperaturerangebetween%d%cCand%d%cC\n",minValue,g_degree,maxValue,g_degree);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUhotspottemperaturerangenotsupported\n");

//GetGPUpowerrange
res=gpuMetricsSupport->pVtbl->GetGPUPowerRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUpowerrangebetween%dWand%dW\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUpowerrangenotsupported\n");

//GetGPUfanspeedrange
res=gpuMetricsSupport->pVtbl->GetGPUFanSpeedRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUfanspeedrangebetween%dRPMand%dRPM\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUfanspeedrangenotsupported\n");

//GetGPUVRAMrange
res=gpuMetricsSupport->pVtbl->GetGPUVRAMRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUVRAMrangebetween%dMBand%dMB\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUVRAMrangenotsupported\n");

//GetGPUvoltagerange
res=gpuMetricsSupport->pVtbl->GetGPUVoltageRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUvoltagerangebetween%dmVand%dmV\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUvoltagerangenotsupported\n");

//GetGPUtotalboardpowerrange
res=gpuMetricsSupport->pVtbl->GetGPUTotalBoardPowerRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUtotalboardpowerrangebetween%dWand%dW\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUtotalboardpowerrangenotsupported\n");

//GetGPUintaketemperaturerange
res=gpuMetricsSupport->pVtbl->GetGPUIntakeTemperatureRange(gpuMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheGPUintaketemperaturerangebetween%dWand%dW\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("GPUintaketemperaturerangenotsupported\n");
}
if(gpuMetricsSupport!=NULL)
{
gpuMetricsSupport->pVtbl->Release(gpuMetricsSupport);
gpuMetricsSupport=NULL;
}
}

//Displaythesystemtimestamp(inms)
voidGetTimeStamp(IADLXGPUMetrics*gpuMetrics)
{
adlx_int64timeStamp=0;
ADLX_RESULTres=gpuMetrics->pVtbl->TimeStamp(gpuMetrics,&timeStamp);
if(ADLX_SUCCEEDED(res))
printf("TheGPUtimpstampis:%lldms\n",timeStamp);
}

//DisplayGPUusage(in%)
voidShowGPUUsage(IADLXGPUMetricsSupport*gpuMetricsSupport,IADLXGPUMetrics*gpuMetrics)
{
adlx_boolsupported=false;
//DisplayGPUusagesupportstatus
ADLX_RESULTres=gpuMetricsSupport->pVtbl->IsSupportedGPUUsage(gpuMetricsSupport,&supported);
if(ADLX_SUCCEEDED(res))
{
printf("GPUusagesupportstatus:%d\n",supported);
if(supported)
{
adlx_doubleusage=0;
res=gpuMetrics->pVtbl->GPUUsage(gpuMetrics,&usage);
if(ADLX_SUCCEEDED(res))
printf("TheGPUusageis:%f%%\n",usage);
}
}
}

