//DisplaycurrentGPUmetrics
voidShowCurrentGPUMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU)
{
//GetGPUmetricssupport
IADLXGPUMetricsSupport*gpuMetricsSupport=NULL;
ADLX_RESULTres1=perfMonitoringServices->pVtbl->GetSupportedGPUMetrics(perfMonitoringServices,oneGPU,&gpuMetricsSupport);

//Accumulateanddisplaycurrentmetricsforeachof10loops
IADLXGPUMetrics*gpuMetrics=NULL;
for(inti=0;i<10;++i)
{
//Clearscreen
system("cls");
//GetcurrentGPUmetrics
ADLX_RESULTres2=perfMonitoringServices->pVtbl->GetCurrentGPUMetrics(perfMonitoringServices,oneGPU,&gpuMetrics);

//DisplaytimestampandGPUmetrics
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
printf("ThecurrentGPUmetrics:\n");
GetTimeStamp(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUTotalBoardPower(gpuMetricsSupport,gpuMetrics);
ShowGPUIntakeTemperature(gpuMetricsSupport,gpuMetrics);
}
Sleep(1000);

//Free
if(gpuMetrics!=NULL)
{
gpuMetrics->pVtbl->Release(gpuMetrics);
gpuMetrics=NULL;
}
}

//Free
if(gpuMetricsSupport!=NULL)
{
gpuMetricsSupport->pVtbl->Release(gpuMetricsSupport);
gpuMetricsSupport=NULL;
}

MainMenu();
}

voidShowCurrentGPUMetricsFromHistorical(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU)
{
//Clearhistoricalperformancemetricsdata
ADLX_RESULTres=perfMonitoringServices->pVtbl->ClearPerformanceMetricsHistory(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtoclearhistoricaldata\n");
return;
}

//Starttrackingperformancemetrics
res=perfMonitoringServices->pVtbl->StartPerformanceMetricsTracking(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtostarttrackingperformancemetrics\n");
return;
}

//GetGPUmetricssupport
IADLXGPUMetricsSupport*gpuMetricsSupport=NULL;
ADLX_RESULTmetricsSupportRet=perfMonitoringServices->pVtbl->GetSupportedGPUMetrics(perfMonitoringServices,oneGPU,&gpuMetricsSupport);

//Accumulateanddisplaycurrentmetricsforeachof10loops
adlx_intstartMs=0;
adlx_intstopMs=0;
for(intit=0;it<10;++it)
{
//Clearscreen
system("cls");

IADLXGPUMetricsList*gpuMetricsList=NULL;
res=perfMonitoringServices->pVtbl->GetGPUMetricsHistory(perfMonitoringServices,oneGPU,startMs,stopMs,&gpuMetricsList);
if(ADLX_SUCCEEDED(res))
{
//DisplayalltheGPUmetricsinthelist
IADLXGPUMetrics*gpuMetrics=NULL;
for(inti=gpuMetricsList->pVtbl->Begin(gpuMetricsList);i!=gpuMetricsList->pVtbl->End(gpuMetricsList);++i)
{
res=gpuMetricsList->pVtbl->At_GPUMetricsList(gpuMetricsList,i,&gpuMetrics);
//DisplaytimestampandGPUmetrics
if(ADLX_SUCCEEDED(metricsSupportRet)&&ADLX_SUCCEEDED(res))
{
printf("ThecurrentGPUmetrics:\n");
GetTimeStamp(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUTotalBoardPower(gpuMetricsSupport,gpuMetrics);
ShowGPUIntakeTemperature(gpuMetricsSupport,gpuMetrics);
}

//ReleaseIADLXGPUMetricsinterface
if(gpuMetrics!=NULL)
{
gpuMetrics->pVtbl->Release(gpuMetrics);
gpuMetrics=NULL;
}
}
}

//ReleaseIADLXGPUMetricsListinterface
if(gpuMetricsList!=NULL)
{
gpuMetricsList->pVtbl->Release(gpuMetricsList);
gpuMetricsList=NULL;
}

Sleep(1000);
}

//ReleaseIADLXGPUMetricsSupportinterface
if(gpuMetricsSupport!=NULL)
{
gpuMetricsSupport->pVtbl->Release(gpuMetricsSupport);
gpuMetricsSupport=NULL;
}

//Stoptrackingperformancemetrics
res=perfMonitoringServices->pVtbl->StopPerformanceMetricsTracking(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtostoptrackingperformancemetrics\n");
}

MainMenu();
}

//DisplayhistoricalGPUMetrics
voidShowHistoricalGPUMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices,IADLXGPU*oneGPU)
{
//Clearhistoricalperformancemetricsdata
ADLX_RESULTres=perfMonitoringServices->pVtbl->ClearPerformanceMetricsHistory(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtoclearhistoricaldata\n");
return;
}

//Starttrackingperformancemetrics
res=perfMonitoringServices->pVtbl->StartPerformanceMetricsTracking(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtostarttrackingperformancemetrics\n");
return;
}

//Waitfor10secondstoaccumulatemetrics
printf("Waitfor10secondstoaccumulatemetrics...\n");
Sleep(10000);

//GetGPUmetricshistoryfrom10secondsago(10000ms:thethirdparameter)tothepresenttime(0ms:thefourthparameter)
IADLXGPUMetricsList*gpuMetricsList=NULL;
res=perfMonitoringServices->pVtbl->GetGPUMetricsHistory(perfMonitoringServices,oneGPU,10000,0,&gpuMetricsList);
if(ADLX_SUCCEEDED(res))
{
//GetGPUmetricssupport
IADLXGPUMetricsSupport*gpuMetricsSupport=NULL;
ADLX_RESULTres1=perfMonitoringServices->pVtbl->GetSupportedGPUMetrics(perfMonitoringServices,oneGPU,&gpuMetricsSupport);

//DisplayalltheGPUmetricsinthelist
IADLXGPUMetrics*gpuMetrics=NULL;
for(inti=gpuMetricsList->pVtbl->Begin(gpuMetricsList);i!=gpuMetricsList->pVtbl->End(gpuMetricsList);++i)
{
printf("**********historicalGPUmetrics%d:**********\n",i+1);
ADLX_RESULTres2=gpuMetricsList->pVtbl->At_GPUMetricsList(gpuMetricsList,i,&gpuMetrics);
//DisplaytimestampandGPUmetrics
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
GetTimeStamp(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUTotalBoardPower(gpuMetricsSupport,gpuMetrics);
ShowGPUIntakeTemperature(gpuMetricsSupport,gpuMetrics);
}
printf("\n");
if(gpuMetrics!=NULL)
{
gpuMetrics->pVtbl->Release(gpuMetrics);
gpuMetrics=NULL;
}
}
if(gpuMetricsSupport!=NULL)
{
gpuMetricsSupport->pVtbl->Release(gpuMetricsSupport);
gpuMetricsSupport=NULL;
}
}
if(gpuMetricsList!=NULL)
{
gpuMetricsList->pVtbl->Release(gpuMetricsList);
gpuMetricsList=NULL;
}

//Stoptrackingperformancemetrics
res=perfMonitoringServices->pVtbl->StopPerformanceMetricsTracking(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtostoptrackingperformancemetrics\n");
return;
}
}
