#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IPerformanceMonitoring1.h"

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServices*perfMonitoringServices);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//Displaysystemmetricsrange
voidShowSystemMetricsRange(IADLXPerformanceMonitoringServices*perfMonitoringServices);

/*
*Iftheapponlyrequiresthecurrentmetricset,metricstrackingcanbeomittedinfavorofcallingthemoreefficientGetCurrentfunction.
*Iftheappalsorequiresmetricshistory,GetHistoryretrievestherequiredaccumulatedhistorybuffer,thatmayalsoincludethecurrentmetricset.
*/
//Displaycurrentsystemmetrics
voidShowCurrentSystemMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices);

//Displayhistoricalsystemmetrics
voidShowHistoricalSystemMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
//GetPerformanceMonitoringservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXPerformanceMonitoringServices*perfMonitoringServices=NULL;
ADLX_RESULTres=sys->pVtbl->GetPerformanceMonitoringServices(sys,&perfMonitoringServices);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(perfMonitoringServices);
}
else
printf("\tGetperformancemonitoringservicesfailed\n");
if(perfMonitoringServices!=NULL)
{
perfMonitoringServices->pVtbl->Release(perfMonitoringServices);
perfMonitoringServices=NULL;
}
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXresult:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
printf("\tChooseonefromthefollowingoptions\n");

printf("\t->Press1todisplaythesystemmetricsrange\n");
printf("\t->Press2todisplaythecurrentsystemmetrics\n");
printf("\t->Press3todisplaythehistoricalsystemmetrics\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaythemainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServices*perfMonitoringServices)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displaysystemmetricsrange
case'1':
ShowSystemMetricsRange(perfMonitoringServices);
break;

//Displaycurrentmetrics
case'2':
ShowCurrentSystemMetrics(perfMonitoringServices);
break;

//Displayhistoricalmetrics
case'3':
ShowHistoricalSystemMetrics(perfMonitoringServices);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}

//Displaysystemmetricsrange
voidShowSystemMetricsRange(IADLXPerformanceMonitoringServices*perfMonitoringServices)
{
//Getsystemmetricssupport
IADLXSystemMetricsSupport*systemMetricsSupport=NULL;
ADLX_RESULTres=perfMonitoringServices->pVtbl->GetSupportedSystemMetrics(perfMonitoringServices,&systemMetricsSupport);
if(ADLX_SUCCEEDED(res))
{
adlx_intminValue=0,maxValue=0;

//GetCPUusagerange
res=systemMetricsSupport->pVtbl->GetCPUUsageRange(systemMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("TheCPUusagerangebetween%d%%and%d%%\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("CPUusagerangenotsupported\n");

//GetsystemRAMrange
res=systemMetricsSupport->pVtbl->GetSystemRAMRange(systemMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("ThesystemRAMrangebetween%dMBand%dMB\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("SystemRAMrangenotsupported\n");

//GetSmartShiftrange
res=systemMetricsSupport->pVtbl->GetSmartShiftRange(systemMetricsSupport,&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
printf("Thesmartshiftrangebetween%dand%d\n",minValue,maxValue);
elseif(res==ADLX_NOT_SUPPORTED)
printf("SmartShiftrangenotsupported\n");
}
if(systemMetricsSupport!=NULL)
{
systemMetricsSupport->pVtbl->Release(systemMetricsSupport);
systemMetricsSupport=NULL;
}
}

//Displaythesystemtimestamp(inms)
voidGetTimeStamp(IADLXSystemMetrics*systemMetrics)
{
adlx_int64timeStamp=0;
ADLX_RESULTres=systemMetrics->pVtbl->TimeStamp(systemMetrics,&timeStamp);
if(ADLX_SUCCEEDED(res))
printf("Thesystemtimpstampis:%lldms\n",timeStamp);
}

//DisplayCPUusage(in%)
voidShowCPUUsage(IADLXSystemMetricsSupport*systemMetricsSupport,IADLXSystemMetrics*systemMetrics)
{
adlx_boolsupported=false;
//DisplayCPUusagesupportstatus
ADLX_RESULTres=systemMetricsSupport->pVtbl->IsSupportedCPUUsage(systemMetricsSupport,&supported);
if(ADLX_SUCCEEDED(res))
{
printf("CPUusagesupportstatus:%d\n",supported);
if(supported)
{
adlx_doublecpuUsage=0;
res=systemMetrics->pVtbl->CPUUsage(systemMetrics,&cpuUsage);
if(ADLX_SUCCEEDED(res))
printf("TheCPUusageis:%f%%\n",cpuUsage);
}
}
}

//DisplaysystemRAM(inMB)
voidShowSystemRAM(IADLXSystemMetricsSupport*systemMetricsSupport,IADLXSystemMetrics*systemMetrics)
{
adlx_boolsupported=false;
//DisplaysystemRAMusagesupportstatus
ADLX_RESULTres=systemMetricsSupport->pVtbl->IsSupportedSystemRAM(systemMetricsSupport,&supported);
if(ADLX_SUCCEEDED(res))
{
printf("SystemRAMusagesupportstatus:%d\n",supported);
if(supported)
{
adlx_intsystemRAM=0;
res=systemMetrics->pVtbl->SystemRAM(systemMetrics,&systemRAM);
if(ADLX_SUCCEEDED(res))
printf("ThesystemRAMis:%dMB\n",systemRAM);
}
}
}

//ShowSmartShift
voidShowSmartShift(IADLXSystemMetricsSupport*systemMetricsSupport,IADLXSystemMetrics*systemMetrics)
{
adlx_boolsupported=false;
//DisplaySmartShiftsupportstatus
ADLX_RESULTres=systemMetricsSupport->pVtbl->IsSupportedSmartShift(systemMetricsSupport,&supported);
