//ShowSmartShiftMax
voidShowSmartShiftMax(IADLXSystemMetricsSupport*systemMetricsSupport,IADLXSystemMetrics*systemMetrics)
{
IADLXSystemMetricsSupport1*sysMetricsSupport1=NULL;
ADLX_RESULTres=systemMetricsSupport->pVtbl->QueryInterface(systemMetricsSupport,IID_IADLXSystemMetricsSupport1(),(void**)&sysMetricsSupport1);
if(ADLX_SUCCEEDED(res))
{
adlx_boolsupported=false;
//Displaypowerdistributionsupportstatus
res=sysMetricsSupport1->pVtbl->IsSupportedPowerDistribution(sysMetricsSupport1,&supported);
if(ADLX_SUCCEEDED(res))
{
printf("PowerDistributionsupportstatus:%d\n",supported);
if(supported)
{
IADLXSystemMetrics1*sysMetrics1=NULL;
res=systemMetrics->pVtbl->QueryInterface(systemMetrics,IID_IADLXSystemMetrics1(),(void*)&sysMetrics1);
if(ADLX_SUCCEEDED(res))
{
intapuShiftValue,gpuShiftValue,apuShiftLimit,gpuShiftLimit,totalShiftLimit;
res=sysMetrics1->pVtbl->PowerDistribution(sysMetrics1,&apuShiftValue,&gpuShiftValue,&apuShiftLimit,&gpuShiftLimit,&totalShiftLimit);
if(ADLX_SUCCEEDED(res))
printf("ThePowerDistributionis:\napuShiftValue:%d,gpuShiftValue:%d,apuShiftLimit:%d,gpuShiftLimit:%d,totalShiftLimit:%d\n"
,apuShiftValue,gpuShiftValue,apuShiftLimit,gpuShiftLimit,totalShiftLimit);

//ReleaseIADLXSystemMetrics1interface
sysMetrics1->pVtbl->Release(sysMetrics1);
}
else
{
printf("\tGetIADLXSystemMetrics1Ptrfailed\n");
}
}
}

//ReleaseIADLXSystemMetricsSupport1interface
sysMetricsSupport1->pVtbl->Release(sysMetricsSupport1);
}
else
{
printf("\tGetIADLXSystemMetricsSupport1Ptrfailed\n");
}
}

//Displaycurrentsystemmetrics
voidShowCurrentSystemMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices)
{
//Getsystemmetricssupport
IADLXSystemMetricsSupport*systemMetricsSupport=NULL;
ADLX_RESULTres1=perfMonitoringServices->pVtbl->GetSupportedSystemMetrics(perfMonitoringServices,&systemMetricsSupport);

//Accumulateanddisplaycurrentmetricsforeachofthe10loops
IADLXSystemMetrics*systemMetrics=NULL;
for(inti=0;i<10;++i)
{
//Clearscreen
system("cls");
//Getcurrentsystemmetrics
ADLX_RESULTres2=perfMonitoringServices->pVtbl->GetCurrentSystemMetrics(perfMonitoringServices,&systemMetrics);

//Displaytimestamp,CPUusage,systemRAM,andSmartShift
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
printf("Thecurrentsystemmetrics:\n");
GetTimeStamp(systemMetrics);
ShowCPUUsage(systemMetricsSupport,systemMetrics);
ShowSystemRAM(systemMetricsSupport,systemMetrics);
ShowSmartShift(systemMetricsSupport,systemMetrics);
ShowSmartShiftMax(systemMetricsSupport,systemMetrics);
}
Sleep(1000);

//Free
if(systemMetrics!=NULL)
{
systemMetrics->pVtbl->Release(systemMetrics);
systemMetrics=NULL;
}
}

//Free
if(systemMetricsSupport!=NULL)
{
systemMetricsSupport->pVtbl->Release(systemMetricsSupport);
systemMetricsSupport=NULL;
}

MainMenu();
}

//Displayhistoricalsystemmetrics
voidShowHistoricalSystemMetrics(IADLXPerformanceMonitoringServices*perfMonitoringServices)
{
//Clearhistoricalperformancemetricsdata
ADLX_RESULTres=perfMonitoringServices->pVtbl->ClearPerformanceMetricsHistory(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtoclearhistoricaldata\n");
return;
}

//Getcurrentmaxhistoricalsize(ins)
adlx_intmaxHistorySize=0;
res=perfMonitoringServices->pVtbl->GetMaxPerformanceMetricsHistorySize(perfMonitoringServices,&maxHistorySize);
if(ADLX_SUCCEEDED(res))
printf("Thecurrentmaxhistoricalsizeis:%ds\n",maxHistorySize);
//Getthemaxhistoricalsizerange.Themaxhistoricalsizemustbesetwithinthisrange.
ADLX_IntRangerange;
res=perfMonitoringServices->pVtbl->GetMaxPerformanceMetricsHistorySizeRange(perfMonitoringServices,&range);
if(ADLX_SUCCEEDED(res))
printf("Therangeofmaxhistoricalsizeis:(%d,%d)s\n",range.minValue,range.maxValue);
//Changemaxhistoricalsizetominimumvalue
res=perfMonitoringServices->pVtbl->SetMaxPerformanceMetricsHistorySize(perfMonitoringServices,range.minValue);
if(ADLX_SUCCEEDED(res))
{
perfMonitoringServices->pVtbl->GetMaxPerformanceMetricsHistorySize(perfMonitoringServices,&maxHistorySize);
printf("Setmaxhistoricalsizetominimumvaluesucceeded,thecurrentmaxhistoricalis:%ds\n",maxHistorySize);
}

//Getcurrentsamplinginterval(inms)
adlx_intsamplingInterval=0;
res=perfMonitoringServices->pVtbl->GetSamplingInterval(perfMonitoringServices,&samplingInterval);
if(ADLX_SUCCEEDED(res))
printf("Thecurrentsamplingintervalis:%dms\n",samplingInterval);
//Getthesamplingintervalrange.Thesamplingintervalmustbesetwithinthisrange.
res=perfMonitoringServices->pVtbl->GetSamplingIntervalRange(perfMonitoringServices,&range);
if(ADLX_SUCCEEDED(res))
printf("Therangeofsamplingintervalis:(%d,%d)ms\n",range.minValue,range.maxValue);
//Changesamplingintervaltominimumvalue
res=perfMonitoringServices->pVtbl->SetSamplingInterval(perfMonitoringServices,range.minValue);
if(ADLX_SUCCEEDED(res))
{
perfMonitoringServices->pVtbl->GetSamplingInterval(perfMonitoringServices,&samplingInterval);
printf("Setsamplingintervaltominimumvaluesucceeded,thecurrentsamplingintervalis:%dms\n",samplingInterval);
}

//Starttrackingperformancemetrics
res=perfMonitoringServices->pVtbl->StartPerformanceMetricsTracking(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtostarttrackingperformancemetrics\n");
return;
}

//Waitfor10secondstoaccumulatemetrics
printf("Waitfor10secondstoaccumulatemetrics...\n");
Sleep(10000);

//Checkthecurrentperformancemetricshistoricalsize(ins)uponaccumulation
adlx_intcurrentHistorySize=0;
res=perfMonitoringServices->pVtbl->GetCurrentPerformanceMetricsHistorySize(perfMonitoringServices,&currentHistorySize);
if(ADLX_SUCCEEDED(res))
printf("Thecurrenthistoricalsizeis:%ds\n",currentHistorySize);

//Getsystemmetricshistoryfrom10secondsago(10000ms:thesecondparameter)tothepresenttime(0ms:thethirdparameter)
IADLXSystemMetricsList*systemMetricsList=NULL;
res=perfMonitoringServices->pVtbl->GetSystemMetricsHistory(perfMonitoringServices,10000,0,&systemMetricsList);
if(ADLX_SUCCEEDED(res))
{
//Getthesystemmetricssupport
IADLXSystemMetricsSupport*systemMetricsSupport=NULL;
ADLX_RESULTres1=perfMonitoringServices->pVtbl->GetSupportedSystemMetrics(perfMonitoringServices,&systemMetricsSupport);

//Displayallthesystemmetricsinthelist
IADLXSystemMetrics*systemMetrics=NULL;
for(inti=systemMetricsList->pVtbl->Begin(systemMetricsList);i!=systemMetricsList->pVtbl->End(systemMetricsList);++i)
{
printf("**********historicalsystemmetrics%d:**********\n",i+1);
ADLX_RESULTres2=systemMetricsList->pVtbl->At_SystemMetricsList(systemMetricsList,i,&systemMetrics);
//Displayfirstsystemmetricstimestamp,CPUusage,systemRAM,andSmartShift
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
GetTimeStamp(systemMetrics);
ShowCPUUsage(systemMetricsSupport,systemMetrics);
ShowSystemRAM(systemMetricsSupport,systemMetrics);
ShowSmartShift(systemMetricsSupport,systemMetrics);
ShowSmartShiftMax(systemMetricsSupport,systemMetrics);
}
printf("\n");
if(systemMetrics!=NULL)
{
systemMetrics->pVtbl->Release(systemMetrics);
systemMetrics=NULL;
}
}
if(systemMetricsSupport!=NULL)
{
systemMetricsSupport->pVtbl->Release(systemMetricsSupport);
systemMetricsSupport=NULL;
}
}
if(systemMetricsList!=NULL)
{
systemMetricsList->pVtbl->Release(systemMetricsList);
systemMetricsList=NULL;
}

//Stoptrackingperformancemetrics
res=perfMonitoringServices->pVtbl->StopPerformanceMetricsTracking(perfMonitoringServices);
if(ADLX_FAILED(res))
{
printf("Failedtostoptrackingperformancemetrics\n");
return;
}
}
