//Blockeventtoverifycallback
HANDLEdisplaySettingsEvent=NULL;
HANDLEdisplay3DLUTEvent=NULL;
//Quiteventtoexit
HANDLEquitEvent=NULL;

//Getthedisplayuniquename
voidDisplayUniqueName(IADLXDisplay*display,char*uniqueName);

//Callbacktohandlechangedevents
adlx_boolADLX_STD_CALLOnDisplaySettingsChanged(IADLXDisplaySettingsChangedListener*pThis,IADLXDisplaySettingsChangedEvent*pDisplaySettingsChangedEvent)
{
ADLX_SYNC_ORIGINorigin=pDisplaySettingsChangedEvent->pVtbl->GetOrigin(pDisplaySettingsChangedEvent);

if(origin==SYNC_ORIGIN_EXTERNAL)
{
IADLXDisplay*display=NULL;
pDisplaySettingsChangedEvent->pVtbl->GetDisplay(pDisplaySettingsChangedEvent,&display);
chardisplayName[128]="Unknown";
DisplayUniqueName(display,displayName);
if(pDisplaySettingsChangedEvent->pVtbl->IsColorDepthChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,ColorDepthischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsCustomColorBrightnessChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,CustomColorBrightnessischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsCustomColorContrastChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,CustomColorContrastischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsCustomColorHueChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,CustomColorHueischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsCustomColorSaturationChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,CustomColorSaturationischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsCustomColorTemperatureChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,CustomColorTemperatureischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsCustomResolutionChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,CustomResolutionischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsFreeSyncChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,FreeSyncischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsGPUScalingChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,GPUScalingischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsHDCPChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,HDCPischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsIntegerScalingChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,IntegerScalingischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsPixelFormatChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,PixelFormatischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsScalingModeChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,ScalingModeischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsVariBrightChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,VariBrightischanged\n",displayName);
}
elseif(pDisplaySettingsChangedEvent->pVtbl->IsVSRChanged(pDisplaySettingsChangedEvent))
{
printf("Display%sgetsyncevent,VSRischanged\n",displayName);
}

//GetIADLXDisplaySettingsChangedEvent1interface
IADLXDisplaySettingsChangedEvent1*pDisplaySettingChangedEvent1=NULL;
ADLX_RESULTres=pDisplaySettingsChangedEvent->pVtbl->QueryInterface(pDisplaySettingsChangedEvent,IID_IADLXDisplaySettingsChangedEvent1(),&pDisplaySettingChangedEvent1);
if(!ADLX_SUCCEEDED(res)||NULL==pDisplaySettingChangedEvent1)
{
printf("IID_IADLXDisplaySettingsChangedEvent1notsupported");
}
else
{
if(pDisplaySettingChangedEvent1->pVtbl->IsDisplayBlankingChanged(pDisplaySettingChangedEvent1))
{
printf("Display%sgetsyncevent,Displayblankingischanged\n",displayName);
}
}

//ReleasetheIADLXDisplaySettingsChangedEvent1interface
if(pDisplaySettingChangedEvent1!=NULL)
{
pDisplaySettingChangedEvent1->pVtbl->Release(pDisplaySettingChangedEvent1);
pDisplaySettingChangedEvent1=NULL;
}

//GetIADLXDisplaySettingsChangedEvent2interface
IADLXDisplaySettingsChangedEvent2*pDisplaySettingChangedEvent2=NULL;
res=pDisplaySettingsChangedEvent->pVtbl->QueryInterface(pDisplaySettingsChangedEvent,IID_IADLXDisplaySettingsChangedEvent2(),&pDisplaySettingChangedEvent2);
if(!ADLX_SUCCEEDED(res)||NULL==pDisplaySettingChangedEvent2)
{
printf("IID_IADLXDisplaySettingsChangedEvent2notsupported");
}
else
{
if(pDisplaySettingChangedEvent2->pVtbl->IsDisplayConnectivityExperienceChanged(pDisplaySettingChangedEvent2))
{
printf("Display%sgetsyncevent,Displayconnectivityexperienceischanged\n",displayName);
}
}

//ReleasetheIADLXDisplaySettingsChangedEvent2interface
if(pDisplaySettingChangedEvent2!=NULL)
{
pDisplaySettingChangedEvent2->pVtbl->Release(pDisplaySettingChangedEvent2);
pDisplaySettingChangedEvent2=NULL;
}

//ReleasetheDisplayinterface
if(display!=NULL)
{
display->pVtbl->Release(display);
display=NULL;
}
}
SetEvent(displaySettingsEvent);

//ReturntrueforADLXtocontinuenotifyingthenextlistener,orfalsetostopnotification
returntrue;
}

adlx_boolADLX_STD_CALLOnDisplay3DLUTChanged(IADLXDisplay3DLUTChangedListener*pThis,IADLXDisplay3DLUTChangedEvent*pDisplay3DLUTChangedEvent)
{
ADLX_SYNC_ORIGINorigin=pDisplay3DLUTChangedEvent->pVtbl->GetOrigin(pDisplay3DLUTChangedEvent);
if(origin==SYNC_ORIGIN_EXTERNAL)
{
IADLXDisplay*display=NULL;
pDisplay3DLUTChangedEvent->pVtbl->GetDisplay(pDisplay3DLUTChangedEvent,&display);
constchar*displayName=NULL;
display->pVtbl->Name(display,&displayName);
if(pDisplay3DLUTChangedEvent->pVtbl->IsSCEChanged(pDisplay3DLUTChangedEvent))
{
printf("Display%sgetsyncevent,SCEischanged\n",displayName);
}
elseif(pDisplay3DLUTChangedEvent->pVtbl->IsCustom3DLUTChanged(pDisplay3DLUTChangedEvent))
{
printf("Display%sgetsyncevent,Custom3DLUTischanged\n",displayName);
}

//ReleasetheDisplayinterface
if(display!=NULL)
{
display->pVtbl->Release(display);
display=NULL;
}
}
SetEvent(display3DLUTEvent);

//ReturntrueforADLXtocontinuenotifyingthenextlistener,orfalsetostopnotification
returntrue;
}

staticIADLXDisplaySettingsChangedListenerVtbldisplaySettingsVtbl={&OnDisplaySettingsChanged};
staticIADLXDisplaySettingsChangedListenerdisplaySettingsEventListener={&displaySettingsVtbl};
staticIADLXDisplay3DLUTChangedListenerVtbldisplay3DLUTVtbl={&OnDisplay3DLUTChanged};
staticIADLXDisplay3DLUTChangedListenerdisplay3DLUTEventListener={&display3DLUTVtbl};

DWORDWINAPIQuitWait(LPVOIDlpParam)
{
adlx_bool*loopFlag=(adlx_bool*)lpParam;
while(true)
{
//Non-blockingcheckingiftheI/Ocachehascharacters
if(_kbhit())
{
charc;
if((c=getchar())=='q'||c=='Q')
{
*loopFlag=false;
SetEvent(quitEvent);
break;
}
}
Sleep(100);
}
return0;
}

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Createblockevent
displaySettingsEvent=CreateEvent(NULL,FALSE,FALSE,NULL);
display3DLUTEvent=CreateEvent(NULL,FALSE,FALSE,NULL);
//Createquitevent
quitEvent=CreateEvent(NULL,TRUE,FALSE,NULL);
//Createeventarray
HANDLEeventArray[]={displaySettingsEvent,display3DLUTEvent,quitEvent};

//GetDisplayServices
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXDisplayServices*displayServices=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayServices);

if(ADLX_SUCCEEDED(res))
{
//GettheChangeHandleinterface
IADLXDisplayChangedHandling*changeHandle=NULL;
res=displayServices->pVtbl->GetDisplayChangedHandling(displayServices,&changeHandle);
if(ADLX_SUCCEEDED(res))
{
//Addcallback
changeHandle->pVtbl->AddDisplaySettingsEventListener(changeHandle,&displaySettingsEventListener);
changeHandle->pVtbl->AddDisplay3DLUTEventListener(changeHandle,&display3DLUTEventListener);

//Createathreadtodetectquitinput
adlx_boolloopFlag=true;
DWORDthreadId=0;
HANDLEhThread=CreateThread(NULL,0,QuitWait,&loopFlag,0,&threadId);

printf("\nWaitingfordisplaychangeevent...PressQ/qtoquit\n");
while(true)
{
//Waitforchangeeventorrequesttoquit
DWORDwaitRet=WaitForMultipleObjects(3,eventArray,FALSE,INFINITE);
if(!loopFlag)
{
CloseHandle(hThread);
break;
}
}

//Removecallback
changeHandle->pVtbl->RemoveDisplaySettingsEventListener(changeHandle,&displaySettingsEventListener);
changeHandle->pVtbl->RemoveDisplay3DLUTEventListener(changeHandle,&display3DLUTEventListener);
}
//ReleasetheChangeHandleinterface
if(changeHandle!=NULL)
{
changeHandle->pVtbl->Release(changeHandle);
changeHandle=NULL;
}
//ReleasetheDisplayServicesinterface
if(displayServices!=NULL)
{
displayServices->pVtbl->Release(displayServices);
displayServices=NULL;
}
}
else
{
printf("FailedtogetDisplayServices\n");
}
}
else
{
printf("ADLXinitializationfailed\n");
return0;
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Closeevent
if(displaySettingsEvent)
CloseHandle(displaySettingsEvent);
if(display3DLUTEvent)
CloseHandle(display3DLUTEvent);
if(quitEvent)
CloseHandle(quitEvent);

//Pausetoseetheprintout
system("pause");

return0;
}

voidDisplayUniqueName(IADLXDisplay*display,char*uniqueName)
{
if(NULL!=display&&NULL!=uniqueName)
{
constchar*gpuName=NULL;
display->pVtbl->Name(display,&gpuName);
adlx_sizeid;
display->pVtbl->UniqueId(display,&id);
sprintf_s(uniqueName,128,"name:%s,id:%zu",gpuName,id);
}
}
