#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"

//DisplaydisplayColorDepthSupport
voidShowDisplayColorDepthSupport(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//DisplaycurrentdisplayColorDepthstate
voidGetColorDepthState(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//SetdisplayColorDepth
voidSetColorDepthState(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,constintkey);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//Getdisplayservices
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService->pVtbl->GetDisplays(displayService,&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=displayList->pVtbl->At_DisplayList(displayList,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayService,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}
}

//ReleasethedisplayServiceinterface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXresult:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

voidShowDisplayColorDepthSupport(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
IADLXDisplayColorDepth*pDisplayColorDepth=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetColorDepth(pDisplayService,pDisplay,&pDisplayColorDepth);
if(ADLX_SUCCEEDED(res))
{
printf("===GetColorDepthSupported===\n");
adlx_boolsupported=false;
res=pDisplayColorDepth->pVtbl->IsSupported(pDisplayColorDepth,&supported);
printf("\tIsSupported,res%d,supported:%s\n",res,supported?"true":"false");
}

if(pDisplayColorDepth!=NULL)
{
pDisplayColorDepth->pVtbl->Release(pDisplayColorDepth);
pDisplayColorDepth=NULL;
}
}

voidGetSupportStateForEachColorDepth(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
IADLXDisplayColorDepth*pDisplayColorDepth=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetColorDepth(pDisplayService,pDisplay,&pDisplayColorDepth);
if(ADLX_SUCCEEDED(res)&&pDisplayColorDepth)
{
printf("===GetColorDepthSupportList===\n");

adlx_boolisSupported=false;
res=pDisplayColorDepth->pVtbl->IsSupportedBPC_6(pDisplayColorDepth,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_6,result%d,BPC_6support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedBPC_8(pDisplayColorDepth,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_8,result%d,BPC_8support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedBPC_10(pDisplayColorDepth,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_10,result%d,BPC_10support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedBPC_12(pDisplayColorDepth,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_12,result%d,BPC_12support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedBPC_14(pDisplayColorDepth,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_14,result%d,BPC_14support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedBPC_16(pDisplayColorDepth,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_16,result%d,BPC_16support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedColorDepth(pDisplayColorDepth,BPC_6,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_6,result%d,BPC_6support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedColorDepth(pDisplayColorDepth,BPC_8,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_8,result%d,BPC_8support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedColorDepth(pDisplayColorDepth,BPC_10,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_10,result%d,BPC_10support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedColorDepth(pDisplayColorDepth,BPC_12,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_12,result%d,BPC_12support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedColorDepth(pDisplayColorDepth,BPC_14,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_14,result%d,BPC_14support:%d\n",res,isSupported);

res=pDisplayColorDepth->pVtbl->IsSupportedColorDepth(pDisplayColorDepth,BPC_16,&isSupported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupportedBPC_16,result%d,BPC_16support:%d\n",res,isSupported);

//ReleasetheDisplayColorDepthinterface
pDisplayColorDepth->pVtbl->Release(pDisplayColorDepth);
pDisplayColorDepth=NULL;
}
}

voidGetColorDepthState(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
IADLXDisplayColorDepth*pDisplayColorDepth=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetColorDepth(pDisplayService,pDisplay,&pDisplayColorDepth);
if(ADLX_SUCCEEDED(res))
{
printf("===GetColorDepthEnabled===\n");
ADLX_COLOR_DEPTHcd=BPC_UNKNOWN;
res=pDisplayColorDepth->pVtbl->GetValue(pDisplayColorDepth,&cd);
char*cdStr="BPC_Unknown";
switch(cd)
{
caseBPC_6:
cdStr="BPC_6";
break;
caseBPC_8:
cdStr="BPC_8";
break;
caseBPC_10:
cdStr="BPC_10";
break;
}

printf("\tGetValue,result%d,colordepth:%s\n",res,cdStr);
}

if(pDisplayColorDepth!=NULL)
{
pDisplayColorDepth->pVtbl->Release(pDisplayColorDepth);
pDisplayColorDepth=NULL;
}
}

voidSetColorDepthState(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,constintkey)
{
IADLXDisplayColorDepth*pDisplayColorDepth=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetColorDepth(pDisplayService,pDisplay,&pDisplayColorDepth);
if(ADLX_SUCCEEDED(res))
{
ADLX_RESULTres=ADLX_FAIL;
switch(key)
{
//SetColorDepthBPC_6
case0:
printf("===Setcolordepth:BPC_6\n");
res=pDisplayColorDepth->pVtbl->SetValue(pDisplayColorDepth,BPC_6);
break;
//SetColorDepthBPC_8
case1:
printf("===Setcolordepth:BPC_8\n");
res=pDisplayColorDepth->pVtbl->SetValue(pDisplayColorDepth,BPC_8);
break;
//setColorDepthBPC_10
case2:
printf("===Setcolordepth:BPC_10\n");
res=pDisplayColorDepth->pVtbl->SetValue(pDisplayColorDepth,BPC_10);
break;
default:
break;
}
printf("\treturncodeis:%d(0meansSuccess)\n",res);
}

if(pDisplayColorDepth!=NULL)
{
pDisplayColorDepth->pVtbl->Release(pDisplayColorDepth);
pDisplayColorDepth=NULL;
}
}

voidMainMenu()
{
printf("\tChoosefromthefollowingoptions\n");

printf("\t->Press1todisplaycolordepthsupport\n");

printf("\t->Press2togetcolordepthstate\n");

printf("\t->Press3tosetcurrentcolordepthtoBPC_6\n");
printf("\t->Press4tosetcurrentcolordepthtoBPC_8\n");
printf("\t->Press5tosetcurrentcolordepthtoBPC_10\n");

printf("\t->Press6togetsupportstateforeachcolordepth\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplaydisplayColorDepthSupport
case'1':
ShowDisplayColorDepthSupport(pDisplayService,pDisplay);
break;

//DisplaycurrentdisplayColorDepthstate
case'2':
GetColorDepthState(pDisplayService,pDisplay);
break;

//SetdisplayColorDepth
case'3':
case'4':
case'5':
SetColorDepthState(pDisplayService,pDisplay,num-'3');
break;

case'6':
GetSupportStateForEachColorDepth(pDisplayService,pDisplay);
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}
