#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"

//Customcolortypes
enum
{
CUSTOM_COLOR_HUE=1,
CUSTOM_COLOR_SATURATION,
CUSTOM_COLOR_BRIGHTNESS,
CUSTOM_COLOR_CONTRAST,
CUSTOM_COLOR_TEMPERATURE
};

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//Handlerforuserinput
voidExecuteCommand(IADLXDisplayCustomColor*pDisplayColor_,intcolorType_,constchar*colorTypeString);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//Getdisplayservice
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService->pVtbl->GetDisplays(displayService,&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=displayList->pVtbl->At_DisplayList(displayList,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(displayService,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}
}

//ReleasethedisplayServiceinterface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXresult:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

ADLX_RESULTGetSupport(IADLXDisplayCustomColor*pDisplayColor_,intcolorType_,adlx_bool*supported)
{
switch(colorType_)
{
caseCUSTOM_COLOR_HUE:
returnpDisplayColor_->pVtbl->IsHueSupported(pDisplayColor_,supported);
caseCUSTOM_COLOR_SATURATION:
returnpDisplayColor_->pVtbl->IsSaturationSupported(pDisplayColor_,supported);
caseCUSTOM_COLOR_BRIGHTNESS:
returnpDisplayColor_->pVtbl->IsBrightnessSupported(pDisplayColor_,supported);
caseCUSTOM_COLOR_CONTRAST:
returnpDisplayColor_->pVtbl->IsContrastSupported(pDisplayColor_,supported);
caseCUSTOM_COLOR_TEMPERATURE:
returnpDisplayColor_->pVtbl->IsTemperatureSupported(pDisplayColor_,supported);
}
returnADLX_FAIL;
}

ADLX_RESULTGetRange(IADLXDisplayCustomColor*pDisplayColor_,intcolorType_,ADLX_IntRange*range)
{
switch(colorType_)
{
caseCUSTOM_COLOR_HUE:
returnpDisplayColor_->pVtbl->GetHueRange(pDisplayColor_,range);
caseCUSTOM_COLOR_SATURATION:
returnpDisplayColor_->pVtbl->GetSaturationRange(pDisplayColor_,range);
caseCUSTOM_COLOR_BRIGHTNESS:
returnpDisplayColor_->pVtbl->GetBrightnessRange(pDisplayColor_,range);
caseCUSTOM_COLOR_CONTRAST:
returnpDisplayColor_->pVtbl->GetContrastRange(pDisplayColor_,range);
caseCUSTOM_COLOR_TEMPERATURE:
returnpDisplayColor_->pVtbl->GetTemperatureRange(pDisplayColor_,range);
}
returnADLX_FAIL;
}

ADLX_RESULTGetValue(IADLXDisplayCustomColor*pDisplayColor_,intcolorType_,adlx_int*value)
{
switch(colorType_)
{
caseCUSTOM_COLOR_HUE:
returnpDisplayColor_->pVtbl->GetHue(pDisplayColor_,value);
caseCUSTOM_COLOR_SATURATION:
returnpDisplayColor_->pVtbl->GetSaturation(pDisplayColor_,value);
caseCUSTOM_COLOR_BRIGHTNESS:
returnpDisplayColor_->pVtbl->GetBrightness(pDisplayColor_,value);
caseCUSTOM_COLOR_CONTRAST:
returnpDisplayColor_->pVtbl->GetContrast(pDisplayColor_,value);
caseCUSTOM_COLOR_TEMPERATURE:
returnpDisplayColor_->pVtbl->GetTemperature(pDisplayColor_,value);
}
returnADLX_FAIL;
}

ADLX_RESULTSetValue(IADLXDisplayCustomColor*pDisplayColor_,intcolorType_,adlx_intvalue)
{
switch(colorType_)
{
caseCUSTOM_COLOR_HUE:
returnpDisplayColor_->pVtbl->SetHue(pDisplayColor_,value);
caseCUSTOM_COLOR_SATURATION:
returnpDisplayColor_->pVtbl->SetSaturation(pDisplayColor_,value);
caseCUSTOM_COLOR_BRIGHTNESS:
returnpDisplayColor_->pVtbl->SetBrightness(pDisplayColor_,value);
caseCUSTOM_COLOR_CONTRAST:
returnpDisplayColor_->pVtbl->SetContrast(pDisplayColor_,value);
caseCUSTOM_COLOR_TEMPERATURE:
returnpDisplayColor_->pVtbl->SetTemperature(pDisplayColor_,value);
}
returnADLX_FAIL;
}

voidExecuteCommand(IADLXDisplayCustomColor*pDisplayColor_,intcolorType_,constchar*colorTypeString)
{
printf("\tChoosefromfollowingoptionsfor%ssettings\n",colorTypeString);
printf("\t->Press1todisplaysupport\n");
printf("\t->Press2togetrangedata\n");
printf("\t->Press3todisplaycurrentvalue\n");
printf("\t->Press4tosetcurrentvalue\n");
printf("\t->PressX/xtogetbacktomainmenu\n");

intnum=0;
while((num=getchar())!='x'&&num!='X')
{
switch(num)
{
case'1':
{
adlx_boolsupported=false;
printf("===Get%ssupported===\n",colorTypeString);
GetSupport(pDisplayColor_,colorType_,&supported);
printf("\tIs%ssupported:%s\n",colorTypeString,supported?"true":"false");
break;
}
case'2':
{
ADLX_IntRangerange={0};
printf("===Get%sRangedata===\n",colorTypeString);
GetRange(pDisplayColor_,colorType_,&range);
printf("\tGet%sRange,minimum:%d,maximum:%d,step:%d\n",colorTypeString,range.minValue,range.maxValue,range.step);
break;
}
case'3':
{
adlx_intvalue=0;
printf("===Getcurrent%svalue===\n",colorTypeString);
GetValue(pDisplayColor_,colorType_,&value);
printf("\tGet%s,value:%d\n",colorTypeString,value);
break;
}
case'4':
{
ADLX_IntRangerange={0};
GetRange(pDisplayColor_,colorType_,&range);
adlx_intcurrentValue=0;
GetValue(pDisplayColor_,colorType_,&currentValue);
adlx_intbaseValue=(range.minValue+range.maxValue)/2;
adlx_intvalue=(currentValue==baseValue)?baseValue+range.step:baseValue;
printf("\tSet%s,value:%d\n",colorTypeString,value);
autores=SetValue(pDisplayColor_,colorType_,value);
printf("\treturncodeis:%d(0meansSuccess)\n",res);
break;
}
default:
break;
}
}

MainMenu();
}

voidShowCustomColorSettings(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,constintcolorType)
{
IADLXDisplayCustomColor*pDisplayCustomColor=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetCustomColor(pDisplayService,pDisplay,&pDisplayCustomColor);
if(ADLX_SUCCEEDED(res))
{
switch(colorType)
{
caseCUSTOM_COLOR_HUE:
ExecuteCommand(pDisplayCustomColor,colorType,"Hue");
break;
caseCUSTOM_COLOR_SATURATION:
ExecuteCommand(pDisplayCustomColor,colorType,"Saturation");
break;
caseCUSTOM_COLOR_BRIGHTNESS:
ExecuteCommand(pDisplayCustomColor,colorType,"Brightness");
break;
caseCUSTOM_COLOR_CONTRAST:
ExecuteCommand(pDisplayCustomColor,colorType,"Contrast");
break;
caseCUSTOM_COLOR_TEMPERATURE:
ExecuteCommand(pDisplayCustomColor,colorType,"Temperature");
break;
}

pDisplayCustomColor->pVtbl->Release(pDisplayCustomColor);
}
else
{
printf("Getcustomcolorfailed.\n");
}
}

voidMainMenu()
{
printf("\tChoosefromfollowingoptions\n");

printf("\t->Press1toenterHuesettings\n");
printf("\t->Press2toenterSaturationsettings\n");
printf("\t->Press3toenterBrightnesssettings\n");
printf("\t->Press4toenterContrastsettings\n");
printf("\t->Press5toenterTemperaturesettings\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
case'1':
ShowCustomColorSettings(pDisplayService,pDisplay,CUSTOM_COLOR_HUE);
break;
//DisplaySaturationsettings
case'2':
ShowCustomColorSettings(pDisplayService,pDisplay,CUSTOM_COLOR_SATURATION);
break;
//DisplayBrightnesssettings
case'3':
ShowCustomColorSettings(pDisplayService,pDisplay,CUSTOM_COLOR_BRIGHTNESS);
break;
//DisplayContrastsettings
case'4':
ShowCustomColorSettings(pDisplayService,pDisplay,CUSTOM_COLOR_CONTRAST);
break;
//DisplayTemperaturesettings
case'5':
ShowCustomColorSettings(pDisplayService,pDisplay,CUSTOM_COLOR_TEMPERATURE);
break;
//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}
