#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"

//Displaycustomresolutionsupport
voidShowCustomResolutionSupport(IADLXDisplayCustomResolution*pCustomResolution);

//Displayavailabedisplayresolutions
voidGetResolutionList(IADLXDisplayCustomResolution*pCustomResolution,IADLXDisplayResolutionList**ppResolutionList);

//Displaycurrentdisplayresolution
voidGetCurrentResolution(IADLXDisplayCustomResolution*pCustomResolution,IADLXDisplayResolution**ppResolution);

//Createnewcustomizeddisplayresolution
voidCreateNewResolution(IADLXDisplayCustomResolution*pCustomResolution,IADLXDisplayResolution*pResolution);

//Deletedisplayresolution
voidDeleteResolution(IADLXDisplayCustomResolution*pCustomResolution,IADLXDisplayResolution*pResolution);

//Printoutgivenresolution
voidPrintResolution(IADLXDisplayResolution*pResolution);

//Printoutgivenresolutionlist
voidPrintResolutions(IADLXDisplayResolutionList*pResolutionList);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//Getdisplayservice
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService->pVtbl->GetDisplays(displayService,&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=displayList->pVtbl->At_DisplayList(displayList,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Showamenuwithoptionstochosefrom
MainMenu();
//Getandexecutethechoice
MenuControl(displayService,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}
}

//ReleasethedisplayServiceinterface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

voidShowCustomResolutionSupport(IADLXDisplayCustomResolution*pCustomResolution)
{
printf("===Getcustomresolutionsupported===\n");
adlx_boolsupported=false;
ADLX_RESULTres=pCustomResolution->pVtbl->IsSupported(pCustomResolution,&supported);
printf("\tIsSupported,result%d,supported:%s\n",res,supported?"true":"false");
}

voidGetResolutionList(IADLXDisplayCustomResolution*pCustomResolution,IADLXDisplayResolutionList**ppResolutionList)
{
ADLX_RESULTres=pCustomResolution->pVtbl->GetResolutionList(pCustomResolution,ppResolutionList);
if(ADLX_FAILED(res))
printf("GetResolutionList,failed.\n");
}

voidGetCurrentResolution(IADLXDisplayCustomResolution*pCustomResolution,IADLXDisplayResolution**ppResolution)
{
ADLX_RESULTres=pCustomResolution->pVtbl->GetCurrentAppliedResolution(pCustomResolution,ppResolution);
if(ADLX_FAILED(res))
printf("GetCurrentResolution,failed.\n");
}

voidCreateNewResolution(IADLXDisplayCustomResolution*pCustomResolution,IADLXDisplayResolution*pResolution)
{
ADLX_RESULTres=pCustomResolution->pVtbl->CreateNewResolution(pCustomResolution,pResolution);
if(ADLX_FAILED(res))
printf("CreateNewResolution,failed.\n");
}

voidDeleteResolution(IADLXDisplayCustomResolution*pCustomResolution,IADLXDisplayResolution*pResolution)
{
PrintResolution(pResolution);
ADLX_RESULTres=pCustomResolution->pVtbl->DeleteResolution(pCustomResolution,pResolution);
if(ADLX_FAILED(res))
printf("DeleteResolution,failed.\n");
}

voidPrintResolution(IADLXDisplayResolution*pResolution)
{
ADLX_CustomResolutioncr;
pResolution->pVtbl->GetValue(pResolution,&cr);

char*sPresention=(cr.presentation==INTERLACED)?"Interlaced":"Progressive";
char*sTimingStandard="Manual";
char*sHPolarity=(cr.detailedTiming.hPolarity==POSITIVE)?"Positive":"Negative";
char*sVPolarity=(cr.detailedTiming.vPolarity==POSITIVE)?"Positive":"Negative";

switch(cr.timingStandard)
{
caseMANUAL:
sTimingStandard="Manual";
break;
caseDMT:
sTimingStandard="DMT";
break;
caseGTF:
sTimingStandard="GTF";
break;
caseCVT_RB:
sTimingStandard="CVT-ReducedBlanking";
break;
caseCVT:
sTimingStandard="CVT";
break;
default:
break;
}

charbuff[1024]={0};
intoffset=snprintf(buff,sizeof(buff),"\nResolution(Px)\t\t%dx%d\n",cr.resWidth,cr.resHeight);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"RefreshRate(Hz)\t\t%d\n",cr.refreshRate);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"Presentation\t\t\t%s\n",sPresention);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"TimingStandard\t\t%s\n",sTimingStandard);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"G.PixelClock(kHz)\t\t%d\n",cr.GPixelClock);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"TimingInformation\t\t\tHorizontal\tVertical\n");
offset+=snprintf(buff+offset,sizeof(buff)-offset,"TimingTotal\t\t\t%d\t%d\n",cr.detailedTiming.hTotal,cr.detailedTiming.vTotal);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"TimingDisplay\t\t%d\t%d\n",cr.detailedTiming.hDisplay,cr.detailedTiming.vDisplay);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"TimingFrontPorch\t\t%d\t%d\n",cr.detailedTiming.hFrontPorch,cr.detailedTiming.vFrontPorch);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"TimingSyncWidth\t\t%d\t%d\n",cr.detailedTiming.hSyncWidth,cr.detailedTiming.vSyncWidth);
offset+=snprintf(buff+offset,sizeof(buff)-offset,"TimingPolarity\t\t%s%s\n",sHPolarity,sVPolarity);

printf("%s\n",buff);
}

voidPrintResolutions(IADLXDisplayResolutionList*pResolutionList)
{
printf("Printoutresolutionlist==\n");
if(pResolutionList==NULL)
{
printf("ResolutionlistisNULL\n");
return;
}

if(pResolutionList->pVtbl->Size(pResolutionList)==0)
{
printf("Resolutionlistisempty!\n");
return;
}

ADLX_RESULTres=ADLX_FAIL;
for(adlx_uintcrt=0;crt<pResolutionList->pVtbl->Size(pResolutionList);crt++)
{
IADLXDisplayResolution*pDispResolution=NULL;
res=pResolutionList->pVtbl->At_DisplayResolutionList(pResolutionList,crt,&pDispResolution);
if(ADLX_SUCCEEDED(res))
{
printf("Resolutionindex:%d\n",crt);
PrintResolution(pDispResolution);
}
}
}

voidMainMenu()
{
printf("\tChoosefromfollowingoptions\n");

printf("\t->Press1todisplaycustomresolutionsupport\n");

printf("\t->Press2togetcustomresolutionlist\n");

printf("\t->Press3togetcurrentappliedresolution\n");
printf("\t->Press4tocreatenewcustomizedresolution\n");
printf("\t->Press5todeleteavaibleresolution\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
IADLXDisplayCustomResolution*pCustomResolution=NULL;
pDisplayService->pVtbl->GetCustomResolution(pDisplayService,pDisplay,&pCustomResolution);

if(!pCustomResolution)
{
printf("Menucontrol:cannotretrivecustomresolutioninstance!\n");
return;
}

intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displaycustomresolutionsupport
case'1':
ShowCustomResolutionSupport(pCustomResolution);
break;

//Displaycurrentavailableresolutions
case'2':
{
printf("===GetResolutionList:===\n");
IADLXDisplayResolutionList*pResolutionList=NULL;
GetResolutionList(pCustomResolution,&pResolutionList);
if(pResolutionList!=NULL)
{
PrintResolutions(pResolutionList);
pResolutionList->pVtbl->Release(pResolutionList);
}
break;
}

//Getcurrentappliedresolution
case'3':
{
printf("===Getcurrentresolution:===\n");
IADLXDisplayResolution*pDisplayResolution=NULL;
GetCurrentResolution(pCustomResolution,&pDisplayResolution);
if(pDisplayResolution!=NULL)
{
PrintResolution(pDisplayResolution);
pDisplayResolution->pVtbl->Release(pDisplayResolution);
}
break;
}
//Createcustomizedresolution
case'4':
{
printf("===Createnewresolution:===\n");
IADLXDisplayResolution*pDisplayResolution=NULL;
GetCurrentResolution(pCustomResolution,&pDisplayResolution);
if(pDisplayResolution)
{
ADLX_CustomResolutioncustomRes;
pDisplayResolution->pVtbl->GetValue(pDisplayResolution,&customRes);

customRes.resWidth=1920;
customRes.resHeight=1080;
customRes.detailedTiming.hDisplay=1920;
customRes.detailedTiming.vDisplay=1080;
pDisplayResolution->pVtbl->SetValue(pDisplayResolution,customRes);

PrintResolution(pDisplayResolution);
CreateNewResolution(pCustomResolution,pDisplayResolution);
pDisplayResolution->pVtbl->Release(pDisplayResolution);
}
break;
}
//Deleteavailableresolution
case'5':
{
printf("===Deleteresolution:===\n");
IADLXDisplayResolutionList*pResolutionList=NULL;
GetResolutionList(pCustomResolution,&pResolutionList);
if(pResolutionList!=NULL&&pResolutionList->pVtbl->Size(pResolutionList)>0)
{
IADLXDisplayResolution*pDisplayResolution=NULL;
ADLX_RESULTres=pResolutionList->pVtbl->At_DisplayResolutionList(pResolutionList,0,&pDisplayResolution);
if(ADLX_SUCCEEDED(res))
{
DeleteResolution(pCustomResolution,pDisplayResolution);
pDisplayResolution->pVtbl->Release(pDisplayResolution);
}
pResolutionList->pVtbl->Release(pResolutionList);
}
break;
}
//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}

pCustomResolution->pVtbl->Release(pCustomResolution);
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}
