voidGetEnabledLinkProtection(constIADLXDisplayServices2*displayService2,constIADLXDisplay*display);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServices2*displayService2,constIADLXDisplay*display);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservice
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//Getdisplayservice
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
IADLXDisplayServices2*displayServices2=NULL;
ADLX_RESULTres2=displayService->pVtbl->QueryInterface(displayService,IID_IADLXDisplayServices2(),(void**)&displayServices2);

//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService->pVtbl->GetDisplays(displayService,&displayList);
if(ADLX_SUCCEEDED(res)&&ADLX_SUCCEEDED(res2))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=displayList->pVtbl->At_DisplayList(displayList,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(displayServices2,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}

if(NULL!=displayServices2)
{
displayServices2->pVtbl->Release(displayServices2);
displayServices2=NULL;
}
}

//ReleasethedisplayServiceinterface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

voidShowSupportedHDMIQualityDetection(constIADLXDisplayServices2*displayService2,constIADLXDisplay*display)
{
IADLXDisplayConnectivityExperience*displayConnectivityExperience;
ADLX_RESULTres=displayService2->pVtbl->GetDisplayConnectivityExperience(displayService2,display,&displayConnectivityExperience);
if(ADLX_SUCCEEDED(res))
{
printf("===GetHDMIqualitydetectionSupported===\n");
adlx_boolsupported=false;
res=displayConnectivityExperience->pVtbl->IsSupportedHDMIQualityDetection(displayConnectivityExperience,&supported);
printf("\tHDMIqualitydetectionissupportedonthedisplay:%s,returncode:%d(0meanssuccess)\n",supported?"true":"false",res);
displayConnectivityExperience->pVtbl->Release(displayConnectivityExperience);
}
}

