voidSetEnabledHDMIQualityDetection(constIADLXDisplayServices2*displayService2,constIADLXDisplay*display,adlx_boolenableDisable)
{
IADLXDisplayConnectivityExperience*displayConnectivityExperience;
ADLX_RESULTres=displayService2->pVtbl->GetDisplayConnectivityExperience(displayService2,display,&displayConnectivityExperience);
if(ADLX_SUCCEEDED(res))
{
if(!enableDisable)
{
res=displayConnectivityExperience->pVtbl->SetEnabledHDMIQualityDetection(displayConnectivityExperience,false);
printf("\tSetenabledtofalse,result:%d(0meanssuccess)\n",res);
}
else
{
res=displayConnectivityExperience->pVtbl->SetEnabledHDMIQualityDetection(displayConnectivityExperience,true);
printf("\tSetenabledtotrue,result:%d(0meanssuccess)\n",res);
}
displayConnectivityExperience->pVtbl->Release(displayConnectivityExperience);
}
}

voidGetDPLinkRate(constIADLXDisplayServices2*displayService2,constIADLXDisplay*display)
{
IADLXDisplayConnectivityExperience*displayConnectivityExperience;
ADLX_RESULTres=displayService2->pVtbl->GetDisplayConnectivityExperience(displayService2,display,&displayConnectivityExperience);
if(ADLX_SUCCEEDED(res))
{
printf("===GetDPlinkrate===\n");
ADLX_DP_LINK_RATEDPLinkRate=DP_LINK_RATE_UNKNOWN;
res=displayConnectivityExperience->pVtbl->GetDPLinkRate(displayConnectivityExperience,&DPLinkRate);
char*DPLinkRateStr="DP_LINK_RATE_UNKNOWN";
switch(DPLinkRate)
{
caseDP_LINK_RATE_RBR:
DPLinkRateStr="DP_LINK_RATE_RBR";
break;
caseDP_LINK_RATE_2_16GBPS:
DPLinkRateStr="DP_LINK_RATE_2_16GBPS";
break;
caseDP_LINK_RATE_2_43GBPS:
DPLinkRateStr="DP_LINK_RATE_2_16GBPS";
break;
caseDP_LINK_RATE_HBR:
DPLinkRateStr="DP_LINK_RATE_HBR";
break;
caseDP_LINK_RATE_4_32GBPS:
DPLinkRateStr="DP_LINK_RATE_4_32GBPS";
break;
caseDP_LINK_RATE_HBR2:
DPLinkRateStr="DP_LINK_RATE_HBR2";
break;
caseDP_LINK_RATE_HBR3:
DPLinkRateStr="DP_LINK_RATE_HBR3";
break;
caseDP_LINK_RATE_UHBR10:
DPLinkRateStr="DP_LINK_RATE_UHBR10";
break;
caseDP_LINK_RATE_UHBR13D5:
DPLinkRateStr="DP_LINK_RATE_UHBR13D5";
break;
caseDP_LINK_RATE_UHBR20:
DPLinkRateStr="DP_LINK_RATE_UHBR20";
break;
default:
break;
}
printf("\tTheDPlinkrateonthedisplayis%s,returncode:%d(0meanssuccess)\n",DPLinkRateStr,res);
displayConnectivityExperience->pVtbl->Release(displayConnectivityExperience);
}
}

