printf("\t->Pressintegerin[-2:2]tosetrelativepre-emphasisorreturnmainmenu\n");
intnum=0;
if(scanf_s("%d",&num)&&(-2<=num&&num<=2))
{
SetRelativePreEmphasis(displayService2,display,num);
}
}

