#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"

//DisplaydisplayVari-Brightsupport
voidShowDisplayVariBrightSupport(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//DisplaycurrentdisplayVari-Brightstate
voidGetVariBrightEnabled(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//SetdisplayVari-Brightstatetoenableordisable
voidSetVariBrightEnabled(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,adlx_boolenableDisable);

//GetdisplayVari-Brightcurrentstate
voidGetCurrentVariBright(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,intuserChoice);

//GetdisplayVari-Brightcurrentstate
voidSetCurrentVariBright(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,intuserChoice);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//Waitforexitwitherrormessage
intWaitAndExit(char*msg,intretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservice
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//Getdisplayservice
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService->pVtbl->GetDisplays(displayService,&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=displayList->pVtbl->At_DisplayList(displayList,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(displayService,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}
}

//ReleasethedisplayServiceinterface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

voidShowDisplayVariBrightSupport(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
IADLXDisplayVariBright*displayVariBright;
ADLX_RESULTres=pDisplayService->pVtbl->GetVariBright(pDisplayService,pDisplay,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
printf("===GetVari-Brightsupported===\n");
adlx_boolsupported=false;
res=displayVariBright->pVtbl->IsSupported(displayVariBright,&supported);
printf("\tVari-Brightissupportedonthedisplay:%s,returncode:%d(0meanssuccess)\n",supported?"true":"false",res);
displayVariBright->pVtbl->Release(displayVariBright);
}
}

voidGetVariBrightEnabled(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
IADLXDisplayVariBright*displayVariBright;
ADLX_RESULTres=pDisplayService->pVtbl->GetVariBright(pDisplayService,pDisplay,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
printf("===GetVariBrightenabled===\n");
adlx_boolenabled=false;
res=displayVariBright->pVtbl->IsEnabled(displayVariBright,&enabled);
printf("\tVari-Brightisenabledonthedisplay:%s,returncode:%d(0meanssuccess)\n",enabled?"true":"false",res);
displayVariBright->pVtbl->Release(displayVariBright);
}
}

voidSetVariBrightEnabled(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,adlx_boolenableDisable)
{
IADLXDisplayVariBright*displayVariBright;
ADLX_RESULTres=pDisplayService->pVtbl->GetVariBright(pDisplayService,pDisplay,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
if(!enableDisable)
{
res=displayVariBright->pVtbl->SetEnabled(displayVariBright,false);
printf("\tSetenabledtofalse,result:%d\n",res);
}
else
{
res=displayVariBright->pVtbl->SetEnabled(displayVariBright,true);
printf("\tSetenabledtotrue,result:%d\n",res);
}
displayVariBright->pVtbl->Release(displayVariBright);
}
}

voidGetCurrentVariBright(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,intuserChoice)
{
IADLXDisplayVariBright*displayVariBright;
ADLX_RESULTres=pDisplayService->pVtbl->GetVariBright(pDisplayService,pDisplay,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
adlx_boolapplied=false;
switch(userChoice)
{
case0:
res=displayVariBright->pVtbl->IsCurrentMaximizeBrightness(displayVariBright,&applied);
printf("\tMaximizeBrightnesspresetisusedonthedisplay:%s,returncode:%d(0meanssuccess)\n",(applied?"Yes":"No"),res);
break;

case1:
res=displayVariBright->pVtbl->IsCurrentOptimizeBrightness(displayVariBright,&applied);
printf("\tOptimizeBrightnesspresetisusedonthedisplay:%s,returncode:%d(0meanssuccess)\n",(applied?"Yes":"No"),res);
break;

case2:
res=displayVariBright->pVtbl->IsCurrentBalanced(displayVariBright,&applied);
printf("\tBalancedpresetisusedonthedisplay:%s,returncode:%d(0meanssuccess)\n",(applied?"Yes":"No"),res);
break;

case3:
res=displayVariBright->pVtbl->IsCurrentOptimizeBattery(displayVariBright,&applied);
printf("\tOptimizeBatterypresetisusedonthedisplay:%s,returncode:%d(0meanssuccess)\n",(applied?"Yes":"No"),res);
break;

case4:
res=displayVariBright->pVtbl->IsCurrentMaximizeBattery(displayVariBright,&applied);
printf("\tMaximizeBatterypresetisusedonthedisplay:%s,returncode:%d(0meanssuccess)\n",(applied?"Yes":"No"),res);
break;
}
displayVariBright->pVtbl->Release(displayVariBright);
}
}

voidSetCurrentVariBright(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,intuserChoice)
{
IADLXDisplayVariBright*displayVariBright;
ADLX_RESULTres=pDisplayService->pVtbl->GetVariBright(pDisplayService,pDisplay,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
switch(userChoice)
{
case0:
res=displayVariBright->pVtbl->SetMaximizeBrightness(displayVariBright);
printf("\tSetMaximizeBrightnesspreset,result:%d\n",res);
break;

case1:
res=displayVariBright->pVtbl->SetOptimizeBrightness(displayVariBright);
printf("\tSetOptimizeBrightnesspreset,result:%d\n",res);
break;

case2:
res=displayVariBright->pVtbl->SetBalanced(displayVariBright);
printf("\tSetBalancedpreset,result:%d\n",res);
break;
case3:
res=displayVariBright->pVtbl->SetOptimizeBattery(displayVariBright);
printf("\tSetOptimizeBatterypreset,result:%d\n",res);
break;

case4:
res=displayVariBright->pVtbl->SetMaximizeBattery(displayVariBright);
printf("\tSetMaximizeBatterypreset,result:%d\n",res);
break;
}
displayVariBright->pVtbl->Release(displayVariBright);
}
}

voidMainMenu()
{
printf("\tChoosefromthefollowingoptions:\n");

printf("\t->Press1todisplayVari-Brightsupport\n");
printf("\t->Press2togetVari-BrightEnablestate\n");
printf("\t->Press3toenableVari-Bright\n");
printf("\t->Press4toDisbaleVari-Bright\n");
printf("\t->Press5tocheckifVari-Brightmodeismaximizedbrightnesspresetonthedisplay\n");
printf("\t->Press6tocheckifVari-Brightmodeisoptimizedbrightnesspresetonthedisplay\n");
printf("\t->Press7tocheckifVari-Brightmodeisbalancedpresetonthedisplay\n");
printf("\t->Press8tocheckifVari-Brightmodeisoptimizedbatterypresetonthedisplay\n");
printf("\t->Press9tocheckifVari-Brightmodeismaximizedbatterypresetonthedisplay\n");
printf("\t->PressatosettheVari-Brightmodetomaximizedbrightnesspresetonthedisplay\n");
printf("\t->PressbtosettheVari-Brightmodetooptimizedbrightnesspresetonthedisplay\n");
printf("\t->PressctosettheVari-Brightmodetobalancedpresetonthedisplay\n");
printf("\t->PressdtosettheVari-Brightmodetooptimizedbatterypresetonthedisplay\n");
printf("\t->PressetosetstheVari-Brightmodetomaximizedbatterypresetonthedisplay\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaythemainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplaydisplayVariBrightsupport
case'1':
ShowDisplayVariBrightSupport(pDisplayService,pDisplay);
break;

//DisplaycurrentdisplayVariBrightstate
case'2':
GetVariBrightEnabled(pDisplayService,pDisplay);
break;

//SetdisplayVariBrightenable
case'3':
SetVariBrightEnabled(pDisplayService,pDisplay,true);
break;

//SetdisplayVariBrightdisable
case'4':
SetVariBrightEnabled(pDisplayService,pDisplay,false);
break;

case'5':
case'6':
case'7':
case'8':
case'9':
GetCurrentVariBright(pDisplayService,pDisplay,num-'5');
break;

case'a':
case'b':
case'c':
case'd':
case'e':
SetCurrentVariBright(pDisplayService,pDisplay,num-'a');
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;

default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(char*msg,intretCode)
{
//Printoutthemessageandpausetoseeitbefore returningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("Pause");
returnretCode;
}
