}

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplaydisplayVariBrightsupport
case'1':
ShowDisplayVariBrightSupport(pDisplayService,pDisplay);
break;

//DisplaycurrentdisplayVariBrightstate
case'2':
GetVariBrightEnabled(pDisplayService,pDisplay);
break;

//SetdisplayVariBrightenable
case'3':
SetVariBrightEnabled(pDisplayService,pDisplay,true);
break;

//SetdisplayVariBrightdisable
case'4':
SetVariBrightEnabled(pDisplayService,pDisplay,false);
break;

case'5':
case'6':
case'7':
case'8':
case'9':
GetCurrentVariBright(pDisplayService,pDisplay,num-'5');
break;

case'a':
case'b':
case'c':
case'd':
case'e':
SetCurrentVariBright(pDisplayService,pDisplay,num-'a');
break;

case'f':
case'g':
case'h':
case'i':
{
IADLXDisplayVariBright*displayVariBright=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetVariBright(pDisplayService,pDisplay,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
IADLXDisplayVariBright1*displayVariBright1=NULL;
ADLX_RESULTres=displayVariBright->pVtbl->QueryInterface(displayVariBright,IID_IADLXDisplayVariBright1(),(void**)&displayVariBright1);
if(ADLX_SUCCEEDED(res))
{
VariBrightScalingMenuControl(displayVariBright1,num-'f');
if(NULL!=displayVariBright1)
{
displayVariBright1->pVtbl->Release(displayVariBright1);
displayVariBright1=NULL;
}
}
else
printf("FailedtogettheVari-BrightScaling\n");

if(NULL!=displayVariBright)
{
displayVariBright->pVtbl->Release(displayVariBright);
displayVariBright=NULL;
}
}
else
printf("FailedtogettheVari-Bright\n");
break;
}

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;

default:
break;
}
}
}

//Vari-BrightScalingmenuactioncontrol
voidVariBrightScalingMenuControl(IADLXDisplayVariBright1*displayVariBright1,intuserChoice)
{
switch(userChoice)
{
case0:
