default:
break;
}
}

//Waitforexitwitherrormessage
intWaitAndExit(char*msg,intretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("Pause");
returnretCode;
}
