#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"

//DisplaydisplayPixelFormatsupport
voidShowDisplayPixelFormatSupport(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//DisplaycurrentdisplayPixelFormatstate
voidGetPixelFormatState(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//SetdisplayPixelFormat
voidSetPixelFormatState(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,constintkey);

//Getsupportstateforeachpixelformat
voidGetSupportStateForEachPixelFormat(IADLXDisplayServices*displayService,IADLXDisplay*display);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservice
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//Getdisplayservice
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService->pVtbl->GetDisplays(displayService,&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=displayList->pVtbl->At_DisplayList(displayList,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(displayService,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}
}

//ReleasethedisplayServiceinterface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

voidShowDisplayPixelFormatSupport(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
IADLXDisplayPixelFormat*pDisplayPixelFormat=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetPixelFormat(pDisplayService,pDisplay,&pDisplayPixelFormat);
if(ADLX_SUCCEEDED(res))
{
printf("===GetPixelFormatsupported===\n");
adlx_boolsupported=false;
res=pDisplayPixelFormat->pVtbl->IsSupported(pDisplayPixelFormat,&supported);
printf("\tPixelformatissupportedonthedisplay:%s,returncode:%d(0meanssuccess)\n",supported?"true":"false",res);
pDisplayPixelFormat->pVtbl->Release(pDisplayPixelFormat);
}
}

voidGetPixelFormatState(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
IADLXDisplayPixelFormat*pDisplayPixelFormat=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetPixelFormat(pDisplayService,pDisplay,&pDisplayPixelFormat);
if(ADLX_SUCCEEDED(res))
{
printf("===GetPixelFormatenabled===\n");
ADLX_PIXEL_FORMATpf=FORMAT_UNKNOWN;
res=pDisplayPixelFormat->pVtbl->GetValue(pDisplayPixelFormat,&pf);
char*pfStr="FORMAT_UNKNOWN";
switch(pf)
{
caseRGB_444_FULL:
pfStr="RGB_444_FULL";
break;
caseYCBCR_444:
pfStr="YCBCR_444";
break;
caseYCBCR_422:
pfStr="YCBCR_422";
break;
caseRGB_444_LIMITED:
pfStr="RGB_444_LIMITED";
break;
caseYCBCR_420:
pfStr="YCBCR_420";
break;
default:
break;
}
printf("\tThepixelformatonthedisplayis%s,returncode:%d(0meanssuccess)\n",pfStr,res);
pDisplayPixelFormat->pVtbl->Release(pDisplayPixelFormat);
}
}

voidSetPixelFormatState(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay,constintkey)
{
IADLXDisplayPixelFormat*pDisplayPixelFormat=NULL;
ADLX_RESULTres=pDisplayService->pVtbl->GetPixelFormat(pDisplayService,pDisplay,&pDisplayPixelFormat);
if(ADLX_SUCCEEDED(res))
{
ADLX_RESULTres=ADLX_FAIL;
switch(key)
{
//SetPixelFormatRGB_444_FULL
case0:
printf("===SetpixelformattoRGB_444_FULL\n");
res=pDisplayPixelFormat->pVtbl->SetValue(pDisplayPixelFormat,RGB_444_FULL);
break;
//SetPixelFormatYCBCR_444
case1:
printf("===SetpixelformattoYCBCR_444\n");
res=pDisplayPixelFormat->pVtbl->SetValue(pDisplayPixelFormat,YCBCR_444);
break;
//SetpixelformatYCBCR_422
case2:
printf("===SetpixelformattoYCBCR_422\n");
res=pDisplayPixelFormat->pVtbl->SetValue(pDisplayPixelFormat,YCBCR_422);
break;
//SetpixelformatRGB_444_LIMITED
case3:
printf("===SetpixelformattoRGB_444_LIMITED\n");
res=pDisplayPixelFormat->pVtbl->SetValue(pDisplayPixelFormat,RGB_444_LIMITED);
break;
default:
break;
}
printf("\treturncodeis:%d(0meanssuccess)\n",res);
pDisplayPixelFormat->pVtbl->Release(pDisplayPixelFormat);
}
}

//Getsupportstateforeachpixelformat
voidGetSupportStateForEachPixelFormat(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
IADLXDisplayPixelFormat*displayPixelFormat=NULL;
ADLX_RESULTres=displayService->pVtbl->GetPixelFormat(displayService,display,&displayPixelFormat);
if(ADLX_SUCCEEDED(res)&&displayPixelFormat)
{
printf("===Getpixelformatlist===\n");
adlx_boolsupport=false;

res=displayPixelFormat->pVtbl->IsSupportedRGB444Full(displayPixelFormat,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportRGB4:4:4PCStandard(FullRGB):%d\n",support);
res=displayPixelFormat->pVtbl->IsSupportedYCbCr444(displayPixelFormat,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportYCbCr4:4:4:%d\n",support);
res=displayPixelFormat->pVtbl->IsSupportedYCbCr422(displayPixelFormat,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportYCbCr4:2:2:%d\n",support);
res=displayPixelFormat->pVtbl->IsSupportedRGB444Limited(displayPixelFormat,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportRGB4:4:4Studio(LimitedRGB):%d\n",support);
res=displayPixelFormat->pVtbl->IsSupportedYCbCr420(displayPixelFormat,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportYCbCr4:2:0:%d\n",support);

res=displayPixelFormat->pVtbl->IsSupportedPixelFormat(displayPixelFormat,RGB_444_FULL,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportRGB4:4:4PCStandard(FullRGB):%d\n",support);
res=displayPixelFormat->pVtbl->IsSupportedPixelFormat(displayPixelFormat,YCBCR_444,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportYCbCr4:4:4:%d\n",support);
res=displayPixelFormat->pVtbl->IsSupportedPixelFormat(displayPixelFormat,YCBCR_422,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportYCbCr4:2:2:%d\n",support);
res=displayPixelFormat->pVtbl->IsSupportedPixelFormat(displayPixelFormat,RGB_444_LIMITED,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportRGB4:4:4Studio(LimitedRGB):%d\n",support);
res=displayPixelFormat->pVtbl->IsSupportedPixelFormat(displayPixelFormat,YCBCR_420,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssupportYCbCr4:2:0:%d\n",support);

displayPixelFormat->pVtbl->Release(displayPixelFormat);
displayPixelFormat=NULL;
}
}

voidMainMenu()
{
printf("\tChoosefromthefollowingoptions:\n");

printf("\t->Press1todisplaypixelformatsupport\n");

printf("\t->Press2togetpixelformatstate\n");

printf("\t->Press3tosetcurrentpixelformattoRGB_444_FULL\n");
printf("\t->Press4tosetcurrentpixelformattoYCBCR_444\n");
printf("\t->Press5tosetcurrentpixelformattoYCBCR_422\n");
printf("\t->Press6tosetcurrentpixelformattoRGB_444_LIMITED\n");

printf("\t->Press7togetsupportstateforeachpixelformat\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*pDisplayService,IADLXDisplay*pDisplay)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplaydisplayPixelFormatsupport
case'1':
ShowDisplayPixelFormatSupport(pDisplayService,pDisplay);
break;

//DisplaycurrentdisplayPixelFormatstate
case'2':
GetPixelFormatState(pDisplayService,pDisplay);
break;

//SetdisplayPixelFormat
case'3':
case'4':
case'5':
case'6':
SetPixelFormatState(pDisplayService,pDisplay,num-'3');
break;

//Getsupportstateforeachpixelformat
case'7':
GetSupportStateForEachPixelFormat(pDisplayService,pDisplay);
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}
