#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/ISmartAccessMemory.h"
#include"SDK/Include/IGPUTuning1.h"

staticHANDLESAMEvent=NULL;

staticadlx_boolADLX_STD_CALLOnGPUTuningChanged(IADLXGPUTuningChangedListener*pGPUTuningChangedListener,IADLXGPUTuningChangedEvent*pGPUTuningChangedEvent)
{
IADLXGPUTuningChangedEvent1*pGPUTuningChangedEvent1=NULL;
pGPUTuningChangedEvent->pVtbl->QueryInterface(pGPUTuningChangedEvent,IID_IADLXGPUTuningChangedEvent1(),&pGPUTuningChangedEvent1);
adlx_boolSAMChange=pGPUTuningChangedEvent1->pVtbl->IsSmartAccessMemoryChanged(pGPUTuningChangedEvent1);
if(SAMChange)
{
adlx_boolenabled=false,completed=false;
pGPUTuningChangedEvent1->pVtbl->GetSmartAccessMemoryStatus(pGPUTuningChangedEvent1,&enabled,&completed);
pGPUTuningChangedEvent1->pVtbl->Release(pGPUTuningChangedEvent1);
pGPUTuningChangedEvent1=NULL;
}

SetEvent(SAMEvent);
returntrue;
}
staticIADLXGPUTuningChangedListenerVtblgpuTuningChangedVtable={&OnGPUTuningChanged};
staticIADLXGPUTuningChangedListenergpuTuningChangedListener={&gpuTuningChangedVtable};

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//FindthefirstGPUindexthatsupportsAMDSmartAccessMemory.Return-1ifallGPUsarenotsupported
adlx_uintGetGPUIndexSupportSAM(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus);

//DisplayAMDSmartAccessMemorysupportstatusonaGPU
voidShowSmartAccessMemorySupport(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus);

//DisplayAMDSmartAccessMemoryenabledstatus
voidShowSmartAccessMemoryState(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus);

//SetAMDSmartAccessMemorystate
voidSetSmartAccessMemoryState(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXGPUTuningServices*gpuTuningService=NULL;
IADLXGPUTuningServices1*gpuTuningService1=NULL;
IADLXGPUTuningChangedHandling*gpuTuningHandling=NULL;
IADLXGPUList*gpus=NULL;
if(sys!=NULL)
{
res=sys->pVtbl->GetGPUTuningServices(sys,&gpuTuningService);
if(ADLX_SUCCEEDED(res))
{
//GetgpuTuningService1viagpuTuningService::QueryInterface()
res=gpuTuningService->pVtbl->QueryInterface(gpuTuningService,IID_IADLXGPUTuningServices1(),&gpuTuningService1);
if(ADLX_SUCCEEDED(res))
{

res=gpuTuningService1->pVtbl->GetGPUTuningChangedHandling(gpuTuningService1,&gpuTuningHandling);
if(ADLX_SUCCEEDED(res))
{
//Addlistener
SAMEvent=CreateEvent(NULL,FALSE,FALSE,NULL);
ADLX_RESULTeventRes=gpuTuningHandling->pVtbl->AddGPUTuningEventListener(gpuTuningHandling,&gpuTuningChangedListener);

//Menu
res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(gpuTuningService1,gpus);
}
else
printf("FailedtogetGPUlist\n");

//Removelistener
if(ADLX_SUCCEEDED(eventRes))
eventRes=gpuTuningHandling->pVtbl->RemoveGPUTuningEventListener(gpuTuningHandling,&gpuTuningChangedListener);
}
else
printf("FailedtogetGPUTuningChangedHandling\n");
}
else
printf("FailedtogetgpuTuningService1\n");
}
else
printf("FailedtogetgpuTuningService\n");
}
else
printf("FailedtogetsystemService\n");
//Free
if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
gpus=NULL;
}
if(gpuTuningHandling!=NULL)
{
gpuTuningHandling->pVtbl->Release(gpuTuningHandling);
gpuTuningHandling=NULL;
}
if(gpuTuningService1!=NULL)
{
gpuTuningService1->pVtbl->Release(gpuTuningService1);
gpuTuningService1=NULL;
}
if(gpuTuningService!=NULL)
{
gpuTuningService->pVtbl->Release(gpuTuningService);
gpuTuningService=NULL;
}
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
printf("\tChoosefromthefollowingoptions:\n");

printf("\t->Press1todisplayAMDSmartAccessMemorysupportedGPUs\n");
printf("\t->Press2todisplayAMDSmartAccessMemoryenabledstatus\n");
printf("\t->Press3tosettheAMDSmartAccessMemorystate\n");
printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplayAMDSmartAccessMemorysupportstatusonaGPU
case'1':
ShowSmartAccessMemorySupport(gpuTuningService1,gpus);
break;

//DisplayAMDSmartAccessMemoryenabledstatus
case'2':
ShowSmartAccessMemoryState(gpuTuningService1,gpus);
break;

//SetAMDSmartAccessMemorystate
case'3':
SetSmartAccessMemoryState(gpuTuningService1,gpus);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}

//FindthefirstGPUindexthatsupportsAMDSmartAccessMemory.Return-1ifallGPUsarenotsupported
adlx_uintGetGPUIndexSupportSAM(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus)
{
adlx_uintsupportedIndex=-1;
IADLXGPU*oneGPU=NULL;
IADLXSmartAccessMemory*smartAccessMemory=NULL;
for(autoindex=gpus->pVtbl->Begin(gpus);index!=gpus->pVtbl->End(gpus);++index)
{
ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,index,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
//GettheAMDSmartAccessMemoryinterface
res=gpuTuningService1->pVtbl->GetSmartAccessMemory(gpuTuningService1,oneGPU,&smartAccessMemory);
if(ADLX_SUCCEEDED(res))
{
adlx_boolsupported=false;
ADLX_RESULTres=smartAccessMemory->pVtbl->IsSupported(smartAccessMemory,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
supportedIndex=index;
smartAccessMemory->pVtbl->Release(smartAccessMemory);
oneGPU->pVtbl->Release(oneGPU);
break;
}
}
}
if(smartAccessMemory!=NULL)
{
smartAccessMemory->pVtbl->Release(smartAccessMemory);
smartAccessMemory=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
returnsupportedIndex;
}

//DisplayAMDSmartAccessMemorysupportstatusonaGPU
voidShowSmartAccessMemorySupport(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus)
{
autoindex=GetGPUIndexSupportSAM(gpuTuningService1,gpus);
if(index==-1)
printf("AllGPUsdoesn'tsupportAMDSmartAccessMemory\n");
else
printf("The%dthGPUsupportAMDSmartAccessMemory\n",(index+1));
}

//DisplayAMDSmartAccessMemoryenabledstatus
voidShowSmartAccessMemoryState(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus)
{
autoindex=GetGPUIndexSupportSAM(gpuTuningService1,gpus);
if(index==-1)
printf("AllGPUsdoesn'tsupportAMDSmartAccessMemory\n");
else
{
IADLXGPU*oneGPU=NULL;
IADLXSmartAccessMemory*smartAccessMemory=NULL;
ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,index,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
res=gpuTuningService1->pVtbl->GetSmartAccessMemory(gpuTuningService1,oneGPU,&smartAccessMemory);
if(ADLX_SUCCEEDED(res))
{
adlx_boolenabled=false;
res=smartAccessMemory->pVtbl->IsEnabled(smartAccessMemory,&enabled);
if(ADLX_SUCCEEDED(res))
printf("TheAMDSmartAccessMemoryis%sonthe%dthGPU\n",(enabled?"enabled":"disabled"),(index+1));
else
printf("CallIsEnabled()failed\n");
}
else
printf("FailedtogetsmartAccessMemory\n");
}
else
printf("FailedtogettheGPU\n");

if(smartAccessMemory!=NULL)
{
smartAccessMemory->pVtbl->Release(smartAccessMemory);
smartAccessMemory=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
}

//SetAMDSmartAccessMemorystate
voidSetSmartAccessMemoryState(IADLXGPUTuningServices1*gpuTuningService1,IADLXGPUList*gpus)
{
autoindex=GetGPUIndexSupportSAM(gpuTuningService1,gpus);
if(index==-1)
printf("AllGPUsdoesn'tsupportAMDSmartAccessMemory\n");
else
{
IADLXGPU*oneGPU=NULL;
IADLXSmartAccessMemory*smartAccessMemory=NULL;
ADLX_RESULTres=gpus->pVtbl->At_GPUList(gpus,index,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
res=gpuTuningService1->pVtbl->GetSmartAccessMemory(gpuTuningService1,oneGPU,&smartAccessMemory);
if(ADLX_SUCCEEDED(res))
{
adlx_boolenabled=false;
res=smartAccessMemory->pVtbl->IsEnabled(smartAccessMemory,&enabled);
if(ADLX_SUCCEEDED(res))
printf("CurrentlyAMDSmartAccessMemoryis%son%dthGPU\n",(enabled?"enabled":"disabled"),(index+1));
else
printf("CallIsEnabled()failed\n");
res=smartAccessMemory->pVtbl->SetEnabled(smartAccessMemory,!enabled);
if(ADLX_SUCCEEDED(res))
printf("SetAMDSmartAccessMemoryto%sfor%dthGPU\n",(!enabled?"enabled":"disabled"),(index+1));
else
printf("CallSetEnabled()failed\n");

//FirsteventreceivedquicklybeforeSAMstart
WaitForSingleObject(SAMEvent,2000);

//Whenreceivethefirstevent,avoidcallinganyotherADLXmethod,andifanyUIapplicationisrunning,wemustcloseittoavoidcrashing
//Close(application)......

//SecondeventreceivedafterSAMcompleted,themaximumconsumingtimelessthan20seconds.
WaitForSingleObject(SAMEvent,20000);

//NowSAMcompleted,wecanrestarttheUIapplication,andcontinuetocallADLXfunction
//Start(application)......

res=smartAccessMemory->pVtbl->IsEnabled(smartAccessMemory,&enabled);
if(ADLX_SUCCEEDED(res))
printf("Aftersetting,AMDSmartAccessMemoryis%son%dthGPU\n",(enabled?"enabled":"disabled"),(index+1));
else
printf("CallIsEnabled()failed\n");
}
else
printf("FailedtogetsmartAccessMemory\n");
}
else
printf("FailedtogettheGPU\n");

if(smartAccessMemory!=NULL)
{
smartAccessMemory->pVtbl->Release(smartAccessMemory);
smartAccessMemory=NULL;
}
if(oneGPU!=NULL)
{
oneGPU->pVtbl->Release(oneGPU);
oneGPU=NULL;
}
}
}
