voidXTrace(wchar_t*lpszFormat,...)
{
va_listargs;
va_start(args,lpszFormat);
intnBuf;
WCHARszBuffer[MAX_DEBUG_STR_LEN];
nBuf=_vsnwprintf_s(szBuffer,MAX_DEBUG_STR_LEN,_TRUNCATE,lpszFormat,args);
OutputDebugStringW(szBuffer);
va_end(args);
}

//Servicename
#defineADLX_SERVICE_NAMEL"ADLXGPUService"

//Globalvariablesforserviceroutine
SERVICE_STATUS_HANDLEg_StatusHandle=NULL;
SERVICE_STATUSg_ServiceStatus={0};
HANDLEg_ServiceStopEvent=INVALID_HANDLE_VALUE;

//Serviceroutine
VOIDWINAPIServiceMain(DWORDargc,LPTSTR*argv);
VOIDWINAPIServiceCtrlHandler(DWORD);
DWORDWINAPIServiceWorkerThread(LPVOIDlpParam);

//Install/unistallthisservice
//N.B:Needadminpermission
//Usercanalsoinstall/unistallthisservicebywindowscommandline
//Usage:SCCREATE"ADLXGPUService""GPUService.exe"
//SCDELETE"ADLXGPUService"
voidInstallService();
voidRemoveService();

//InvokerGPUinformationwithADLXinuserprocessbysharememorycommunication
externBOOLGetUserProcessData(Messager*messager,ResponseData*responseData);

//ADLXexecutortoberuninuserprocess
//RetrievethefirstavailableGPUvenderidandname
BOOLGetAdlxGpuInfo(WCHAR*resultBuffer)
{
if(resultBuffer==NULL)
{
XTrace(L"ADLXCallService:GetAdlxGpuInfo:invalidargument");
returnFALSE;
}

if(ADLX_FAILED(ADLXHelper_Initialize()))
{
XTrace(L"ADLXCallService:GetAdlxGpuInfo:ADLXhelperinitalizefailed.");
returnFALSE;
}

BOOLret=FALSE;

IADLXSystem*sys=ADLXHelper_GetSystemServices();
IADLXGPUList*gpus=NULL;
ADLX_RESULTres=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_SUCCEEDED(res)&&!gpus->pVtbl->Empty(gpus))
{
IADLXGPU*gpu=NULL;
res=gpus->pVtbl->At_GPUList(gpus,0,&gpu);
if(ADLX_SUCCEEDED(res)&&gpu)
{
constchar*vendorId=NULL;
res=gpu->pVtbl->VendorId(gpu,&vendorId);
if(ADLX_SUCCEEDED(res))
{
swprintf_s(resultBuffer,MAX_RESULT_LEN,L"VenderId=%hs",vendorId);
}

constchar*gpuName=NULL;
res=gpu->pVtbl->Name(gpu,&gpuName);
if(ADLX_SUCCEEDED(res))
{
swprintf_s(resultBuffer+wcslen(resultBuffer),MAX_RESULT_LEN-wcslen(resultBuffer),L"GpuName=%hs",gpuName);
}

gpu->pVtbl->Release(gpu);
ret=TRUE;
}

if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
}
}

returnret;
}

//GetanddumpGPUvenderidandname
voidShowGPUInfo()
{
ResponseDataresult;
MessageroneMessager;
oneMessager.adlxEntityName=ADLX_FUNCTION_NAME;
oneMessager.exector=GetAdlxGpuInfo;

if(GetUserProcessData(&oneMessager,&result))
{
//Dumpresults
XTrace(L"ADLXCallService:ShowGPUInfo:%s",result.executedResults);
}
}

//Usage
voidUsage()
{
printf("\n");
printf("\tThisexeisaserviceexecutable,youcaninstall/unistallitwith:\n");
printf("\tSCCREATE'ADLXGPUService''GPUService.exe'\n");
printf("\tSCDELETE'ADLXGPUService'\n");
printf("\n");
printf("\tOrusetheexecutableitselftoinstall/uninstall:\n");
printf("\tGPUService.exe-installorGPUService.exe/install\n");
printf("\tGPUService.exe-removeorGPUService.exe/remove\n");
printf("\n");
printf("\tUseGPUService.exe-help,GPUService.exe/helptoshowthisusage,orGPUService.exe?\n");
printf("\n");
}

intwmain(intargc,wchar_t*argv[])
{
XTrace(L"ADLXCallService:Main:Entry");
XTrace(L"ADLXCallService:Main:Args:%s",GetCommandLineW());

if((argc>1))
{
if(((*argv[1]=='-')||(*argv[1]=='/')))
{
if(_wcsicmp(L"install",argv[1]+1)==0)
{
InstallService();
return0;
}
elseif(_wcsicmp(L"remove",argv[1]+1)==0)
{
RemoveService();
return0;
}
elseif(_wcsicmp(L"help",argv[1]+1)==0)
{
Usage();
return0;
}
}
elseif(_wcsicmp(L"?",argv[1])==0)
{
Usage();
return0;
}
}
elseif(_wcsicmp(L"GPUInfo",GetCommandLineW())==0)
{
WCHAReventPath[MAX_PATH]={0};
WCHARshmPath[MAX_PATH]={0};

swprintf_s(eventPath,MAX_PATH,ADXL_EVENT_NAME,ADLX_FUNCTION_NAME);
swprintf_s(shmPath,MAX_PATH,ADXL_SHAREMENORY_NAME,ADLX_FUNCTION_NAME);

XTrace(L"ADLXCallService:Main:GPUInfoevt:%s,shmpath:%s\n",eventPath,shmPath);

if(ShmCreate(shmPath,SHARE_MEMORY_MAX_SIZE))
{
Messagermessager;
if(ShmReadBuffer(shmPath,(PVOID)&messager,sizeof(Messager)))
{
ResponseDataresult;
if(messager.exector(result.executedResults))
{
result.size=(int)wcslen(result.executedResults);
ShmWriteBuffer(shmPath,(PVOID*)&result,(ULONG)sizeof(result));

HANDLEhEvent=OpenEventW(EVENT_ALL_ACCESS,FALSE,eventPath);
if(hEvent)
{
SetEvent(hEvent);
CloseHandle(hEvent);
XTrace(L"ADLXCallService:Main:GPUInfo,userappfinished.");
}
}
}
}

return0;
}

SERVICE_TABLE_ENTRYWServiceTable[]=
{
{ADLX_SERVICE_NAME,(LPSERVICE_MAIN_FUNCTIONW)ServiceMain},
{NULL,NULL}
};

if(StartServiceCtrlDispatcherW(ServiceTable)==FALSE)
{
XTrace(L"ADLXCallService:Main:StartServiceCtrlDispatcherreturnederror.");
returnGetLastError();
}

XTrace(L"ADLXCallService:Main:Exit");

return0;
}

VOIDWINAPIServiceMain(DWORDargc,LPTSTR*argv)
{
DWORDStatus=E_FAIL;

XTrace(L"ADLXCallService:ServiceMain:Entry");

g_StatusHandle=RegisterServiceCtrlHandlerW(ADLX_SERVICE_NAME,ServiceCtrlHandler);
if(g_StatusHandle==NULL)
{
XTrace(L"ADLXCallService:ServiceMain:RegisterServiceCtrlHandlerreturnederror");
gotoEXIT;
}

//Telltheservicecontrollerwearestarting
ZeroMemory(&g_ServiceStatus,sizeof(g_ServiceStatus));
g_ServiceStatus.dwServiceType=SERVICE_WIN32_OWN_PROCESS;
g_ServiceStatus.dwControlsAccepted=0;
g_ServiceStatus.dwCurrentState=SERVICE_START_PENDING;
g_ServiceStatus.dwWin32ExitCode=0;
g_ServiceStatus.dwServiceSpecificExitCode=0;
g_ServiceStatus.dwCheckPoint=0;

if(SetServiceStatus(g_StatusHandle,&g_ServiceStatus)==FALSE)
{
XTrace(L"ADLXCallService:ServiceMain:SetServiceStatusreturnederror");
}

/*
*Performtasksneccesarytostarttheservicehere
*/
XTrace(L"ADLXCallService:ServiceMain:PerformingServiceStartOperations");

//Createstopeventtowaitonlater.
g_ServiceStopEvent=CreateEvent(NULL,TRUE,FALSE,NULL);
if(g_ServiceStopEvent==NULL)
{
XTrace(L"ADLXCallService:ServiceMain:CreateEvent(g_ServiceStopEvent)returnederror");

g_ServiceStatus.dwControlsAccepted=0;
g_ServiceStatus.dwCurrentState=SERVICE_STOPPED;
g_ServiceStatus.dwWin32ExitCode=GetLastError();
g_ServiceStatus.dwCheckPoint=1;

if(SetServiceStatus(g_StatusHandle,&g_ServiceStatus)==FALSE)
{
XTrace(L"ADLXCallService:ServiceMain:SetServiceStatusreturnederror");
}
gotoEXIT;
}

//Telltheservicecontrollerwearestarted
g_ServiceStatus.dwControlsAccepted=SERVICE_ACCEPT_STOP;
g_ServiceStatus.dwCurrentState=SERVICE_RUNNING;
g_ServiceStatus.dwWin32ExitCode=0;
g_ServiceStatus.dwCheckPoint=0;

if(SetServiceStatus(g_StatusHandle,&g_ServiceStatus)==FALSE)
{
XTrace(L"ADLXCallService:ServiceMain:SetServiceStatusreturnederror");
}

//Startthethreadthatwillperformthemaintaskoftheservice
HANDLEhThread=CreateThread(NULL,0,ServiceWorkerThread,NULL,0,NULL);

XTrace(L"ADLXCallService:ServiceMain:WaitingforWorkerThreadtocomplete");

//Waituntilourworkerthreadexitseffectivelysignalingthattheserviceneedstostop
WaitForSingleObject(hThread,INFINITE);

XTrace(L"ADLXCallService:ServiceMain:WorkerThreadStopEventsignaled");

/*
*Performanycleanuptasks
*/
XTrace(L"ADLXCallService:ServiceMain:PerformingCleanupOperations");

CloseHandle(g_ServiceStopEvent);

g_ServiceStatus.dwControlsAccepted=0;
g_ServiceStatus.dwCurrentState=SERVICE_STOPPED;
g_ServiceStatus.dwWin32ExitCode=0;
g_ServiceStatus.dwCheckPoint=3;

if(SetServiceStatus(g_StatusHandle,&g_ServiceStatus)==FALSE)
{
XTrace(L"ADLXCallService:ServiceMain:SetServiceStatusreturnederror");
}

EXIT:
XTrace(L"ADLXCallService:ServiceMain:Exit");

return;
}

VOIDWINAPIServiceCtrlHandler(DWORDCtrlCode)
{
XTrace(L"ADLXCallService:ServiceCtrlHandler:Entry");

switch(CtrlCode)
{
caseSERVICE_CONTROL_STOP:

XTrace(L"ADLXCallService:ServiceCtrlHandler:SERVICE_CONTROL_STOPRequest");

if(g_ServiceStatus.dwCurrentState!=SERVICE_RUNNING)
break;

/*
*Performtasksneccesarytostoptheservicehere
*/

g_ServiceStatus.dwControlsAccepted=0;
g_ServiceStatus.dwCurrentState=SERVICE_STOP_PENDING;
g_ServiceStatus.dwWin32ExitCode=0;
g_ServiceStatus.dwCheckPoint=4;

if(SetServiceStatus(g_StatusHandle,&g_ServiceStatus)==FALSE)
{
XTrace(L"ADLXCallService:ServiceCtrlHandler:SetServiceStatusreturnederror");
}

//Thiswillsignaltheworkerthreadtoshutdown
SetEvent(g_ServiceStopEvent);
break;

default:
break;
}

XTrace(L"ADLXCallService:ServiceCtrlHandler:Exit");
}

DWORDWINAPIServiceWorkerThread(LPVOIDlpParam)
{
XTrace(L"ADLXCallService:ServiceWorkerThread:Entry");

//Periodicallycheckiftheservicehasbeenrequestedtostop
while(WaitForSingleObject(g_ServiceStopEvent,0)!=WAIT_OBJECT_0)
{
ShowGPUInfo();
Sleep(3*1000);
}

XTrace(L"ADLXCallService:ServiceWorkerThread:Exit");

returnERROR_SUCCESS;
}

voidInstallService()
{
WCHARpath[512]={0};
if(GetModuleFileNameW(NULL,path,512)==0)
{
XTrace(L"ADLXCallService:InstallService:Cannotgetg_SharedFilepath.");
return;
}

SC_HANDLEserviceManager=OpenSCManager(NULL,NULL,SC_MANAGER_CONNECT|SC_MANAGER_CREATE_SERVICE);
if(serviceManager)
{
SC_HANDLEserviceHandle=CreateServiceW(
serviceManager,ADLX_SERVICE_NAME,ADLX_SERVICE_NAME,SERVICE_QUERY_STATUS,
SERVICE_WIN32_OWN_PROCESS|SERVICE_INTERACTIVE_PROCESS,
SERVICE_AUTO_START,SERVICE_ERROR_NORMAL,path,
NULL,NULL,L"",NULL,NULL);

if(serviceHandle)
{
CloseServiceHandle(serviceHandle);
}
else
{
XTrace(L"ADLXCallService:InstallService:Cannotcreateservice.");
}

CloseServiceHandle(serviceManager);
}
else
{
XTrace(L"ADLXCallService:InstallService:Cannotopenservice.");
}
}

voidRemoveService()
{
SC_HANDLEserviceManager=OpenSCManager(NULL,NULL,SC_MANAGER_CONNECT);
if(!serviceManager)
{
XTrace(L"ADLXCallService:RemoveService:Cannotopenservicemanager.");
return;
}

SC_HANDLEserviceHandle=OpenServiceW(serviceManager,ADLX_SERVICE_NAME,DELETE|SERVICE_STOP);
if(!serviceHandle)
{
CloseServiceHandle(serviceManager);
XTrace(L"ADLXCallService:RemoveService:Cannotopenservice.");
return;
}

if(!DeleteService(serviceHandle))
{
CloseServiceHandle(serviceHandle);
CloseServiceHandle(serviceManager);
XTrace(L"ADLXCallService:RemoveService:Deleteservicefailed.");
return;
}

XTrace(L"ADLXCallService:RemoveService:Deletedservice.");

SERVICE_STATUSserviceStatus={0};
ControlService(serviceHandle,SERVICE_CONTROL_STOP,(LPSERVICE_STATUS)&serviceStatus);

CloseServiceHandle(serviceHandle);
CloseServiceHandle(serviceManager);
}
