constadlx_intDefaultNumOfPoints=MAX_USER_3DLUT_NUM_POINTS;

//Displaydisplay3DLUTsupport
voidShowDisplay3DLUTSupport(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
//Getdisplay3DLUT
IADLXDisplay3DLUT*display3DLUT=NULL;
ADLX_RESULTres=displayService->pVtbl->Get3DLUT(displayService,display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
printf("===3DLUTsupportedstatus===\n");
adlx_boolsupported;
ADLX_RESULTres=display3DLUT->pVtbl->IsSupportedSCE(display3DLUT,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSCEsupportedonthisdisplay:%d\n",supported);
res=display3DLUT->pVtbl->IsSupportedSCEVividGaming(display3DLUT,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSCEVividGamingpresetsupportedonthisdisplay:%d\n",supported);
res=display3DLUT->pVtbl->IsSupportedSCEDynamicContrast(display3DLUT,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSCEDynamicContrastsupportedonthisdisplay:%d\n",supported);
res=display3DLUT->pVtbl->IsSupportedUser3DLUT(display3DLUT,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tIs3DLUTsupportedonthisdisplay:%d\n",supported);
}

//Releasethedisplay3DLUTinterface
if(NULL!=display3DLUT)
{
display3DLUT->pVtbl->Release(display3DLUT);
display3DLUT=NULL;
}
}

//Displaycurrentdisplay3DLUTstate
voidGet3DLUTState(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
//Getdisplay3DLUT
IADLXDisplay3DLUT*display3DLUT=NULL;
ADLX_RESULTres=displayService->pVtbl->Get3DLUT(displayService,display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
printf("===Get3DLUT===\n");
adlx_boolapplied=false;
res=display3DLUT->pVtbl->IsCurrentSCEDisabled(display3DLUT,&applied);
if(ADLX_SUCCEEDED(res))
printf("\tIsSCEusedonthisdisplay:%d\n",applied);
res=display3DLUT->pVtbl->IsCurrentSCEVividGaming(display3DLUT,&applied);
if(ADLX_SUCCEEDED(res))
printf("\tIsSCEVividGamingpresetusedonthisdisplay:%d\n",applied);
res=display3DLUT->pVtbl->IsCurrentSCEDynamicContrast(display3DLUT,&applied);
if(ADLX_SUCCEEDED(res))
printf("\tIsSCEVividDynamicContrastpresetusedonthisdisplay:%d\n",applied);
if(applied)
{
ADLX_IntRangerange;
display3DLUT->pVtbl->GetSCEDynamicContrastRange(display3DLUT,&range);
printf("\t\tDynamicContrastrangeis:[%d,%d]\n",range.minValue,range.maxValue);
adlx_intcontrast;
display3DLUT->pVtbl->GetSCEDynamicContrast(display3DLUT,&contrast);
printf("\t\tDynamicContrastis:%d\n",contrast);
}
}

//Releasethedisplay3DLUTinterface
if(NULL!=display3DLUT)
{
display3DLUT->pVtbl->Release(display3DLUT);
display3DLUT=NULL;
}
}

//Setdisplay3DLUT
voidSet3DLUTState(IADLXDisplayServices*displayService,IADLXDisplay*display,intkey)
{
//Getdisplay3DLUT
IADLXDisplay3DLUT*display3DLUT=NULL;
ADLX_RESULTres=displayService->pVtbl->Get3DLUT(displayService,display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
printf("===Set3DLUT===\n");
ADLX_RESULTres=ADLX_FAIL;
switch(key)
{
//SetSCEdisabled
case0:
res=display3DLUT->pVtbl->SetSCEDisabled(display3DLUT);
break;
//SetSCEVividGamingpreset
case1:
res=display3DLUT->pVtbl->SetSCEVividGaming(display3DLUT);
break;
//SetSCEDynamicContrast
case2:
{
ADLX_IntRangerange;
display3DLUT->pVtbl->GetSCEDynamicContrastRange(display3DLUT,&range);
adlx_intcurContrast;
display3DLUT->pVtbl->GetSCEDynamicContrast(display3DLUT,&curContrast);
adlx_intcontrast;
if(curContrast!=range.minValue)
{
contrast=range.minValue;
res=display3DLUT->pVtbl->SetSCEDynamicContrast(display3DLUT,contrast);
printf("\tUseminimumDynamicContrast\n");
}
else
{
contrast=range.maxValue;
res=display3DLUT->pVtbl->SetSCEDynamicContrast(display3DLUT,contrast);
printf("\tUsemaximumDynamicContrast\n");
}
}
break;
default:
break;
}
printf("\treturncode(0meansSuccess)is:%d\n",res);
}

//Releasethedisplay3DLUTinterface
if(NULL!=display3DLUT)
{
display3DLUT->pVtbl->Release(display3DLUT);
display3DLUT=NULL;
}
}

