//Helperfunctions
inlinedoubleInputvalue(intindex,intbitDepth,intnumOfPoints)
{
returnfmin((float)(index*pow(2,bitDepth)/(float)(numOfPoints-1)),pow(2,bitDepth)-1);
}

//Generate3DLUTdata
adlx_boolGenerateUser3DLUT_BW(IADLXDisplayServices*displayService,IADLXDisplay*display,adlx_intnumOfPoints,ADLX_3DLUT_Data*user3DLUT)
{
adlx_boolret=true;
constintbitDepth=16;

IADLXDisplay3DLUT*display3DLUT=NULL;
ADLX_RESULTres=displayService->pVtbl->Get3DLUT(displayService,display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
ADLX_UINT16_RGBrgbCordinate={0,0,0};
adlx_intindex=0;

for(intr=0;r<numOfPoints;r++)
{
for(intg=0;g<numOfPoints;g++)
{
for(intb=0;b<numOfPoints;b++)
{
rgbCordinate.red=r;
rgbCordinate.green=g;
rgbCordinate.blue=b;

doubletemp=0.25*Inputvalue(r,bitDepth,numOfPoints)+0.625*Inputvalue(g,bitDepth,numOfPoints)+0.125*Inputvalue(b,bitDepth,numOfPoints);
res=display3DLUT->pVtbl->GetUser3DLUTIndex(display3DLUT,numOfPoints,&rgbCordinate,&index);

if(ADLX_SUCCEEDED(res))
{
adlx_uint16tValue=(adlx_uint16)(temp);
user3DLUT->data[index].red=tValue;
user3DLUT->data[index].green=tValue;
user3DLUT->data[index].blue=tValue;
}
else
{
ret=false;
gotoEXIT;
}
}
}
}
}

//Releasethedisplay3DLUTinterface
if(NULL!=display3DLUT)
{
display3DLUT->pVtbl->Release(display3DLUT);
display3DLUT=NULL;
}

EXIT:
returnret;
}

//GetorSetSDRuser3DLUT
voidDemoSDRUser3DLUT(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
//Getdisplay3DLUT
IADLXDisplay3DLUT*display3DLUT=NULL;
ADLX_RESULTres=displayService->pVtbl->Get3DLUT(displayService,display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
ADLX_3DLUT_TRANSFER_FUNCTIONtf;
ADLX_3DLUT_COLORSPACEcs;
ADLX_3DLUT_Datadata;
adlx_intnumOfPoints=0;

//Generatedata
if(GenerateUser3DLUT_BW(displayService,display,DefaultNumOfPoints,&data))
{
tf=TF_SRGB;
cs=CS_SRGB;

printf("===SetSDRUser3DLUT===\n");
res=display3DLUT->pVtbl->SetSDRUser3DLUT(display3DLUT,tf,cs,DefaultNumOfPoints,&data);
if(ADLX_SUCCEEDED(res))
{
printf("===SetSDRUser3DLUTsuccessful===\n");
}
else
printf("===SetSDRUser3DLUTfailed===\n");

printf("===GetSDRUser3DLUT===\n");
res=display3DLUT->pVtbl->GetSDRUser3DLUT(display3DLUT,&tf,&cs,&numOfPoints,&data);
if(ADLX_SUCCEEDED(res))
{
printf("===GetSDRUser3DLUTsuccessful===\n");
}
else
printf("===GetSDRUser3DLUTfailed===\n");

//Clear
res=display3DLUT->pVtbl->ClearUser3DLUT(display3DLUT);
if(ADLX_SUCCEEDED(res))
{
printf("===Clearsuccessful===\n");
}
else
printf("===Clearfailed===\n");
}
}

//Releaethedisplay3DLUTinterface
if(NULL!=display3DLUT)
{
display3DLUT->pVtbl->Release(display3DLUT);
display3DLUT=NULL;
}
}

