//Mainmenu
voidMainMenu()
{
printf("\tChoosefromfollowingoptions\n");

printf("\t->Press1todisplay3DLUTsupport\n");

printf("\t->Press2toget3DLUTstate\n");

printf("\t->Press3tosetcurrentSCEtodisabled\n");
printf("\t->Press4tosetcurrentSCEfeaturetoVividGamingpreset\n");
printf("\t->Press5tosetcurrentSCEfeaturetoDynamicContrast\n");

printf("\t->Press6togetuser3DLUTsupport\n");
printf("\t->Press7toclearuser3DLUT\n");
printf("\t->Press8toshowhowtoset/getSDRuser3DLUT\n");
printf("\t->Press9toshowhowtoset/getHDRuser3DLUT\n");
printf("\t->Pressatoshowhowtoset/getalluser3DLUT\n");
printf("\t->Pressbtoget3DLUTindex\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaythemainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
case'1':
ShowDisplay3DLUTSupport(displayService,display);
break;
case'2':
Get3DLUTState(displayService,display);
break;
case'3':
case'4':
case'5':
Set3DLUTState(displayService,display,num-'3');
break;
case'6':
IsSupportedUser3DLUT(displayService,display);
break;
case'7':
ClearUser3DLUT(displayService,display);
break;
case'8':
DemoSDRUser3DLUT(displayService,display);
break;
case'9':
DemoHDRUser3DLUT(displayService,display);
break;
case'a':
DemoAllUser3DLUT(displayService,display);
break;
case'b':
GetUser3DLUTIndex(displayService,display);
break;
//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//Getdisplayservice
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService->pVtbl->GetDisplays(displayService,&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=displayList->pVtbl->At_DisplayList(displayList,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(displayService,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}
}

//ReleasethedisplayServiceinterface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXresult:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}
