#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"
#include"SDK/Include/IDisplays1.h"

//Threadtoquitevent
staticHANDLEquitEvent=NULL;

//Threadtoquitflag
staticintg_quit=0;

//Threadtotimeout
staticHANDLEthread=NULL;

//Threadtounblankdisplay
DWORDWINAPIThreadUnBlankDisplay(LPVOIDlpParam);

//Checkifdisplayblankingissupported
voidShowDisplayBlankingSupport(IADLXDisplayServices1*displayService1,IADLXDisplay*display);

//Getthecurrentstateofdisplayblanking
voidGetDisplayBlankingState(IADLXDisplayServices1*displayService1,IADLXDisplay*display);

//Setthestateofdisplayblankingto"blanked"or"unblanked"
voidSetDisplayBlankingState(IADLXDisplayServices1*displayService1,IADLXDisplay*display,constintkey);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices1*displayService1,IADLXDisplay*display);

//Selectdisplay
IADLXDisplay*SelectDisplay(IADLXDisplayList*displays);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
quitEvent=CreateEvent(NULL,TRUE,FALSE,NULL);

//GetSystemServices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//GetdisplayService
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);

//ConverttodisplayService1
IADLXDisplayServices1*displayService1=NULL;
displayService->pVtbl->QueryInterface(displayService,IID_IADLXDisplayServices1(),(void**)&displayService1);

if(ADLX_SUCCEEDED(res)&&displayService1)
{
//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService1->pVtbl->GetDisplays(displayService1,&displayList);
if(ADLX_SUCCEEDED(res))
{
//Selectthedisplaytoconfig
IADLXDisplay*display=SelectDisplay(displayList);
if(display)
{
//Displaythemainmenu
MainMenu();
//Getandexecutethechoice
MenuControl(displayService1,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}
}
else
{
printf("FailedtogetdisplayService1\n");
}

//ReleasethedisplayService1interface
if(NULL!=displayService1)
{
displayService1->pVtbl->Release(displayService1);
displayService1=NULL;
}

//ReleasethedisplayServiceinterface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}

//Quitthread
if(thread)
{
SetEvent(quitEvent);
g_quit=true;
WaitForSingleObject(thread,INFINITE);
CloseHandle(thread);
thread=NULL;
}
CloseHandle(quitEvent);
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

voidShowDisplayBlankingSupport(IADLXDisplayServices1*displayService1,IADLXDisplay*display)
{
IADLXDisplayBlanking*displayBlanking;
ADLX_RESULTres=displayService1->pVtbl->GetDisplayBlanking(displayService1,display,&displayBlanking);
if(ADLX_SUCCEEDED(res))
{
printf("===Getstateofdisplayblankingsupport===\n");
adlx_boolsupported=false;
res=displayBlanking->pVtbl->IsSupported(displayBlanking,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tIsSupported,result:%dsupported:%s\n",res,(supported?"true":"false"));
displayBlanking->pVtbl->Release(displayBlanking);
displayBlanking=NULL;
}
}

voidGetDisplayBlankingState(IADLXDisplayServices1*displayService1,IADLXDisplay*display)
{
IADLXDisplayBlanking*displayBlanking;
ADLX_RESULTres=displayService1->pVtbl->GetDisplayBlanking(displayService1,display,&displayBlanking);
if(ADLX_SUCCEEDED(res))
{
printf("===Getthecurrentstateofdisplayblanking===\n");
adlx_boolblanked=false;
res=displayBlanking->pVtbl->IsCurrentBlanked(displayBlanking,&blanked);
if(ADLX_SUCCEEDED(res))
printf("\tIsCurrentBlanked:%s\n",(blanked?"true":"false"));
adlx_boolunBlanked=false;
res=displayBlanking->pVtbl->IsCurrentUnblanked(displayBlanking,&unBlanked);
if(ADLX_SUCCEEDED(res))
printf("\tIsCurrentUnblanked:%s\n",(unBlanked?"true":"false"));
displayBlanking->pVtbl->Release(displayBlanking);
displayBlanking=NULL;
}
}

voidSetDisplayBlankingState(IADLXDisplayServices1*displayService1,IADLXDisplay*display,constintkey)
{
IADLXDisplayBlanking*displayBlanking;
ADLX_RESULTres=displayService1->pVtbl->GetDisplayBlanking(displayService1,display,&displayBlanking);
if(ADLX_SUCCEEDED(res))
{
ADLX_RESULTres=ADLX_FAIL;
switch(key)
{
//Setdisplaytoblanked
case0:
if(thread)
{
SetEvent(quitEvent);
g_quit=true;
WaitForSingleObject(thread,INFINITE);
CloseHandle(thread);
thread=NULL;
}
printf("===Setdisplaytoblanked===\n");
res=displayBlanking->pVtbl->SetBlanked(displayBlanking);
break;

//Setdisplaytounblanked
case1:
printf("===Setdisplaytounblanked===\n");
res=displayBlanking->pVtbl->SetUnblanked(displayBlanking);
break;

//Setdisplaytoblankedfor20seconds
case2:
if(thread)
{
SetEvent(quitEvent);
g_quit=true;
WaitForSingleObject(thread,INFINITE);
CloseHandle(thread);
thread=NULL;
}
printf("===Setdisplaytoblankedfor20seconds===\n");
res=displayBlanking->pVtbl->SetBlanked(displayBlanking);
thread=CreateThread(NULL,0,ThreadUnBlankDisplay,displayBlanking,0,NULL);
g_quit=false;
break;
default:
break;
}
printf("\tReturncodeis:%d(0meansSuccess)\n",res);
displayBlanking->pVtbl->Release(displayBlanking);
displayBlanking=NULL;
}
}

DWORDWINAPIThreadUnBlankDisplay(LPVOIDlpParam)
{
IADLXDisplayBlanking*displayBlanking=(IADLXDisplayBlanking*)(lpParam);
if(displayBlanking)
{
intopt=0;
while(!g_quit)
{
displayBlanking->pVtbl->Acquire(displayBlanking);
DWORDr=WaitForSingleObject(quitEvent,20000);
//Quitthethread
if(r==WAIT_OBJECT_0)
{
ResetEvent(quitEvent);
printf("\tThreadquit.\n");
displayBlanking->pVtbl->Release(displayBlanking);
break;
}
//Restoredisplayafter20seconds
if(r==WAIT_TIMEOUT&&!opt)
{
ADLX_RESULTres=displayBlanking->pVtbl->SetUnblanked(displayBlanking);
printf("\tRestoredisplayafter20seconds,returncodeis:%d(0meanssuccess).\n",res);
displayBlanking->pVtbl->Release(displayBlanking);
opt=1;
}
}
}
}

voidMainMenu()
{
printf("\tChoosefromthefollowingoptions\n");

printf("\t->Press1tocheckifdisplayblankingissupported\n");

printf("\t->Press2togetthecurrentstateofdisplayblanking\n");

printf("\t->Press3tosetthestateofdisplayblankingtoblanked\n");
printf("\t->Press4tosetthestateofdisplayblankingtounblanked\n");
printf("\t->Press5tosetdisplaytoblankedfor20seconds\n");

printf("\t->PressQ/qtoquittheapplication\n");
printf("\t->PressM/mtodisplaythemainmenu\n");
}

//Displayactioncontrol
voidMenuControl(IADLXDisplayServices1*displayService1,IADLXDisplay*display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Checkifdisplayblankingissupported
case'1':
ShowDisplayBlankingSupport(displayService1,display);
break;

//Getthecurrentstateofdisplayblanking
case'2':
GetDisplayBlankingState(displayService1,display);
break;

//Setthestateofdisplayblankingto"blanked"or"unblanked"
case'3':
case'4':
case'5':
SetDisplayBlankingState(displayService1,display,num-'3');
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

IADLXDisplay*SelectDisplay(IADLXDisplayList*displays)
{
adlx_uintlen=displays->pVtbl->Size(displays);
if(len==0)
returnNULL;

IADLXDisplay*display=NULL;
if(len<2)
displays->pVtbl->At_DisplayList(displays,0,&display);
else
{
printf("\tSelectthedisplayusingthekey(from1to%d).\n",len);
for(adlx_uintit=0;it<len;it++)
{
displays->pVtbl->At_DisplayList(displays,it,&display);
constchar*dispName;
display->pVtbl->Name(display,&dispName);
printf("\t->Press%dtoselect%s.\n",it+1,dispName);
}
intnum=0;
num=getchar()-48;
if(num<1||num>len)
printf("\tInvalidkey.Thefirstdisplaywillbeused.\n");
else
displays->pVtbl->At(displays,num-1,&display);
}
returndisplay;
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}
