#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IDisplay3DLUT.h"
#include"SDK/Include/IDisplayGamma.h"
#include"SDK/Include/IDisplayGamut.h"
#include"SDK/Include/IDisplays.h"
#include<windows.h>

//CallbackfordisplayListChanged
adlx_boolADLX_STD_CALLOnDisplayListChanged(IADLXDisplayListChangedListener*pThis,IADLXDisplayList*pNewDisplays)
{
printf("Displaylisthasbeenchanged\n");

//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();
//Getdisplayservice
IADLXDisplayServices*displayService=NULL;
ADLX_RESULTres=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
//Triggergamutchange,gammachange,3DLUTchangeforthefirstdisplay
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=pNewDisplays->pVtbl->At_DisplayList(pNewDisplays,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Triggergamutchange
IADLXDisplayGamut*pDispGamut=NULL;
res=displayService->pVtbl->GetGamut(displayService,display,&pDispGamut);
if(ADLX_SUCCEEDED(res))
{
pDispGamut->pVtbl->SetGamut_PW_PG(pDispGamut,WHITE_POINT_5000K,GAMUT_SPACE_CIE_RGB);
}
if(NULL!=pDispGamut)
{
pDispGamut->pVtbl->Release(pDispGamut);
pDispGamut=NULL;
}

//Triggergammachange
IADLXDisplayGamma*pDispGamma=NULL;
res=displayService->pVtbl->GetGamma(displayService,display,&pDispGamma);
if(ADLX_SUCCEEDED(res))
{
ADLX_RegammaCoeffcoeff;
coeff.coefficientA0=31308;
coeff.coefficientA1=12920;
coeff.coefficientA2=55;
coeff.coefficientA3=55;
coeff.gamma=2400;
pDispGamma->pVtbl->SetReGammaCoefficient(pDispGamma,coeff);
}
if(NULL!=pDispGamma)
{
pDispGamma->pVtbl->Release(pDispGamma);
pDispGamma=NULL;
}

//Trigger3DLUTchange
IADLXDisplay3DLUT*pDisp3DLUT=NULL;
res=displayService->pVtbl->Get3DLUT(displayService,display,&pDisp3DLUT);
if(ADLX_SUCCEEDED(res))
{
pDisp3DLUT->pVtbl->SetSCEDisabled(pDisp3DLUT);
}
if(NULL!=pDisp3DLUT)
{
pDisp3DLUT->pVtbl->Release(pDisp3DLUT);
pDisp3DLUT=NULL;
}
}
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}

//IftrueisreturnedADLXcontinuestonotifynextlistenerelseiffalseisretunedADLXstopsthenotification.
returntrue;
}

//CallbackstructfordisplayListChanged
typedefstructDisplayListCallBack
{
adlx_bool(ADLX_STD_CALL*OnDisplayListChanged)(IADLXDisplayListChangedListener*,IADLXDisplayList*);
}DisplayListCallBack;

//CallbackfordisplayGamut
adlx_boolADLX_STD_CALLOnDisplayGamutChanged(IADLXDisplayGamutChangedListener*pThis,IADLXDisplayGamutChangedEvent*pDisplayGamutChangedEvent)
{
printf("Displaygamuthasbeenchanged\n");
//IftrueisreturnedADLXcontinuestonotifynextlistenerelseiffalseisretunedADLXstopsthenotification.
returntrue;
}

//CallbackstructfordisplayGamut
typedefstructDisplayGamutCallBack
{
adlx_bool(ADLX_STD_CALL*OnDisplayGamutChanged)(IADLXDisplayGamutChangedListener*,IADLXDisplayGamutChangedEvent*);
}DisplayGamutCallBack;

//CallbackfordisplayGammaChanged
adlx_boolADLX_STD_CALLOnDisplayGammaChanged(IADLXDisplayGammaChangedListener*pThis,IADLXDisplayGammaChangedEvent*pDisplayGammaChangedEvent)
{
printf("Displaygammahasbeenchanged\n");
//IftrueisreturnedADLXcontinuestonotifynextlistenerelseiffalseisretunedADLXstopsthenotification.
printf("\tGammarampchange:%d\n\tGammacoefficientchange:%d\n\tRe-gammachange:%d\n\tDeGammachange:%d\n",
pDisplayGammaChangedEvent->pVtbl->IsGammaRampChanged(pDisplayGammaChangedEvent),
pDisplayGammaChangedEvent->pVtbl->IsGammaCoefficientChanged(pDisplayGammaChangedEvent),
pDisplayGammaChangedEvent->pVtbl->IsReGammaChanged(pDisplayGammaChangedEvent),
pDisplayGammaChangedEvent->pVtbl->IsDeGammaChanged(pDisplayGammaChangedEvent));

returntrue;
}

//CallBackstructfordisplayGammaChanged
typedefstructDisplayGammaCallBack
{
adlx_bool(ADLX_STD_CALL*OnDisplayGammaChanged)(IADLXDisplayGammaChangedListener*,IADLXDisplayGammaChangedEvent*);
}DisplayGammaCallBack;

