//Printgammaramp
staticvoidShowGammaRamp(ADLX_GammaRampgammaRamp)
{
for(intj=0;j<3;j++)
{
printf("\nGetGammaRamp[%c]:\n",(j==0)?'R':((j==1)?'G':(j==2)?'B'
:'E'));
for(inti=0;i<256;i++)
{
printf("%05d",gammaRamp.gamma[i+j*256]);
if(i%10==9)
printf("\n");
}
}
printf("\n");
}

//Createregammaramp
ADLX_GammaRampCreateReGammaRamp(constfloatfGamma)
{
ADLX_GammaRampramp;
doubleg_fGammaRemapRGB[3]={1,1,0.5};
for(intj=0;j<3;j++)
{
for(inti=0;i<256;i++)
{

floatnAdj=i/255.0f;
if(nAdj<0.0031308f)
{
nAdj=nAdj*12.92f;
}
else
{
nAdj=(1+0.055f)*powf(nAdj,1/fGamma)-0.055f;
if(nAdj<0.0f)
nAdj=0.0f;
}
ramp.gamma[i+j*256]=(unsignedshort)(1*g_fGammaRemapRGB[j]*(int)(nAdj*0xFFFF));
}
}
returnramp;
}

//Displaygammasupport
voidShowDisplayGammaSupport(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
//Getdisplaygamma
IADLXDisplayGamma*displayGamma=NULL;
ADLX_RESULTres=displayService->pVtbl->GetGamma(displayService,display,&displayGamma);
adlx_boolsupport=false;
if(ADLX_SUCCEEDED(res))
{
printf("===Re-Gammasupportedstatus===\n");
res=displayGamma->pVtbl->IsSupportedReGammaSRGB(displayGamma,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIssRGBre-gammasupportedonthisdisplay:%d\n",support);
res=displayGamma->pVtbl->IsSupportedReGammaBT709(displayGamma,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIsBT709re-gammasupportedonthisdisplay:%d\n",support);
res=displayGamma->pVtbl->IsSupportedReGammaPQ(displayGamma,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIsPQre-gammasupportedonthisdisplay:%d\n",support);
res=displayGamma->pVtbl->IsSupportedReGammaPQ2084Interim(displayGamma,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIsPQ2084Interimre-gammasupportedonthisdisplay:%d\n",support);
res=displayGamma->pVtbl->IsSupportedReGamma36(displayGamma,&support);
if(ADLX_SUCCEEDED(res))
printf("\tIs3.6re-gammasupportedonthisdisplay:%d\n",support);
}

//ReleasethedisplayGammainterface
if(NULL!=displayGamma)
{
displayGamma->pVtbl->Release(displayGamma);
displayGamma=NULL;
}
}

//Getcurrentgammastate
voidGetCurrentGammaState(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
//Getdisplaygamma
IADLXDisplayGamma*displayGamma;
ADLX_RESULTres=displayService->pVtbl->GetGamma(displayService,display,&displayGamma);
if(ADLX_SUCCEEDED(res))
{
printf("===Currentgammastate===\n");
adlx_boolapplied=false;
ADLX_RESULTres=ADLX_FAIL;
ADLX_GammaRampramp;
ADLX_RegammaCoeffcoeff;

res=displayGamma->pVtbl->IsCurrentReGammaSRGB(displayGamma,&applied);
if(ADLX_SUCCEEDED(res))
printf("\tIssRGBre-gammausedbythisdisplay:%d\n",applied);
res=displayGamma->pVtbl->IsCurrentReGammaBT709(displayGamma,&applied);
if(ADLX_SUCCEEDED(res))
printf("\tIsBT709re-gammausedbythisdisplay:%d\n",applied);
res=displayGamma->pVtbl->IsCurrentReGammaPQ(displayGamma,&applied);
if(ADLX_SUCCEEDED(res))
printf("\tIsPQre-gammausedbythisdisplay:%d\n",applied);
res=displayGamma->pVtbl->IsCurrentReGammaPQ2084Interim(displayGamma,&applied);
if(ADLX_SUCCEEDED(res))
printf("\tIsPQ2084Interimre-gammausedbythisdisplay%d\n",applied);
res=displayGamma->pVtbl->IsCurrentReGamma36(displayGamma,&applied);
if(ADLX_SUCCEEDED(res))
printf("\tIs3.6re-gammausedbythisdisplay%d\n",applied);

adlx_boolcurCoeff;
res=displayGamma->pVtbl->IsCurrentRegammaCoefficient(displayGamma,&curCoeff);
if(ADLX_SUCCEEDED(res))
printf("\tIsre-gammacoefficientusedbythisdisplay%d\n",curCoeff);

adlx_boolreGammaRamp=false;
adlx_booldeGammaRamp=false;
displayGamma->pVtbl->IsCurrentReGammaRamp(displayGamma,&reGammaRamp);
displayGamma->pVtbl->IsCurrentDeGammaRamp(displayGamma,&deGammaRamp);
if(reGammaRamp)
{
displayGamma->pVtbl->GetGammaRamp(displayGamma,&ramp);
printf("\tCurrentregammaramp");
ShowGammaRamp(ramp);
}
elseif(deGammaRamp)
{
displayGamma->pVtbl->GetGammaRamp(displayGamma,&ramp);
printf("\tCurrentdegammaramp");
ShowGammaRamp(ramp);
}
elseif(curCoeff)
{
displayGamma->pVtbl->GetGammaCoefficient(displayGamma,&coeff);
printf("\tCurrentgammacoefficent");
printf("\tGetgammacoefficient:\n"
"\tA0%d,A1%d,A2%d,A3%d,Gamma%d\n",
coeff.coefficientA0,coeff.coefficientA1,
coeff.coefficientA2,coeff.coefficientA3,coeff.gamma);
}
}

//ReleasethedisplayGammainterface
if(NULL!=displayGamma)
{
displayGamma->pVtbl->Release(displayGamma);
displayGamma=NULL;
}
}

//If0isretunedSetGammaWithCustomcoeffientissuccesfullysetelseanerroroccured.
ADLX_RESULTSetGammaWithCustomCoeffs(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
//GetdisplayGamma
IADLXDisplayGamma*displayGamma;
ADLX_RESULTres=displayService->pVtbl->GetGamma(displayService,display,&displayGamma);
if(ADLX_SUCCEEDED(res))
{
printf("\t\tPleaseenterfiveintegernumbers,separatedbyspaces(eg:313081292055552400):\n");

ADLX_RESULTres=ADLX_OK;
ADLX_RegammaCoeffcoeff={0};
intuserKey=0;
intcoeffs[5]={31308,12920,55,55,2400};

for(inti=0;i<5;i++)
{
scanf_s("%d",&coeffs[i]);
}
coeff.coefficientA0=coeffs[0];
coeff.coefficientA1=coeffs[1];
coeff.coefficientA2=coeffs[2];
coeff.coefficientA3=coeffs[3];
coeff.gamma=coeffs[4];
res=displayGamma->pVtbl->SetReGammaCoefficient(displayGamma,coeff);
}

//ReleasethedisplayGammainterface
if(NULL!=displayGamma)
{
displayGamma->pVtbl->Release(displayGamma);
displayGamma=NULL;
}

returnres;
}

//SetGamma
voidSetGamma(IADLXDisplayServices*displayService,IADLXDisplay*display,intkey)
{
//GetdisplayGamma
IADLXDisplayGamma*displayGamma=NULL;
ADLX_RESULTres=displayService->pVtbl->GetGamma(displayService,display,&displayGamma);
if(ADLX_SUCCEEDED(res))
{
printf("===Setgamma===\n");
ADLX_RESULTres=ADLX_OK;

switch(key)
{
//UsepresetcoefficientsforReGammaSRGB
case0:
res=displayGamma->pVtbl->SetReGammaSRGB(displayGamma);
break;

//Usecustomcoefficients
case1:
res=SetGammaWithCustomCoeffs(displayService,display);
break;

//Userampformfile,filepath:output-night-light.txt
case2:
res=displayGamma->pVtbl->SetReGammaRamp_File(displayGamma,"output-night-light.txt");
break;

//Userampfrommemory
case3:
{
constfloatreGammaF=2.4f;
ADLX_GammaRampramp=CreateReGammaRamp(reGammaF);
res=displayGamma->pVtbl->SetReGammaRamp_Memory(displayGamma,ramp);
}
break;

//Resetgammaramp
case4:
res=displayGamma->pVtbl->ResetGammaRamp(displayGamma);
break;

default:
break;
}
printf("\treturnis:%d(0meansSuccess)\n",res);
}

//ReleasethedisplayGammainterface
if(NULL!=displayGamma)
{
displayGamma->pVtbl->Release(displayGamma);
displayGamma=NULL;
}
}

//Mainmenu
voidMainMenu()
{
printf("\tChoosefromfollowingoptions\n");

printf("\t->Press1toshowdisplaygammasupport\n");

printf("\t->Press2todisplaycurrentgammainfo\n");

printf("\t->Press3tosetReGammaSRGBusingpredefinedcoefficients\n");
printf("\t->Press4tosetReGammausingcustomcoefficients\n");
printf("\t->Press5tosetReGammausingrampfromfile[filepath:../output-night-light.txt]\n");
printf("\t->Press6tosetReGammausingrampfrommemory\n");
printf("\t->Press7toresetgammaramp\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaythemainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXDisplayServices*displayService,IADLXDisplay*display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displaygammasupport
case'1':
ShowDisplayGammaSupport(displayService,display);
break;

//Getcurrentgammastate
case'2':
GetCurrentGammaState(displayService,display);
break;

//Setgamma
case'3':
case'4':
case'5':
case'6':
case'7':
case'8':
SetGamma(displayService,display,num-'3');
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;

default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//Getdisplayservices
IADLXDisplayServices*displayService=NULL;
res=sys->pVtbl->GetDisplaysServices(sys,&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayList*displayList=NULL;
res=displayService->pVtbl->GetDisplays(displayService,&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplay*display=NULL;
res=displayList->pVtbl->At_DisplayList(displayList,it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(displayService,display);
}

//Releasethedisplayinterface
if(NULL!=display)
{
display->pVtbl->Release(display);
display=NULL;
}
}

//ReleasethedisplayListinterface
if(NULL!=displayList)
{
displayList->pVtbl->Release(displayList);
displayList=NULL;
}
}

//ReleasethedisplayServiceinetrface
if(NULL!=displayService)
{
displayService->pVtbl->Release(displayService);
displayService=NULL;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}
