#include"SDK/ADLXHelper/Windows/C/ADLXHelper.h"
#include"SDK/Include/IDesktops.h"
#include"SDK/Include/IDisplays.h"

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl();

//DisplayEyefinitysupport
voidShowEyefinitySupport();

//CreateanEyefinitydesktop
voidCreateEyefinityDesktop();

//DestroyalltheEyefinitydesktops
voidDestroyAllEyefinityDesktop();

//DestroythegivenEyefinitydesktop
voidDestroyGivenEyefinityDesktop();

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl();
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXresult:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//mainmenu
voidMainMenu()
{
printf("\tChooseonefromthefollowingoptions\n");

printf("\t->Press1todisplayEyefinitysupport\n");
printf("\t->Press2tocreateanEyefinitydesktopwithalltheenableddisplays\n");
printf("\t->Press3todestroyalltheEyefinitydesktops\n");
printf("\t->Press4todestroythegivenEyefinitydesktop\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl()
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplayEyefinitysupport
case'1':
ShowEyefinitySupport();
break;

//CreateanEyefinitydesktop
case'2':
CreateEyefinityDesktop();
break;

//DestroyalltheEyefinitydesktops
case'3':
DestroyAllEyefinityDesktop();
break;

//DestroythegivenEyefinitydesktop
case'4':
DestroyGivenEyefinityDesktop();
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//DisplayEyefinitysupportstatus
voidShowEyefinitySupport()
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

IADLXDesktopServices*desktopsInfo=NULL;
ADLX_RESULTres=sys->pVtbl->GetDesktopsServices(sys,&desktopsInfo);
if(ADLX_SUCCEEDED(res))
{
IADLXSimpleEyefinity*eyefinity=NULL;
res=desktopsInfo->pVtbl->GetSimpleEyefinity(desktopsInfo,&eyefinity);
if(ADLX_SUCCEEDED(res))
{
adlx_boolsupported=0;
res=eyefinity->pVtbl->IsSupported(eyefinity,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tEyefinityissupportedonthedesktop:%s\n",supported?"Yes":"No");
}
else
printf("\tFailedtogetsimpleEyefinity\n");

if(eyefinity!=NULL)
{
eyefinity->pVtbl->Release(eyefinity);
eyefinity=NULL;
}
}
else
printf("\tFailedtogettheDesktopServicesinterface\n");

if(desktopsInfo!=NULL)
{
desktopsInfo->pVtbl->Release(desktopsInfo);
desktopsInfo=NULL;
}
}

//CreateanEyefinitydesktop
voidCreateEyefinityDesktop()
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

IADLXDesktopServices*desktopsInfo=NULL;
ADLX_RESULTres=sys->pVtbl->GetDesktopsServices(sys,&desktopsInfo);
if(ADLX_SUCCEEDED(res))
{
IADLXSimpleEyefinity*eyefinity=NULL;
res=desktopsInfo->pVtbl->GetSimpleEyefinity(desktopsInfo,&eyefinity);
if(ADLX_SUCCEEDED(res))
{
IADLXEyefinityDesktop*eyefinityDesktop=NULL;
res=eyefinity->pVtbl->Create(eyefinity,&eyefinityDesktop);
printf("\treturncodeis:%d(0meansSuccess)\n",res);
if(eyefinityDesktop!=NULL)
{
eyefinityDesktop->pVtbl->Release(eyefinityDesktop);
eyefinityDesktop=NULL;
}
}
else
printf("\tFailedtogetsimpleEyefinity\n");

if(eyefinity!=NULL)
{
eyefinity->pVtbl->Release(eyefinity);
eyefinity=NULL;
}
}
else
printf("\tFailedtogettheDesktopServicesinterface\n");

if(desktopsInfo!=NULL)
{
desktopsInfo->pVtbl->Release(desktopsInfo);
desktopsInfo=NULL;
}
}

//DestroyalltheEyefinitydesktops
voidDestroyAllEyefinityDesktop()
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

IADLXDesktopServices*desktopsInfo=NULL;
ADLX_RESULTres=sys->pVtbl->GetDesktopsServices(sys,&desktopsInfo);
if(ADLX_SUCCEEDED(res))
{
IADLXSimpleEyefinity*eyefinity=NULL;
res=desktopsInfo->pVtbl->GetSimpleEyefinity(desktopsInfo,&eyefinity);
if(ADLX_SUCCEEDED(res))
{
res=eyefinity->pVtbl->DestroyAll(eyefinity);
printf("\treturncodeis:%d(0meanssuccess)\n",res);
}
else
printf("\tFailedtogetsimpleEyefinity\n");

if(eyefinity!=NULL)
{
eyefinity->pVtbl->Release(eyefinity);
eyefinity=NULL;
}
}
else
printf("\tFailedtogettheDesktopServicesinterface\n");

if(desktopsInfo!=NULL)
{
desktopsInfo->pVtbl->Release(desktopsInfo);
desktopsInfo=NULL;
}
}

//DestroythegivenEyefinitydesktop
voidDestroyGivenEyefinityDesktop()
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

IADLXDesktopServices*desktopsInfo=NULL;
ADLX_RESULTres=sys->pVtbl->GetDesktopsServices(sys,&desktopsInfo);
if(ADLX_SUCCEEDED(res))
{
IADLXSimpleEyefinity*eyefinity=NULL;
res=desktopsInfo->pVtbl->GetSimpleEyefinity(desktopsInfo,&eyefinity);
if(ADLX_SUCCEEDED(res))
{
IADLXDesktopList*desktops=NULL;
res=desktopsInfo->pVtbl->GetDesktops(desktopsInfo,&desktops);
if(ADLX_SUCCEEDED(res))
{
for(adlx_uintcrt=desktops->pVtbl->Begin(desktops);crt!=desktops->pVtbl->End(desktops);++crt)
{
IADLXDesktop*desktop=NULL;
res=desktops->pVtbl->At_DesktopList(desktops,crt,&desktop);
if(ADLX_SUCCEEDED(res))
{
IADLXEyefinityDesktop*eyefinityDesktop=NULL;
void*pInterf=NULL;
res=desktop->pVtbl->QueryInterface(desktop,IID_IADLXEyefinityDesktop(),&pInterf);
if(ADLX_SUCCEEDED(res))
{
eyefinityDesktop=(IADLXEyefinityDesktop*)pInterf;
res=eyefinity->pVtbl->Destroy(eyefinity,eyefinityDesktop);
printf("\tReturncodefordestroyingDesktop%dis:%d(0meansSuccess)\n",crt,res);
}
else
printf("\tEyefinityDesktopisNULL\n");

if(eyefinityDesktop!=NULL)
{
eyefinityDesktop->pVtbl->Release(eyefinityDesktop);
eyefinityDesktop=NULL;
}
}
else
printf("\tDesktopisNULL\n");

if(desktop!=NULL)
{
desktop->pVtbl->Release(desktop);
desktop=NULL;
}
}
}
else
printf("\tFailedtogettheDesktopinterface\n");

if(desktops!=NULL)
{
desktops->pVtbl->Release(desktops);
desktops=NULL;
}
}
else
printf("\tFailedtogetsimpleEyefinity\n");

if(eyefinity!=NULL)
{
eyefinity->pVtbl->Release(eyefinity);
eyefinity=NULL;
}
}
else
printf("\tFailedtogettheDesktopServicesinterface\n");

if(desktopsInfo!=NULL)
{
desktopsInfo->pVtbl->Release(desktopsInfo);
desktopsInfo=NULL;
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}
