//CallbackforGPUchangeevent
adlx_boolADLX_STD_CALLOnGPUListChanged(IADLXGPUsEventListener*pThis,IADLXGPUList*pNewGPUs)
{
printf("GPUlistchangeeventreceived\n");
if(pNewGPUs==NULL)
{
printf("GPUlistdoesnotexist\n");
returnfalse;
}

printf("GPUlistsize:%d\n",pNewGPUs->pVtbl->Size(pNewGPUs));

returntrue;
}

