//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//GetGPUlist
IADLXGPUList*gpus=NULL;
res=sys->pVtbl->GetGPUs(sys,&gpus);
if(ADLX_SUCCEEDED(res))
{
IADLXGPU*gpu=NULL;
res=gpus->pVtbl->At_GPUList(gpus,0,&gpu);
if(ADLX_SUCCEEDED(res))
{
//DisplayÂ mainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(gpu);
}

if(gpus!=NULL)
{
gpus->pVtbl->Release(gpus);
}
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

//DisplayGPUinformation
voidShowGPUInfo(IADLXGPU*gpu)
{
if(gpu!=NULL)
{
printf("\n====GPUinfo====\n");
//ShowGPUinfo
constchar*vendorId=NULL;
ADLX_RESULTret=gpu->pVtbl->VendorId(gpu,&vendorId);
printf("VendorId:%s,returncodeis:%d(0meanssuccess)\n",vendorId,ret);

ADLX_ASIC_FAMILY_TYPEasicFamilyType=ASIC_UNDEFINED;
ret=gpu->pVtbl->ASICFamilyType(gpu,&asicFamilyType);
printf("ASICFamilyType:%d,returncodeis:%d(0meanssuccess)\n",asicFamilyType,ret);

ADLX_GPU_TYPEgpuType=GPUTYPE_UNDEFINED;
ret=gpu->pVtbl->Type(gpu,&gpuType);
printf("Type:%d,returncodeis:%d(0meanssuccess)\n",gpuType,ret);

adlx_boolisExternal=false;
ret=gpu->pVtbl->IsExternal(gpu,&isExternal);
printf("IsExternal:%d,returncodeis:%d(0meanssuccess)\n",isExternal,ret);

constchar*gpuName=NULL;
ret=gpu->pVtbl->Name(gpu,&gpuName);
printf("Name:%s,returncodeis:%d(0meanssuccess)\n",gpuName,ret);

constchar*driverPath=NULL;
ret=gpu->pVtbl->DriverPath(gpu,&driverPath);
printf("DriverPath:%s,returncodeis:%d(0meanssuccess)\n",driverPath,ret);

constchar*pnpString=NULL;
ret=gpu->pVtbl->PNPString(gpu,&pnpString);
printf("PNPString:%s,returncodeis:%d(0meanssuccess)\n",pnpString,ret);

adlx_boolhasDesktops=false;
ret=gpu->pVtbl->HasDesktops(gpu,&hasDesktops);
printf("HasDesktops:%d,returncodeis:%d(0meanssuccess)\n",hasDesktops,ret);

adlx_uinttotalVRAM=0;
ret=gpu->pVtbl->TotalVRAM(gpu,&totalVRAM);
printf("TotalVRAM:%dMB,returncodeis:%d(0meanssuccess)\n",totalVRAM,ret);

adlx_intid;
ret=gpu->pVtbl->UniqueId(gpu,&id);
printf("UniqueId:%d,returncodeis:%d(0meanssuccess)\n",id,ret);

ShowDriverInfo(gpu);

IADLXGPU1*gpu1=NULL;
ret=gpu->pVtbl->QueryInterface(gpu,IID_IADLXGPU1(),&gpu1);
if(ADLX_SUCCEEDED(ret))
{
constchar*productName=NULL;
ret=gpu1->pVtbl->ProductName(gpu1,&productName);
printf("ProductName:%s\n",productName);

ADLX_MGPU_MODEmode=MGPU_NONE;
ret=gpu1->pVtbl->MultiGPUMode(gpu1,&mode);
printf("Multi-GPUMode:");
if(mode==MGPU_PRIMARY)
printf("GPUistheprimaryGPU\n");
elseif(mode==MGPU_SECONDARY)
printf("GPUisthesecondaryGPU\n");
else
printf("GPUisnotinMulti-GPU\n");

ADLX_PCI_BUS_TYPEbusType=UNDEFINED;
ret=gpu1->pVtbl->PCIBusType(gpu1,&busType);
printf("PCIBusType:%d\n",busType);

adlx_uintlaneWidth=0;
ret=gpu1->pVtbl->PCIBusLaneWidth(gpu1,&laneWidth);
printf("PCIBusLaneWidth:%d\n",laneWidth);

gpu1->pVtbl->Release(gpu1);
gpu1=NULL;
}

gpu->pVtbl->Release(gpu);
gpu=NULL;
}
}

BOOLGetStringFromLocalMachine(constTCHAR*szKeyPath_,constTCHAR*szValueName_,TCHAR*szValue_,DWORDdwSize_)
{
if((szKeyPath_==NULL)||(szValueName_==NULL))
returnfalse;

BOOLbRetVal=false;
BOOLbKeyOpenStatus=false;
DWORDdwValueType=REG_SZ;
HKEYhKeyRes;

//Opensthespecifiedkey,returningahandleobject.
if(RegOpenKeyEx(HKEY_LOCAL_MACHINE,szKeyPath_,0,KEY_QUERY_VALUE,&hKeyRes)==ERROR_SUCCESS)
bKeyOpenStatus=true;

if(bKeyOpenStatus)
{
//Retrievesthetypeanddataforaspecifiedvaluenameassociatedwithanopenregistrykey
if(RegQueryValueEx(hKeyRes,szValueName_,0,&dwValueType,(LPBYTE)szValue_,&dwSize_)==ERROR_SUCCESS)
{
//Ifthevaluetypeisstringreturntrueotherwisefalse
if(dwValueType==REG_SZ||dwValueType==REG_EXPAND_SZ)
bRetVal=true;
}

RegCloseKey(hKeyRes);
}

returnbRetVal;
}

voidShowDriverInfo(IADLXGPU*gpu)
{
ADLX_ASIC_FAMILY_TYPEasicFamilyType=ASIC_UNDEFINED;
ADLX_RESULTret=gpu->pVtbl->ASICFamilyType(gpu,&asicFamilyType);
TCHARRSVersion[MAX_PATH]="";
TCHARRSEdition[MAX_PATH]="";
switch(asicFamilyType)
{
caseASIC_RADEON:
strcpy_s(RSVersion,MAX_PATH,"RadeonSoftwareVersion");
strcpy_s(RSEdition,MAX_PATH,"RadeonSoftwareEdition");
break;
caseASIC_FIREPRO:
caseASIC_FIREMV:
caseASIC_FIRESTREAM:
strcpy_s(RSVersion,MAX_PATH,"FireproSoftwareVersion");
strcpy_s(RSEdition,MAX_PATH,"FireproSoftwareEdition");
break;
default:
strcpy_s(RSVersion,MAX_PATH,"RadeonSoftwareVersion");
strcpy_s(RSEdition,MAX_PATH,"RadeonSoftwareEdition");
break;
}

constchar*driverPath=NULL;
ret=gpu->pVtbl->DriverPath(gpu,&driverPath);
if(ADLX_SUCCEEDED(ret))
{
//Thefullpathofdriverregistry
TCHARdriverRegistryPath[MAX_PATH]={0};
sprintf_s(driverRegistryPath,MAX_PATH,"SYSTEM\\CurrentControlSet\\Control\\Class\\%s",driverPath);
TCHARvalue[MAX_PATH]="";
//GetAMDSoftwareversionfromtheregistry
GetStringFromLocalMachine(driverRegistryPath,RSVersion,value,sizeof(value));
printf("AMDSoftwareversion:%s\n",value);
//GetAMDSoftwareEditionfromtheregistry
GetStringFromLocalMachine(driverRegistryPath,RSEdition,value,sizeof(value));
printf("%s\n",value);
//GetAMDSoftwareReleaseDatefromtheregistry
GetStringFromLocalMachine(driverRegistryPath,"DriverDate",value,sizeof(value));
printf("AMDSoftwareReleaseDate:%s\n",value);
//GetDriverVersionfromtheregistry
GetStringFromLocalMachine(driverRegistryPath,"DriverVersion",value,sizeof(value));
printf("DriverVersion:%s\n",value);
//GetAMDWindowsDriverVersionfromtheregistry
GetStringFromLocalMachine(driverRegistryPath,"ReleaseVersion",value,sizeof(value));
printf("AMDWindowsDriverVersion:%s\n",value);
}
}

//DisplayGPUhybridgraphictype
voidShowHybridGraphicType()
{
ADLX_HG_TYPEhgType=NONE;

IADLXSystem*sys=ADLXHelper_GetSystemServices();
ADLX_RESULTres=sys->pVtbl->GetHybridGraphicsType(sys,&hgType);
if(ADLX_SUCCEEDED(res))
{
switch(hgType)
{
caseNONE:
printf("TheGPUhybridtypeis:NONE\n");
break;
caseAMD:
printf("TheGPUhybridtypeis:AMD\n");
break;
caseOTHER:
printf("TheGPUhybridtypeis:OTHER\n");
break;
}
}
}

//AddGPUchangeeventlistener
voidAddGPUEventListener()
{
IADLXGPUsEventListenerVtblGPUsEventListenerVtbl={&OnGPUListChanged};
IADLXGPUsEventListenergpuListener={&GPUsEventListenerVtbl};

IADLXGPUsChangedHandling*gpusChangedHandling=NULL;
IADLXSystem*sys=ADLXHelper_GetSystemServices();
ADLX_RESULTres=sys->pVtbl->GetGPUsChangedHandling(sys,&gpusChangedHandling);
if(ADLX_SUCCEEDED(res)&&gpusChangedHandling)
{
res=gpusChangedHandling->pVtbl->AddGPUsListEventListener(gpusChangedHandling,&gpuListener);
if(ADLX_SUCCEEDED(res))
{
printf("AdditionofGPUeventlistenersuccessful\n");
}

//EnableordisableaGPUdeviceusingWindowsDeviceManager
Sleep(15*1000);

res=gpusChangedHandling->pVtbl->RemoveGPUsListEventListener(gpusChangedHandling,&gpuListener);
if(ADLX_SUCCEEDED(res))
{
printf("RemovalofGPUeventlistenersuccessful\n");
}

gpusChangedHandling->pVtbl->Release(gpusChangedHandling);
}
}

//Mainmenu
voidMainMenu()
{
printf("\tChooseonefromthefollowingoptions\n");

printf("\t->Press1todisplayGPUinformation\n");

printf("\t->Press2toaddGPUeventlistener\n");

printf("\t->Press3todisplayGPUhybridgraphictype\n");

printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaythemainmenuoptions\n");
}

//Menuactioncontrol
voidMenuControl(IADLXGPU*gpu)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplayGPUinformation
case'1':
ShowGPUInfo(gpu);
break;
//AddGPUchangeeventlistener
case'2':
AddGPUEventListener();
break;
//DisplayGPUhybridgraphictype
case'3':
ShowHybridGraphicType();
break;
//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}
