staticconstIADLXGPUAppsListEventListenerVtblgpuAppsListChangedCallBackVtbl={&OnGPUAppsListChanged};
staticIADLXGPUAppsListEventListenergpuAppsListChangedCallBack={&gpuAppsListChangedCallBackVtbl};

HANDLEsmartShiftEcoChangedBlockEvent=NULL;
adlx_boolADLX_STD_CALLOnPowerTuningChanged(IADLXPowerTuningChangedListener*pThis,IADLXPowerTuningChangedEvent*pPowerTuningChangedEvent)
{
IADLXPowerTuningChangedEvent1*pPowerTuningChangedEvent1=NULL;
pPowerTuningChangedEvent->pVtbl->QueryInterface(pPowerTuningChangedEvent,IID_IADLXPowerTuningChangedEvent1(),(void**)(&pPowerTuningChangedEvent1));
if(pPowerTuningChangedEvent1->pVtbl->IsSmartShiftEcoChanged(pPowerTuningChangedEvent1))
{
printf("\tSmartShiftEcoChanged\n");
}
pPowerTuningChangedEvent1->pVtbl->Release(pPowerTuningChangedEvent1);
SetEvent(smartShiftEcoChangedBlockEvent);
returntrue;
}
staticconstIADLXPowerTuningChangedListenerVtblsmartShiftecochangedcallbackVtbl={&OnPowerTuningChanged};
staticIADLXPowerTuningChangedListenersmartShiftEcoChangedCallBack={&smartShiftecochangedcallbackVtbl};


intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=ADLXHelper_Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getsystemservices
IADLXSystem*sys=ADLXHelper_GetSystemServices();

//GetIADLXSystem1interfaceviaIADLXSystem::QueryInterface
IADLXSystem2*system2=NULL;
res=sys->pVtbl->QueryInterface(sys,IID_IADLXSystem2(),(void**)(&system2));
if(ADLX_SUCCEEDED(res))
{
smartShiftEcoChangedBlockEvent=CreateEvent(NULL,TRUE,FALSE,NULL);
//AddGPUapplicationslisteventlistener
IADLXGPUAppsListChangedHandling*appsListHandle=NULL;
res=system2->pVtbl->GetGPUAppsListChangedHandling(system2,&appsListHandle);
ADLX_RESULTaddListenerResult1=ADLX_FAIL;
if(ADLX_SUCCEEDED(res))
addListenerResult1=appsListHandle->pVtbl->AddGPUAppsListEventListener(appsListHandle,&gpuAppsListChangedCallBack);
//Getpowertuningservice
IADLXPowerTuningServices*powerTuningService=NULL;
res=system2->pVtbl->GetPowerTuningServices(system2,&powerTuningService);
if(ADLX_SUCCEEDED(res))
{
//Getpowertuningservice1
IADLXPowerTuningServices1*powerTuningService1=NULL;
res=powerTuningService->pVtbl->QueryInterface(powerTuningService,IID_IADLXPowerTuningServices1(),(void**)(&powerTuningService1));
if(ADLX_SUCCEEDED(res))
{
//Addpowertuningchangedlistener
IADLXPowerTuningChangedHandling*powerTuningChangedHandle=NULL;
res=powerTuningService1->pVtbl->GetPowerTuningChangedHandling(powerTuningService1,&powerTuningChangedHandle);
autoaddListenerResult2=ADLX_FAIL;
if(ADLX_SUCCEEDED(res))
addListenerResult2=powerTuningChangedHandle->pVtbl->AddPowerTuningEventListener(powerTuningChangedHandle,&smartShiftEcoChangedCallBack);
//GetAMDSmartShiftEcointerface
IADLXSmartShiftEco*smartShiftEco=NULL;
res=powerTuningService1->pVtbl->GetSmartShiftEco(powerTuningService1,&smartShiftEco);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(powerTuningService1,smartShiftEco);
}
else
printf("FailedtogetAMDSmartShiftEcointerface\n");
//Removepowertuningchangedlistener
if(ADLX_SUCCEEDED(addListenerResult2))
powerTuningChangedHandle->pVtbl->RemovePowerTuningEventListener(powerTuningChangedHandle,&smartShiftEcoChangedCallBack);
if(smartShiftEco!=NULL)
{
smartShiftEco->pVtbl->Release(smartShiftEco);
smartShiftEco=NULL;
}
if(powerTuningChangedHandle!=NULL)
{
powerTuningChangedHandle->pVtbl->Release(powerTuningChangedHandle);
powerTuningChangedHandle=NULL;
}
}
else
printf("Failedtogetpowertuningservice1\n");
if(powerTuningService1!=NULL)
{
powerTuningService1->pVtbl->Release(powerTuningService1);
powerTuningService1=NULL;
}
}
else
printf("Failedtogetpowertuningservice\n");
//RemoveGPUapplicationslisteventlistener
if(ADLX_SUCCEEDED(addListenerResult1))
appsListHandle->pVtbl->RemoveGPUAppsListEventListener(appsListHandle,&gpuAppsListChangedCallBack);
if(powerTuningService!=NULL)
{
powerTuningService->pVtbl->Release(powerTuningService);
powerTuningService=NULL;
}
if(appsListHandle!=NULL)
{
appsListHandle->pVtbl->Release(appsListHandle);
appsListHandle=NULL;
}
}
else
printf("FailedtogetIADLXSystem2\n");
if(system2!=NULL)
{
system2->pVtbl->Release(system2);
system2=NULL;
}
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=ADLXHelper_Terminate();
printf("DestroyADLXres:%d\n",res);

//Pausetoseetheprintout
system("pause");

return0;
}

voidMainMenu()
{
printf("\tChoosefromthefollowingoptions:\n");
printf("\t->Press1todisplayAMDSmartShiftEcosupport\n");
printf("\t->Press2todisplayAMDSmartShiftEcoenabledstate\n");
printf("\t->Press3tosetAMDSmartShiftEcoenabledstate\n");
printf("\t->Press4todisplayAMDSmartShiftEcoinactivestate\n");
printf("\t->Press5todisplayAMDGPUconnectsupport\n");
printf("\t->Press6todisplayGPUpowerstate\n");
printf("\t->Press7topoweronaGPU\n");
printf("\t->Press8topoweroffaGPU\n");
printf("\t->Press9toabortpoweroffaGPU\n");
printf("\t->PressAtosetpowercontroltodefaul\n");
printf("\t->PressBtodisplaygetapplicationlistsupport\n");
printf("\t->PressCtogetapplicationlist\n");
printf("\t->PressQ/qtoterminatetheapplication\n");
printf("\t->PressM/mtodisplaymainmenuoptions\n");
}

voidMenuControl(IADLXPowerTuningServices1*powerTuningServices,IADLXSmartShiftEco*smartShiftEco)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
case'1'://DisplayAMDSmartShiftEcosupport
ShowSmartShiftEcoSupport(smartShiftEco);
break;

case'2'://DisplayAMDSmartShiftEcoenabledstate
ShowSmartShiftEcoEnabledState(smartShiftEco);
break;

case'3'://SetAMDSmartShiftEcoenabledstate
SetSmartShiftEcoEnabledState(smartShiftEco);
break;

case'4'://DisplayAMDSmartShiftEcoinactivestate
ShowSmartShiftEcoInactiveState(smartShiftEco);
break;

case'5'://DisplayAMDGPUconnectsupport
ShowGPUConnectSupport(powerTuningServices);
break;

case'A'://Setpowercontroltodefault
SetPowerControlToDefault(smartShiftEco);
break;

case'6':
case'7':
case'8':
case'9':
case'B':
case'C':
UseGPUConnectFunctions(powerTuningServices,num);
break;

case'm'://Displaymenuoptions
case'M':
MainMenu();
break;

default:
break;
}
}
}

intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(NULL!=msg)
printf("%s\n",msg);

system("pause");
returnretCode;
}

//DisplayAMDSmartShiftEcosupport
voidShowSmartShiftEcoSupport(IADLXSmartShiftEco*smartShiftEco)
{
adlx_boolsupported=false;
ADLX_RESULTres=smartShiftEco->pVtbl->IsSupported(smartShiftEco,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tIsAMDSmartShiftEcosupported:%d\n",supported);
}

//DisplayAMDSmartShiftEcoenabledstate
voidShowSmartShiftEcoEnabledState(IADLXSmartShiftEco*smartShiftEco)
{
adlx_boolsupported=false;
ADLX_RESULTres=smartShiftEco->pVtbl->IsSupported(smartShiftEco,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
adlx_boolenabled=false;
ADLX_RESULTres=smartShiftEco->pVtbl->IsEnabled(smartShiftEco,&enabled);
if(ADLX_SUCCEEDED(res))
printf("\tIsAMDSmartShiftEcoenabled:%d\n",enabled);
}
}

//SetAMDSmartShiftEcoenabledstate
voidSetSmartShiftEcoEnabledState(IADLXSmartShiftEco*smartShiftEco)
{
adlx_boolsupported=false;
ADLX_RESULTres=smartShiftEco->pVtbl->IsSupported(smartShiftEco,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
adlx_boolenabled=false;
ADLX_RESULTres=smartShiftEco->pVtbl->IsEnabled(smartShiftEco,&enabled);
if(ADLX_SUCCEEDED(res))
printf("\tCurrentAMDSmartShiftEcoenablestateis:%d\n",enabled);
res=smartShiftEco->pVtbl->SetEnabled(smartShiftEco,!enabled);
if(ADLX_SUCCEEDED(res))
{
WaitForSingleObject(smartShiftEcoChangedBlockEvent,5000);
ResetEvent(smartShiftEcoChangedBlockEvent);
printf("\tSetAMDSmartShiftEcoenabledstateto:%d\n",!enabled);
}
res=smartShiftEco->pVtbl->IsEnabled(smartShiftEco,&enabled);
if(ADLX_SUCCEEDED(res))
printf("\tAfterset,theAMDSmartShiftEcoenablestateis:%d\n",enabled);
}
}

//DisplayAMDSmartShiftEcoinactivestate
voidShowSmartShiftEcoInactiveState(IADLXSmartShiftEco*smartShiftEco)
{
adlx_boolsupported=false;
ADLX_RESULTres=smartShiftEco->pVtbl->IsSupported(smartShiftEco,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
adlx_boolenabled=false;
ADLX_RESULTres=smartShiftEco->pVtbl->IsEnabled(smartShiftEco,&enabled);
if(ADLX_SUCCEEDED(res)&&enabled)
{
adlx_boolinactive=false;
ADLX_RESULTres=smartShiftEco->pVtbl->IsInactive(smartShiftEco,&inactive);
printf("\tIsAMDSmartShiftEcoinactive:%d\n",inactive);
if(inactive)
{
ADLX_SMARTSHIFT_ECO_INACTIVE_REASONreason=INACTIVE_REASON_UNKNOWN;
ADLX_RESULTres=smartShiftEco->pVtbl->GetInactiveReason(smartShiftEco,&reason);
printf("\tIsAMDSmartShiftEcoinactivereason:%d\n",reason);
}
}
}
}

//DisplayAMDGPUconnectsupport
voidShowGPUConnectSupport(IADLXPowerTuningServices1*powerTuningService)
{
adlx_boolsupported=false;
ADLX_RESULTres=powerTuningService->pVtbl->IsGPUConnectSupported(powerTuningService,&supported);
if(ADLX_SUCCEEDED(res))
printf("\tIsGPUconnectsupported:%d\n",supported);
}

//UsefunctionsofGPUconnect
voidUseGPUConnectFunctions(IADLXPowerTuningServices1*powerTuningService,charnum)
{
adlx_boolsupported=false;
ADLX_RESULTres=powerTuningService->pVtbl->IsGPUConnectSupported(powerTuningService,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
IADLXGPU2List*gpuList=NULL;
res=powerTuningService->pVtbl->GetGPUConnectGPUs(powerTuningService,&gpuList);
if(ADLX_SUCCEEDED(res)&&!gpuList->pVtbl->Empty(gpuList))
{
//UsethefirstGPU
IADLXGPU2*gpu=NULL;
res=gpuList->pVtbl->At_GPU2List(gpuList,gpuList->pVtbl->Begin(gpuList),&gpu);
if(ADLX_SUCCEEDED(res))
{
switch(num)
{
case'6'://DisplayGPUpowerstate
ShowGPUPowerState(gpu);
break;

case'7'://PoweronaGPU
PowerOnGPU(gpu);
break;

case'8'://PoweroffaGPU
PowerOffGPU(gpu);
break;

case'9'://AbortpoweroffaGPU
AbortPowerOffGPU(gpu);
break;

case'B'://Displaygetapplicationlistsupport
ShowGetApplicationListSupport(gpu);
break;

case'C'://Getapplicationlist
GetApplicationList(gpu);
break;

default:
break;
}

}
if(gpu!=NULL)
{
gpu->pVtbl->Release(gpu);
gpu=NULL;
}
}
else
printf("\tNotfoundGPUsupportGPUconnect\n");
if(gpuList!=NULL)
{
gpuList->pVtbl->Release(gpuList);
gpuList=NULL;
}
}
else
printf("\tGPUconnectisnotsupported\n");
}

//DisplayGPUpowerstate
voidShowGPUPowerState(IADLXGPU2*gpu)
{
adlx_boolstate=false;
ADLX_RESULTres=gpu->pVtbl->IsPowerOff(gpu,&state);
if(ADLX_SUCCEEDED(res))
printf("\tIsGPUpoweroff:%d\n",state);
}

//PoweronaGPU
voidPowerOnGPU(IADLXGPU2*gpu)
{
adlx_boolstate=false;
ADLX_RESULTres=gpu->pVtbl->IsPowerOff(gpu,&state);
if(ADLX_SUCCEEDED(res))
printf("\tCurrenttheGPUpowerstateis:%s\n",(state?"OFF":"ON"));
res=gpu->pVtbl->PowerOn(gpu);
if(ADLX_SUCCEEDED(res))
printf("\tPowerontheGPU\n");
res=gpu->pVtbl->IsPowerOff(gpu,&state);
if(ADLX_SUCCEEDED(res))
printf("\tAfterset,theGPUpowerstateis:%s\n",(state?"OFF":"ON"));
}

adlx_boolADLX_STD_CALLOnGPUConnectChanged(IADLXGPUConnectChangedListener*pThis,IADLXGPUConnectChangedEvent*pGPUConnectChangedEvent)
{
if(pGPUConnectChangedEvent->pVtbl->IsGPUPowerChanged(pGPUConnectChangedEvent))
printf("\tGPUpowerchanged\n");
if(pGPUConnectChangedEvent->pVtbl->IsGPUAppsListChanged(pGPUConnectChangedEvent))
{
printf("\tGPUapplicationslistchanged\n");
IADLXGPU2*gpu=NULL;
ADLX_RESULTres=pGPUConnectChangedEvent->pVtbl->GetGPU(pGPUConnectChangedEvent,&gpu);
if(ADLX_SUCCEEDED(res))
{
IADLXApplicationList*appList=NULL;
res=gpu->pVtbl->GetApplications(gpu,&appList);
if(ADLX_SUCCEEDED(res))
{
for(adlx_uintit=appList->pVtbl->Begin(appList);it!=appList->pVtbl->End(appList);++it)
{
IADLXApplication*app=NULL;
res=appList->pVtbl->At_ApplicationList(appList,it,&app);
if(ADLX_SUCCEEDED(res))
{
adlx_ulongpid=0;
res=app->pVtbl->ProcessID(app,&pid);
if(ADLX_SUCCEEDED(res))
printf("\tTheprocessIDoftheprocessrunningontheGPUis:%d\n",pid);

constwchar_t*name=NULL;
res=app->pVtbl->Name(app,&name);
if(ADLX_SUCCEEDED(res))
printf("\tThenameoftheprocessrunningontheGPUis:%ls\n",name);

constwchar_t*fullPath=NULL;
res=app->pVtbl->Name(app,&fullPath);
if(ADLX_SUCCEEDED(res))
printf("\tThefullpathoftheprocessrunningontheGPUis:%ls\n",fullPath);

ADLX_APP_GPU_DEPENDENCYtype=APP_GPU_UNKNOWN;
res=app->pVtbl->GPUDependencyType(app,&type);
if(ADLX_SUCCEEDED(res))
{
if(type==APP_GPU_BOUND)
printf("\tTheGPUdependencytypeoftheprocessrunningontheGPUis:APP_GPU_BOUND\n");
elseif(type==APP_GPU_NOT_BOUND)
printf("\tTheGPUdependencytypeoftheprocessrunningontheGPUis:APP_GPU_NOT_BOUND\n");
else
printf("\tTheGPUdependencytypeoftheprocessrunningontheGPUis:APP_GPU_UNKNOWN\n");
}
}
if(app!=NULL)
{
app->pVtbl->Release(app);
app=NULL;
}
}
}
if(appList!=NULL)
{
appList->pVtbl->Release(appList);
appList=NULL;
}
}
if(gpu!=NULL)
{
gpu->pVtbl->Release(gpu);
gpu=NULL;
}
}
ADLX_RESULTres=ADLX_FAIL;
if(pGPUConnectChangedEvent->pVtbl->IsGPUPowerChangeError(pGPUConnectChangedEvent,&res))
{
if(res==ADLX_TIMEOUT_OPERATION)
printf("\tGPUpowerchangeerror,timeoutoperation\n");
else
printf("\tGPUpowerchangeerror,resultis:%d\n",res);
}

returntrue;
}

staticconstIADLXGPUConnectChangedListenerVtblgpuConnectChangedListenerVtbl={&OnGPUConnectChanged};
staticIADLXGPUConnectChangedListenergpuConnectChangedListener={&gpuConnectChangedListenerVtbl};

//PoweroffaGPU
voidPowerOffGPU(IADLXGPU2*gpu)
{
adlx_boolstate=false;
ADLX_RESULTres=gpu->pVtbl->IsPowerOff(gpu,&state);
if(ADLX_SUCCEEDED(res))
printf("\tCurrenttheGPUpowerstateis:%s\n",(state?"OFF":"ON"));

res=gpu->pVtbl->StartPowerOff(gpu,&gpuConnectChangedListener,10000);
if(ADLX_SUCCEEDED(res))
printf("\tPowerofftheGPU\n");
elseif(res==ADLX_GPU_IN_USE)
{
printf("\tStartpowerofftheGPU,butGPUisinuse,waitfor10seconds\n");
printf("\tPress9canabortpoweroffGPUduringthistime\n");
}
}

//AbortpoweroffaGPU
voidAbortPowerOffGPU(IADLXGPU2*gpu)
{
ADLX_RESULTres=gpu->pVtbl->AbortPowerOff(gpu);
if(ADLX_SUCCEEDED(res))
printf("\tAbortpoweroffGPUsuccessfully\n");
}

