#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/I3DSettings.h"
#include<iostream>
#include<map>
#include<string>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Displayanti-aliasingsupport
voidShowAntiAliasingSupport(constIADLX3DAntiAliasingPtr&antiAliasing);

//Displayanti-aliasingcurrentstate
voidGetAntiAliasingState(constIADLX3DAntiAliasingPtr&antiAliasing);

//Setanti-aliasingmode
voidSetAntiAliasingMode(constIADLX3DAntiAliasingPtr&antiAliasing,intindex);

//Setanti-aliasingmethod
voidSetAntiAliasingMethod(constIADLX3DAntiAliasingPtr&antiAliasing);

//Setanti-aliasinglevel
voidSetAntiAliasingLevel(constIADLX3DAntiAliasingPtr&antiAliasing);

//Menu
voidMainMenu();

//Menucontrol
voidMenuControl(constIADLX3DAntiAliasingPtr&antiAliasing);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//GetGPUs
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_SUCCEEDED(res)&&!gpus->Empty())
{
//Get3DSettingsservice
IADLX3DSettingsServicesPtrd3dSettingSrv;
res=g_ADLXHelp.GetSystemServices()->Get3DSettingsServices(&d3dSettingSrv);
if(ADLX_SUCCEEDED(res))
{
//GetGPUinterface
IADLXGPUPtrgpuInfo;
adlx_uintindex=0;
res=gpus->At(index,&gpuInfo);
if(ADLX_SUCCEEDED(res))
{
//GetAntiAliasinginterface
IADLX3DAntiAliasingPtrantiAliasing;
res=d3dSettingSrv->GetAntiAliasing(gpuInfo,&antiAliasing);
if(ADLX_SUCCEEDED(res))
{
MainMenu();
MenuControl(antiAliasing);
}
}
}
else
{
std::cout<<"Failedtogetthe3DSettingsServices"<<std::endl;
}
}
else
{
std::cout<<"FailedtogetGPU"<<std::endl;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

staticconststd::map<ADLX_ANTI_ALIASING_MODE,constchar*>antiAliasingMode=
{
{AA_MODE_USE_APP_SETTINGS,"Useapplicationsettings"},
{AA_MODE_ENHANCE_APP_SETTINGS,"Enhanceapplicationsettings"},
{AA_MODE_OVERRIDE_APP_SETTINGS,"Overrideapplicationsettings"}};

staticconststd::map<ADLX_ANTI_ALIASING_METHOD,constchar*>antiAliasingMethod=
{
{AA_METHOD_MULTISAMPLING,"Muti-Sampling"},
{AA_METHOD_ADAPTIVE_MULTISAMPLING,"AdaptiveMuti-Sampling"},
{AA_METHOD_SUPERSAMPLING,"SuperSampling"}};

staticconststd::map<ADLX_ANTI_ALIASING_LEVEL,constchar*>antiAliasingLevel=
{
{AA_LEVEL_INVALID,"LevelInvalid"},
{AA_LEVEL_2X,"2X"},
{AA_LEVEL_2XEQ,"2XEQ"},
{AA_LEVEL_4X,"4X"},
{AA_LEVEL_4XEQ,"4XEQ"},
{AA_LEVEL_8X,"8X"},
{AA_LEVEL_8XEQ,"8XEQ"}};

voidShowAntiAliasingSupport(constIADLX3DAntiAliasingPtr&antiAliasing)
{
adlx_boolsupported=false;
ADLX_RESULTres=antiAliasing->IsSupported(&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupported:"<<supported<<std::endl;
}

voidGetAntiAliasingState(constIADLX3DAntiAliasingPtr&antiAliasing)
{
ADLX_ANTI_ALIASING_MODEmode;
ADLX_RESULTres=antiAliasing->GetMode(&mode);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tMode:"<<antiAliasingMode.find(mode)->second<<std::endl;
ADLX_ANTI_ALIASING_METHODmethod;
res=antiAliasing->GetMethod(&method);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tMethod:"<<antiAliasingMethod.find(method)->second<<std::endl;
ADLX_ANTI_ALIASING_LEVELlevel;
res=antiAliasing->GetLevel(&level);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tLevel:"<<antiAliasingLevel.find(level)->second<<std::endl;
}

voidSetAntiAliasingMode(constIADLX3DAntiAliasingPtr&antiAliasing,intindex)
{
ADLX_RESULTres=ADLX_FAIL;
ADLX_ANTI_ALIASING_MODEmode=AA_MODE_USE_APP_SETTINGS;
mode=(ADLX_ANTI_ALIASING_MODE)(mode+index);
res=antiAliasing->SetMode(mode);
std::cout<<"\tReturncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}

voidSetAntiAliasingMethod(constIADLX3DAntiAliasingPtr&antiAliasing)
{
ADLX_ANTI_ALIASING_METHODmethod=AA_METHOD_MULTISAMPLING;
ADLX_RESULTres=antiAliasing->GetMethod(&method);
if(ADLX_SUCCEEDED(res))
{
method=(ADLX_ANTI_ALIASING_METHOD)((method+1)%3);
res=antiAliasing->SetMethod(method);
std::cout<<"\tSetmethod:"<<antiAliasingMethod.find(method)->second<<",resis"<<res<<std::endl;
}
}

voidSetAntiAliasingLevel(constIADLX3DAntiAliasingPtr&antiAliasing)
{
ADLX_ANTI_ALIASING_LEVELlevel;
ADLX_RESULTres=antiAliasing->GetLevel(&level);
if(ADLX_SUCCEEDED(res))
{
level=(level==AA_LEVEL_2X)?AA_LEVEL_4X:AA_LEVEL_2X;
res=antiAliasing->SetLevel(level);
std::cout<<"\tSetlevel:"<<antiAliasingLevel.find(level)->second<<",resis"<<res<<std::endl;
}
}

intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions"<<std::endl;

std::cout<<"\t->Press1todisplayanti-aliasingsupport"<<std::endl;
std::cout<<"\t->Press2todisplaycurrentanti-aliasingstate"<<std::endl;
std::cout<<"\t->Press3tosetanti-aliasingmode:Useapplicationsettings"<<std::endl;
std::cout<<"\t->Press4tosetanti-aliasingmode:Enhanceapplicationsettings"<<std::endl;
std::cout<<"\t->Press5tosetanti-aliasingmode:Overrideapplicationsettings"<<std::endl;

std::cout<<"\t->Press6tochangeanti-aliasingmethod"<<std::endl;
std::cout<<"\t->Press7tochangeanti-aliasinglevel"<<std::endl;

std::cout<<"\t->PressQ/qtoquittheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymenuoptions"<<std::endl;
}

voidMenuControl(constIADLX3DAntiAliasingPtr&antiAliasing)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displayanti-aliasingsupport
case'1':
ShowAntiAliasingSupport(antiAliasing);
break;

//Displaycurrentanti-aliasingstatus
case'2':
GetAntiAliasingState(antiAliasing);
break;

//Setanti-aliasingmode
case'3':
case'4':
case'5':
SetAntiAliasingMode(antiAliasing,num-'3');
break;

//Setanti-aliasingmethod
case'6':
SetAntiAliasingMethod(antiAliasing);
break;

//Setanti-aliasinglevel
case'7':
SetAntiAliasingLevel(antiAliasing);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}
