#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IGPUAutoTuning.h"
#include"SDK/Include/IGPUTuning.h"
#include<iostream>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Eventtoverifycallback.
HANDLEg_ADLXEvent=nullptr;

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//CheckifcurrentGPUtuningstatusissettofactorydefault
voidCheckGPUTuningIsAtFactoryStatus(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus);

//RestoreGPUtuningstatustofactorydefault
voidRestoreGPUTuningToFactoryStatus(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus);

//DisplayGPUauto-tuningsupportstatus
voidShowGPUAutoTuningSupport(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus);

//DisplaycurrentGPUstatus
voidShowCurrentGPUStatus(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus);

//DerivefromIADLXGPUAutoTuningCompleteListener
classGPUAutoTuningCompleteEvents;
//StartGPUauto-tuning
voidStartGPUAutoTuning(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus,intnum);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
//Createevent
g_ADLXEvent=CreateEvent(NULL,TRUE,FALSE,TEXT("ADLXEvent"));

IADLXGPUTuningServicesPtrgpuTuningService;
res=g_ADLXHelp.GetSystemServices()->GetGPUTuningServices(&gpuTuningService);
if(ADLX_SUCCEEDED(res))
{
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(gpuTuningService,gpus);
}
else
std::cout<<"\tGetGPUlistfailed"<<std::endl;
}
else
std::cout<<"\tGetGPUtuningservicesfailed"<<std::endl;
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Closeevent
if(g_ADLXEvent)
CloseHandle(g_ADLXEvent);

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1tocheckifcurrentGPUtuningstatusissettofactorydefault"<<std::endl;
std::cout<<"\t->Press2torestorethecurrentGPUtuningstatustofactorydefault"<<std::endl;
std::cout<<"\t->Press3todisplayGPUauto-tuningsupport"<<std::endl;
std::cout<<"\t->Press4todisplaycurrentGPUauto-tuningstatus"<<std::endl;
std::cout<<"\t->Press5tostartundervoltGPUtuning"<<std::endl;
std::cout<<"\t->Press6tostartoverclockGPUtuning"<<std::endl;
std::cout<<"\t->Press7tostartoverclockVRAMtuning"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//CheckifcurrentGPUtuningstatusissettofactorydefault
case'1':
CheckGPUTuningIsAtFactoryStatus(gpuTuningService,gpus);
break;

//RestoreGPUtuningstatustofactorydefault
case'2':
RestoreGPUTuningToFactoryStatus(gpuTuningService,gpus);
break;

//DisplayGPUauto-tuningsupportstatus
case'3':
ShowGPUAutoTuningSupport(gpuTuningService,gpus);
break;

//DisplaycurrentGPUstatus
case'4':
ShowCurrentGPUStatus(gpuTuningService,gpus);
break;

//StartundervoltGPUtuning
case'5':
//StartoverclockGPUtuning
case'6':
//StartoverclockVRAMtuning
case'7':
StartGPUAutoTuning(gpuTuningService,gpus,num-5);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//CheckifcurrentGPUtuningstatusissettofactorydefault
voidCheckGPUTuningIsAtFactoryStatus(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus)
{
IADLXGPUPtroneGPU;
for(adlx_uintcrt=gpus->Begin();crt!=gpus->End();++crt)
{
ADLX_RESULTres=gpus->At(crt,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==nullptr)
{
std::cout<<"\tGettheGPUatlocation"<<crt<<"failed"<<std::endl;
break;
}
adlx_boolisFactory=false;
res=gpuTuningService->IsAtFactory(oneGPU,&isFactory);
if(ADLX_FAILED(res))
{
std::cout<<"\tCheckoftheGPUatlocation"<<crt<<"defaulttuningstatusfailed"<<std::endl;
break;
}
std::cout<<"\tTheGPUatlocation"<<crt<<"defaultstatus:"<<isFactory<<std::endl;
}
}

//ResetthecurrentGPUtuningstatustofactorydefault
voidRestoreGPUTuningToFactoryStatus(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus)
{
IADLXGPUPtroneGPU;
for(adlx_uintcrt=gpus->Begin();crt!=gpus->End();++crt)
{
ADLX_RESULTres=gpus->At(crt,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==nullptr)
{
std::cout<<"\tGettheGPUatlocation"<<crt<<"failed"<<std::endl;
break;
}
adlx_boolisFactory=false;
res=gpuTuningService->ResetToFactory(oneGPU);
if(ADLX_FAILED(res))
std::cout<<"\tResettheGPUatlocation"<<crt<<"todefaultfailed"<<std::endl;
else
std::cout<<"\tResettheGPUatlocation"<<crt<<"todefaultsuccessful"<<std::endl;
}
}

//DisplayGPUauto-tuningsupportstatus
voidShowGPUAutoTuningSupport(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus)
{
IADLXGPUPtroneGPU;
for(adlx_uintcrt=gpus->Begin();crt!=gpus->End();++crt)
{
ADLX_RESULTres=gpus->At(crt,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==nullptr)
{
std::cout<<"\tGettheGPUatlocation"<<crt<<"failed"<<std::endl;
break;
}
adlx_boolsupported=false;
res=gpuTuningService->IsSupportedAutoTuning(oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
std::cout<<"\tTheGPUatlocation"<<crt<<"doesnotsupportauto-tuning"<<std::endl;
break;
}
IADLXInterfacePtrautoTuningIfc;
res=gpuTuningService->GetAutoTuning(oneGPU,&autoTuningIfc);
if(ADLX_FAILED(res)||autoTuningIfc==nullptr)
{
std::cout<<"\tGetauto-tuningfortheGPUatlocation"<<crt<<"failed"<<std::endl;
break;
}
IADLXGPUAutoTuningPtrautoTuning(autoTuningIfc);
if(autoTuning)
{
//Ifret!=ADLX_OK,returndefaultvalue(false).
supported=false;
res=autoTuning->IsSupportedUndervoltGPU(&supported);
std::cout<<"\tIstheGPUatlocation"<<crt<<"supportedundervolt:"<<supported<<std::endl;
supported=false;
res=autoTuning->IsSupportedOverclockGPU(&supported);
std::cout<<"\tIstheGPUatlocation"<<crt<<"supportedoverclock:"<<supported<<std::endl;
supported=false;
res=autoTuning->IsSupportedOverclockVRAM(&supported);
std::cout<<"\tIstheGPUatlocation"<<crt<<"supportedoverclockVRAM:"<<supported<<std::endl;
}
}
}

//DisplaycurrentGPUstatus
voidShowCurrentGPUStatus(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus)
{
IADLXGPUPtroneGPU;
for(adlx_uintcrt=gpus->Begin();crt!=gpus->End();++crt)
{
ADLX_RESULTres=gpus->At(crt,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==nullptr)
{
std::cout<<"\tGetGPUatlocation"<<crt<<"failed"<<std::endl;
break;
}
adlx_boolsupported=false;
res=gpuTuningService->IsSupportedAutoTuning(oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
std::cout<<"\tTheGPUatlocation"<<crt<<"doesnotsupportauto-tuning"<<std::endl;
break;
}
IADLXInterfacePtrautoTuningIfc;
res=gpuTuningService->GetAutoTuning(oneGPU,&autoTuningIfc);
if(ADLX_FAILED(res)||autoTuningIfc==nullptr)
{
std::cout<<"\tGetauto-tuningfortheGPUatlocation"<<crt<<"failed"<<std::endl;
break;
}
IADLXGPUAutoTuningPtrautoTuning(autoTuningIfc);
adlx_boolapplied=false;
if(autoTuning)
{
//Ifret!=ADLX_OK,returndefaultvalue(false).
applied=false;
res=autoTuning->IsCurrentUndervoltGPU(&applied);
std::cout<<"\tIstheGPUatlocation"<<crt<<"undervoltapplied:"<<applied<<std::endl;
applied=false;
res=autoTuning->IsCurrentOverclockGPU(&applied);
std::cout<<"\tIstheGPUatlocation"<<crt<<"overclockapplied:"<<applied<<std::endl;
applied=false;
res=autoTuning->IsCurrentOverclockVRAM(&applied);
std::cout<<"\tIstheGPUatlocation"<<crt<<"overclockVRAMapplied:"<<applied<<std::endl;
}
}
}

classGPUAutoTuningCompleteEvents:publicIADLXGPUAutoTuningCompleteListener
{
public:
adlx_boolADLX_STD_CALLOnGPUAutoTuningComplete(IADLXGPUAutoTuningCompleteEvent*pGPUAutoTuningCompleteEvent)
{
adlx_boolchange=false;
change=pGPUAutoTuningCompleteEvent->IsUndervoltGPUCompleted();
change=pGPUAutoTuningCompleteEvent->IsOverclockGPUCompleted();
change=pGPUAutoTuningCompleteEvent->IsOverclockVRAMCompleted();
SetEvent(g_ADLXEvent);
returntrue;
}
};

GPUAutoTuningCompleteEventsgpuAutoTuningCompleteEvents;

voidStartGPUAutoTuning(IADLXGPUTuningServicesPtrgpuTuningService,IADLXGPUListPtrgpus,intnum)
{
IADLXGPUPtroneGPU;
//SetthefirstGPUauto-tuning
ADLX_RESULTres=gpus->At(0,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==nullptr)
{
std::cout<<"\tGetGPUfailed"<<std::endl;
return;
}
adlx_boolsupported=false;
res=gpuTuningService->IsSupportedAutoTuning(oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
std::cout<<"\tTheGPUdoesnotsupportauto-tuning"<<std::endl;
return;
}
IADLXInterfacePtrautoTuningIfc;
res=gpuTuningService->GetAutoTuning(oneGPU,&autoTuningIfc);
if(ADLX_FAILED(res)||autoTuningIfc==nullptr)
{
std::cout<<"\tGetGPUauto-tuningfailed"<<std::endl;
return;
}
IADLXGPUAutoTuningPtrautoTuning(autoTuningIfc);
if(autoTuning)
{
boolmodeIsSupported=false;
switch(num)
{
case'0':
res=autoTuning->IsSupportedUndervoltGPU(&modeIsSupported);
if(!modeIsSupported){
std::cout<<"\tUndervoltprofileisnotsupported"<<std::endl;
}
else{
res=autoTuning->StartUndervoltGPU(&gpuAutoTuningCompleteEvents);
WaitForSingleObject(g_ADLXEvent,1000);
ResetEvent(g_ADLXEvent);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tStartundervoltGPUsuccessful"<<std::endl;
else
std::cout<<"\tStartundervoltGPUfailed"<<std::endl;
}
break;
case'1':
res=autoTuning->IsSupportedOverclockGPU(&modeIsSupported);
if(!modeIsSupported){
std::cout<<"\tGPUOverclockprofileisnotsupported"<<std::endl;
}
else{
res=autoTuning->StartOverclockGPU(&gpuAutoTuningCompleteEvents);
WaitForSingleObject(g_ADLXEvent,1000);
ResetEvent(g_ADLXEvent);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tStartoverclockGPUsuccessful"<<std::endl;
else
std::cout<<"\tStartoverclockGPUfailed"<<std::endl;
}
break;
case'2':
res=autoTuning->IsSupportedOverclockVRAM(&modeIsSupported);
if(!modeIsSupported){
std::cout<<"\tVRAMoverclockprofileisnotsupported"<<std::endl;
}
else{
res=autoTuning->StartOverclockVRAM(&gpuAutoTuningCompleteEvents);
WaitForSingleObject(g_ADLXEvent,1000);
ResetEvent(g_ADLXEvent);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tStartoverclockVRAMsuccessful"<<std::endl;
else
std::cout<<"\tStartoverclockVRAMfailed"<<std::endl;
}
break;
default:
break;
}
}
}
