#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IGPUPresetTuning.h"
#include"SDK/Include/IGPUTuning.h"
#include<iostream>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXGPUPresetTuningPtrgpuPresetTuning);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//DisplayGPUtuningsupportstates
voidShowIsSupported(IADLXGPUPresetTuningPtrgpuPresetTuning);

//DisplaycurrentGPUtuningstates
voidGetCurrentStates(IADLXGPUPresetTuningPtrgpuPresetTuning);

//SetGPUstates
voidSetGPUStates(IADLXGPUPresetTuningPtrgpuPresetTuning);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXGPUTuningServicesPtrgpuTuningService;
res=g_ADLXHelp.GetSystemServices()->GetGPUTuningServices(&gpuTuningService);
if(ADLX_SUCCEEDED(res))
{
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_SUCCEEDED(res))
{
IADLXGPUPtroneGPU;
res=gpus->At(0,&oneGPU);
if(ADLX_SUCCEEDED(res)&&oneGPU!=nullptr)
{
adlx_boolsupported=false;
res=gpuTuningService->IsSupportedPresetTuning(oneGPU,&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
IADLXInterfacePtrgpuPresetTuningIfc;
res=gpuTuningService->GetPresetTuning(oneGPU,&gpuPresetTuningIfc);
if(ADLX_SUCCEEDED(res)&&gpuPresetTuningIfc!=nullptr)
{
IADLXGPUPresetTuningPtrgpuPresetTuning(gpuPresetTuningIfc);
if(gpuPresetTuning!=nullptr)
{
//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(gpuPresetTuning);
}
else
{
std::cout<<"\tGetGPUpresettuningfailed"<<std::endl;
}
}
else
{
std::cout<<"\tGetGPUpresettuninginterfacefailed"<<std::endl;
}
}
else
{
std::cout<<"\tGPUpresettuningisnotsupportedbythisGPU"<<std::endl;
}
}
else
{
std::cout<<"\tGetGPUfailed"<<std::endl;
}
}
else
{
std::cout<<"\tGetGPUlistfailed"<<std::endl;
}
}
else
{
std::cout<<"\tGetGPUtuningservicesfailed"<<std::endl;
}
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1todisplayGPUtuningsupportstates"<<std::endl;
std::cout<<"\t->Press2todisplaycurrentGPUtuningstates"<<std::endl;
std::cout<<"\t->Press3tosetGPUstates"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

voidMenuControl(IADLXGPUPresetTuningPtrgpuPresetTuning)
{
charnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplayGPUtuningsupportstates
case'1':
ShowIsSupported(gpuPresetTuning);
break;

//DisplaycurrentGPUtuningstates
case'2':
GetCurrentStates(gpuPresetTuning);
break;

//SetGPUstates
case'3':
SetGPUStates(gpuPresetTuning);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//DisplayGPUtuningsupportstates
voidShowIsSupported(IADLXGPUPresetTuningPtrgpuPresetTuning)
{
adlx_boolsupported=false;
ADLX_RESULTres=gpuPresetTuning->IsSupportedPowerSaver(&supported);
std::cout<<"\tIsPowerSaversupportedbytheGPU:"<<supported<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
supported=false;
res=gpuPresetTuning->IsSupportedQuiet(&supported);
std::cout<<"\tIsQuietsupportedbytheGPU:"<<supported<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
supported=false;
res=gpuPresetTuning->IsSupportedBalanced(&supported);
std::cout<<"\tIsBalancedsupportedbytheGPU:"<<supported<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
supported=false;
res=gpuPresetTuning->IsSupportedTurbo(&supported);
std::cout<<"\tIsTurbosupportedbytheGPU:"<<supported<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
supported=false;
res=gpuPresetTuning->IsSupportedRage(&supported);
std::cout<<"\tIsRagesupportedbytheGPU:"<<supported<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}

//DisplaycurrentGPUtuningstates
voidGetCurrentStates(IADLXGPUPresetTuningPtrgpuPresetTuning)
{
adlx_boolapplied=false;
ADLX_RESULTres=gpuPresetTuning->IsCurrentPowerSaver(&applied);
std::cout<<"\tIsPowerSaverapplied:"<<applied<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
applied=false;
res=gpuPresetTuning->IsCurrentQuiet(&applied);
std::cout<<"\tIsQuietapplied:"<<applied<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
applied=false;
res=gpuPresetTuning->IsCurrentBalanced(&applied);
std::cout<<"\tIsBalancedapplied:"<<applied<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
applied=false;
res=gpuPresetTuning->IsCurrentTurbo(&applied);
std::cout<<"\tIsTurboapplied:"<<applied<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
applied=false;
res=gpuPresetTuning->IsCurrentRage(&applied);
std::cout<<"\tIsRageapplied:"<<applied<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}

//SetGPUstates
voidSetGPUStates(IADLXGPUPresetTuningPtrgpuPresetTuning)
{
adlx_boolapplied=false;
ADLX_RESULTres=gpuPresetTuning->SetPowerSaver();
res=gpuPresetTuning->IsCurrentPowerSaver(&applied);
std::cout<<"\tSetPowerSaverpresettuning"<<(applied?"Successful":"failed")<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
applied=false;
res=gpuPresetTuning->SetQuiet();
res=gpuPresetTuning->IsCurrentQuiet(&applied);
std::cout<<"\tSetQuietpresettuning"<<(applied?"Successful":"failed")<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
applied=false;
res=gpuPresetTuning->SetBalanced();
res=gpuPresetTuning->IsCurrentBalanced(&applied);
std::cout<<"\tSetBalancedpresettuning"<<(applied?"Successful":"failed")<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
applied=false;
res=gpuPresetTuning->SetTurbo();
res=gpuPresetTuning->IsCurrentTurbo(&applied);
std::cout<<"\tSetTurbopresettuning"<<(applied?"Successful":"failed")<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
applied=false;
res=gpuPresetTuning->SetRage();
res=gpuPresetTuning->IsCurrentRage(&applied);
std::cout<<"\tSetRagepresettuning"<<(applied?"Successful":"failed")<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}
