#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IGPUManualFanTuning.h"
#include"SDK/Include/IGPUTuning.h"
#include<iostream>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXManualFanTuningPtrmanualFanTuning);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//Displayandsetfansettings
voidShowGetAndSetFan(IADLXManualFanTuningPtrmanualFanTuning);

//DisplayandsetZeroRPMsettings
voidShowGetAndSetZeroRPM(IADLXManualFanTuningPtrmanualFanTuning);

//DisplayandsetMinAcousticsettings
voidShowGetAndSetMinAcoustic(IADLXManualFanTuningPtrmanualFanTuning);

//DisplayandsetMinFanSpeedsettings
voidShowGetAndSetMinFanSpeed(IADLXManualFanTuningPtrmanualFanTuning);

//DisplayandsetTargetFanSpeedsettings
voidShowGetAndSetTargetFanSpeed(IADLXManualFanTuningPtrmanualFanTuning);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXGPUTuningServicesPtrgpuTuningService;
res=g_ADLXHelp.GetSystemServices()->GetGPUTuningServices(&gpuTuningService);
if(ADLX_FAILED(res))
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUtuningservicesfailed",0);
}
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_FAILED(res))
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUlistfailed",0);
}
IADLXGPUPtroneGPU;
res=gpus->At(0,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUfailed",0);
}
adlx_boolsupported=false;
res=gpuTuningService->IsSupportedManualFanTuning(oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tThisGPUdoesn'tsupportedmanualfantuning",0);
}
IADLXInterfacePtrmanualFanTuningIfc;
res=gpuTuningService->GetManualFanTuning(oneGPU,&manualFanTuningIfc);
if(ADLX_FAILED(res)||manualFanTuningIfc==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetmanualfantuninginterfacefailed",0);
}
IADLXManualFanTuningPtrmanualFanTuning(manualFanTuningIfc);
if(manualFanTuning==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetmanualfantuningfailed",0);
}
//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(manualFanTuning);
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1todisplayandsetfansettings"<<std::endl;
std::cout<<"\t->Press2todisplayandsetZeroRPMsettings"<<std::endl;
std::cout<<"\t->Press3todisplayandsetMinAcousticsettings"<<std::endl;
std::cout<<"\t->Press4todisplayandsetMinFanSpeedsettings"<<std::endl;
std::cout<<"\t->Press5todisplayandsetTargetFanSpeedsettings"<<std::endl;
std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(IADLXManualFanTuningPtrmanualFanTuning)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displayandsetfansettings
case'1':
ShowGetAndSetFan(manualFanTuning);
break;

//DisplayandsetZeroRPMsettings
case'2':
ShowGetAndSetZeroRPM(manualFanTuning);
break;

//DisplayandsetMinAcousticsettings
case'3':
ShowGetAndSetMinAcoustic(manualFanTuning);
break;

//DisplayandsetMinFanSpeedsettings
case'4':
ShowGetAndSetMinFanSpeed(manualFanTuning);
break;

//DisplayandsetTargetFanSpeedsettings
case'5':
ShowGetAndSetTargetFanSpeed(manualFanTuning);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//Displayandsetfansettings
voidShowGetAndSetFan(IADLXManualFanTuningPtrmanualFanTuning)
{
//Displaymanualfantuningranges(speed,temperature)
ADLX_IntRangefanSpeedRange,fanTemperatureRange;
ADLX_RESULTres=manualFanTuning->GetFanTuningRanges(&fanSpeedRange,&fanTemperatureRange);
std::cout<<"\tFanspeedrangeis:("<<fanSpeedRange.minValue
<<","<<fanSpeedRange.maxValue<<")"<<std::endl;
std::cout<<"\tFantemperaturerangeis:("<<fanTemperatureRange.minValue
<<","<<fanTemperatureRange.maxValue<<")"<<std::endl;

//Displaycurrentfantuningstates
IADLXManualFanTuningStateListPtrstates;
IADLXManualFanTuningStatePtroneState;
res=manualFanTuning->GetFanTuningStates(&states);
for(adlx_uintcrt=states->Begin();crt!=states->End();++crt)
{
res=states->At(crt,&oneState);
adlx_intspeed=0,temperature=0;
oneState->GetFanSpeed(&speed);
oneState->GetTemperature(&temperature);
std::cout<<"\tThecurrent"<<crt<<"state:speedis"<<speed<<"temperatureis"<<temperature<<std::endl;
}

//Setemptyfantuningstates
res=manualFanTuning->GetEmptyFanTuningStates(&states);
for(adlx_uintcrt=states->Begin();crt!=states->End();++crt)
{
res=states->At(crt,&oneState);
adlx_intspeed=0,temperature=0;
intfanSpeedStep=(fanSpeedRange.maxValue-fanSpeedRange.minValue)/states->Size();
intfanTemperatureStep=(fanTemperatureRange.maxValue-fanTemperatureRange.minValue)/states->Size();
oneState->SetFanSpeed(fanSpeedRange.minValue+fanSpeedStep*crt);
oneState->GetFanSpeed(&speed);
oneState->SetTemperature(fanTemperatureRange.minValue+fanTemperatureStep*crt);
oneState->GetTemperature(&temperature);
std::cout<<"\tSetempty"<<crt<<"state:speedis"<<speed<<"temperatureis"<<temperature<<std::endl;
}

//Setemptyfantuningstatestocurrentfantuningstates
adlx_interrorIndex;
res=manualFanTuning->IsValidFanTuningStates(states,&errorIndex);
std::cout<<"\tIsValidGPUTuningStates,errorIndexis:"<<errorIndex<<std::endl;
if(ADLX_SUCCEEDED(res))
{
manualFanTuning->SetFanTuningStates(states);
}
res=manualFanTuning->GetFanTuningStates(&states);
std::cout<<"\tAftersetting:"<<std::endl;
for(adlx_uintcrt=states->Begin();crt!=states->End();++crt)
{
res=states->At(crt,&oneState);
adlx_intspeed=0,temperature=0;
oneState->GetFanSpeed(&speed);
oneState->GetTemperature(&temperature);
std::cout<<"\tThecurrent"<<crt<<"state:speedis"<<speed<<"temperatureis"<<temperature<<std::endl;
}
}

//DisplayandsetZeroRPMsettings
voidShowGetAndSetZeroRPM(IADLXManualFanTuningPtrmanualFanTuning)
{
adlx_boolsupported=false;
ADLX_RESULTres=manualFanTuning->IsSupportedZeroRPM(&supported);
std::cout<<"\tIsZeroRPMsupported:"<<supported<<std::endl;
if(ADLX_FAILED(res)||!supported)
return;

adlx_boolisZeroRPMStateSet=false;
manualFanTuning->SetZeroRPMState(true);
std::cout<<"\tSetZeroRPMstate"<<std::endl;
manualFanTuning->GetZeroRPMState(&isZeroRPMStateSet);
std::cout<<"\tIsZeroRPMstateset:"<<isZeroRPMStateSet<<std::endl;
manualFanTuning->SetZeroRPMState(false);
std::cout<<"\tResetZeroRPMstate"<<std::endl;
manualFanTuning->GetZeroRPMState(&isZeroRPMStateSet);
std::cout<<"\tIsZeroRPMstateset:"<<isZeroRPMStateSet<<std::endl;
}

//DisplayandsetMinAcousticsettings
voidShowGetAndSetMinAcoustic(IADLXManualFanTuningPtrmanualFanTuning)
{
adlx_boolsupported=false;
ADLX_RESULTres=manualFanTuning->IsSupportedMinAcousticLimit(&supported);
std::cout<<"\tIsMinAcousticLimitsupported:"<<supported<<std::endl;
if(ADLX_FAILED(res)||!supported)
return;

ADLX_IntRangetuningRange;
manualFanTuning->GetMinAcousticLimitRange(&tuningRange);
std::cout<<"\tDisplayMinAcousticlimitrange:("<<tuningRange.minValue
<<","<<tuningRange.maxValue<<")"<<std::endl;

adlx_intminAcousticLimit;
manualFanTuning->GetMinAcousticLimit(&minAcousticLimit);
std::cout<<"\tDisplaycurrentminacousticlimit:"<<minAcousticLimit<<std::endl;
manualFanTuning->SetMinAcousticLimit(tuningRange.minValue+(tuningRange.maxValue-tuningRange.minValue)/2);
manualFanTuning->GetMinAcousticLimit(&minAcousticLimit);
std::cout<<"\tSetcurrentminacousticlimitto:"<<minAcousticLimit<<std::endl;
}

//DisplayandsetMinFanSpeedsettings
voidShowGetAndSetMinFanSpeed(IADLXManualFanTuningPtrmanualFanTuning)
{
adlx_boolsupported=false;
ADLX_RESULTres=manualFanTuning->IsSupportedMinFanSpeed(&supported);

std::cout<<"\tIsMinFanSpeedsupported:"<<supported<<std::endl;
if(ADLX_FAILED(res)||!supported)
return;

ADLX_IntRangetuningRange;
manualFanTuning->GetMinFanSpeedRange(&tuningRange);
std::cout<<"\tDisplayMinFanSpeedrange:("<<tuningRange.minValue
<<","<<tuningRange.maxValue<<")"<<std::endl;

adlx_intminFanSpeed=0;
manualFanTuning->GetMinFanSpeed(&minFanSpeed);
std::cout<<"\tDisplaycurrentMinFanSpeed:"<<minFanSpeed<<std::endl;

manualFanTuning->SetMinAcousticLimit(tuningRange.minValue+(tuningRange.maxValue-tuningRange.minValue)/2);
manualFanTuning->GetMinFanSpeed(&minFanSpeed);
std::cout<<"\tSetcurrentMinFanSpeedto:"<<minFanSpeed<<std::endl;
}

