#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IGPUManualGFXTuning.h"
#include"SDK/Include/IGPUTuning.h"
#include<iostream>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Mainmenu
voidMainMenu(IADLXManualGraphicsTuning1PtrmanualGFXTuning1,IADLXManualGraphicsTuning2PtrmanualGFXTuning2);

//Menuactioncontrol
voidMenuControl(IADLXManualGraphicsTuning1PtrmanualGFXTuning1,IADLXManualGraphicsTuning2PtrmanualGFXTuning2);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

/*****Thefirstispre-Navi,thesecondispost-Navi*****/
//DisplayGPUfrequencyandvoltagerange
voidShowFrequencyAndVoltageRange(IADLXManualGraphicsTuning1PtrmanualGFXTuning1);
voidShowFrequencyAndVoltageRange(IADLXManualGraphicsTuning2PtrmanualGFXTuning2);
//DisplaycurrentGPUtuningstates
voidGetCurrentStates(IADLXManualGraphicsTuning1PtrmanualGFXTuning1);
voidGetCurrentStates(IADLXManualGraphicsTuning2PtrmanualGFXTuning2);
//SetGPUstates
voidSetGPUStates(IADLXManualGraphicsTuning1PtrmanualGFXTuning1);
voidSetGPUStates(IADLXManualGraphicsTuning2PtrmanualGFXTuning2);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXGPUTuningServicesPtrgpuTuningService;
res=g_ADLXHelp.GetSystemServices()->GetGPUTuningServices(&gpuTuningService);
if(ADLX_FAILED(res))
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUtuningservicesfailed",0);
}
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_FAILED(res))
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUlistfailed",0);
}
IADLXGPUPtroneGPU;
res=gpus->At(0,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUfailed",0);
}
adlx_boolsupported=false;
res=gpuTuningService->IsSupportedManualGFXTuning(oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tThisGPUdoesn'tsupportedmanualgraphicstuning",0);
}
IADLXInterfacePtrmanualGFXTuningIfc;
res=gpuTuningService->GetManualGFXTuning(oneGPU,&manualGFXTuningIfc);
if(ADLX_FAILED(res)||manualGFXTuningIfc==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetmanualgraphicstuninginterfacefailed",0);
}
//Pre-NaviASIC
IADLXManualGraphicsTuning1PtrmanualGFXTuning1(manualGFXTuningIfc);
//Post-NaviASIC
IADLXManualGraphicsTuning2PtrmanualGFXTuning2(manualGFXTuningIfc);
if(manualGFXTuning1==nullptr&&manualGFXTuning2==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetmanualgraphicstuningfailed",0);
}
//Displaymainmenuoptions
MainMenu(manualGFXTuning1,manualGFXTuning2);

//Getandexecutethechoice
MenuControl(manualGFXTuning1,manualGFXTuning2);
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu(IADLXManualGraphicsTuning1PtrmanualGFXTuning1,IADLXManualGraphicsTuning2PtrmanualGFXTuning2)
{

if(manualGFXTuning1)
std::cout<<"\tThecurrentGPUispre-Navi.Choosefromthefollowingoptions:"<<std::endl;
elseif(manualGFXTuning2)
std::cout<<"\tThecurrentGPUispost-Navi.Choosefromthefollowingoptions:"<<std::endl;
std::cout<<"\t->Press1todisplayGPUfrequencyandvoltage"<<std::endl;
std::cout<<"\t->Press2todisplaycurrentGPUstates"<<std::endl;
std::cout<<"\t->Press3tosetGPUstates"<<std::endl;
std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

voidMenuControl(IADLXManualGraphicsTuning1PtrmanualGFXTuning1,IADLXManualGraphicsTuning2PtrmanualGFXTuning2)
{
charnum=0;
while((num=getchar())!='q'&&num!='Q')
{
//Pre-NaviMenuactioncontrol
if(manualGFXTuning1)
{
switch(num)
{
//DisplayGPUfrequencyandvoltagerange
case'1':
ShowFrequencyAndVoltageRange(manualGFXTuning1);
break;

//DisplaycurrentGPUtuningstates
case'2':
GetCurrentStates(manualGFXTuning1);
break;

//SetGPUstates
case'3':
SetGPUStates(manualGFXTuning1);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu(manualGFXTuning1,manualGFXTuning2);
break;
default:
break;
}
}
//Post-NaviMenuactioncontrol
else
{
switch(num)
{
//DisplayGPUfrequencyandvoltagerange
case'1':
ShowFrequencyAndVoltageRange(manualGFXTuning2);
break;

//DisplaycurrentGPUtuningstates
case'2':
GetCurrentStates(manualGFXTuning2);
break;

//SetGPUstates
case'3':
SetGPUStates(manualGFXTuning2);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu(manualGFXTuning1,manualGFXTuning2);
break;
default:
break;
}
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//DisplayGPUfrequencyandvoltagerange
voidShowFrequencyAndVoltageRange(IADLXManualGraphicsTuning1PtrmanualGFXTuning1)
{
ADLX_IntRangefreqRange,voltRange;
ADLX_RESULTres=manualGFXTuning1->GetGPUTuningRanges(&freqRange,&voltRange);
std::cout<<"\tFrequencyrange:("<<freqRange.minValue
<<","<<freqRange.maxValue<<")"<<std::endl;
std::cout<<"\tVoltagerange:("<<voltRange.minValue
<<","<<voltRange.maxValue<<")"<<std::endl;
}

//DisplaycurrentGPUtuningstates
voidGetCurrentStates(IADLXManualGraphicsTuning1PtrmanualGFXTuning1)
{
IADLXManualTuningStateListPtrstates;
IADLXManualTuningStatePtroneState;
manualGFXTuning1->GetGPUTuningStates(&states);
for(adlx_uintcrt=states->Begin();crt!=states->End();++crt)
{
states->At(crt,&oneState);
adlx_intfreq=0,volt=0;
oneState->GetFrequency(&freq);
oneState->GetVoltage(&volt);
std::cout<<"\tThecurrentstate"<<crt<<":frequencyis"<<freq<<",voltageis"<<volt<<std::endl;
}
}

//SetGPUstates
voidSetGPUStates(IADLXManualGraphicsTuning1PtrmanualGFXTuning1)
{
IADLXManualTuningStateListPtrstates;
IADLXManualTuningStatePtroneState;
manualGFXTuning1->GetEmptyGPUTuningStates(&states);
ADLX_IntRangefreqRange,voltRange;
ADLX_RESULTres=manualGFXTuning1->GetGPUTuningRanges(&freqRange,&voltRange);
for(adlx_uintcrt=states->Begin();crt!=states->End();++crt)
{
states->At(crt,&oneState);
adlx_intfreq=0,volt=0;
intfreqStep=(freqRange.maxValue-freqRange.minValue)/(states->Size());
intvoltStep=(voltRange.maxValue-voltRange.minValue)/(states->Size());
oneState->SetFrequency(freqRange.minValue+freqStep*crt);
oneState->GetFrequency(&freq);
oneState->SetVoltage(voltRange.minValue+voltStep*crt);
oneState->GetVoltage(&volt);
std::cout<<"\tSetemptystate"<<crt<<":frequencyis"<<freq<<",voltageis"<<volt<<std::endl;
}
adlx_interrorIndex;
res=manualGFXTuning1->IsValidGPUTuningStates(states,&errorIndex);
if(ADLX_SUCCEEDED(res))
{
manualGFXTuning1->SetGPUTuningStates(states);
}
else
std::cout<<"\tIsValidGPUTuningStates,errorIndexis:"<<errorIndex<<std::endl;

res=manualGFXTuning1->GetGPUTuningStates(&states);
std::cout<<"\tAftersetting:"<<std::endl;
for(adlx_uintcrt=states->Begin();crt!=states->End();++crt)
{
res=states->At(crt,&oneState);
adlx_intfreq=0,volt=0;
oneState->GetFrequency(&freq);
oneState->GetVoltage(&volt);
std::cout<<"\tThecurrentstate"<<crt<<":frequencyis"<<freq<<",voltageis"<<volt<<std::endl;
}
}

//DisplayGPUfrequencyandvoltagerange
voidShowFrequencyAndVoltageRange(IADLXManualGraphicsTuning2PtrmanualGFXTuning2)
{
ADLX_IntRangefreqRange,voltRange;
manualGFXTuning2->GetGPUMinFrequencyRange(&freqRange);
std::cout<<"\tGPUminfrequencyrange:("<<freqRange.minValue
<<","<<freqRange.maxValue<<")"<<std::endl;
manualGFXTuning2->GetGPUMaxFrequencyRange(&freqRange);
std::cout<<"\tGPUmaxfrequencyrange:("<<freqRange.minValue
<<","<<freqRange.maxValue<<")"<<std::endl;
ADLX_RESULTres=manualGFXTuning2->GetGPUVoltageRange(&voltRange);
std::cout<<"\tVoltagerange:("<<voltRange.minValue
<<","<<voltRange.maxValue<<")"<<std::endl;
}

//DisplaycurrentGPUtuningstates
voidGetCurrentStates(IADLXManualGraphicsTuning2PtrmanualGFXTuning2)
{
adlx_intminFreq=0,maxFreq=0,volt=0;
manualGFXTuning2->GetGPUMinFrequency(&minFreq);
manualGFXTuning2->GetGPUMaxFrequency(&maxFreq);
manualGFXTuning2->GetGPUVoltage(&volt);
std::cout<<"\tCurrentGPUminfrequency:"<<minFreq<<std::endl;
std::cout<<"\tCurrentGPUmaxfrequency:"<<maxFreq<<std::endl;
std::cout<<"\tCurrentGPUclockvoltage:"<<volt<<std::endl;
}

//SetGPUstates
voidSetGPUStates(IADLXManualGraphicsTuning2PtrmanualGFXTuning2)
{
ADLX_IntRangefreqRange,voltRange;
manualGFXTuning2->GetGPUMinFrequencyRange(&freqRange);
manualGFXTuning2->GetGPUVoltageRange(&voltRange);
manualGFXTuning2->SetGPUMinFrequency(freqRange.minValue);
manualGFXTuning2->SetGPUMaxFrequency(freqRange.maxValue);
manualGFXTuning2->SetGPUVoltage(voltRange.minValue+(voltRange.maxValue-voltRange.minValue)/2);
std::cout<<"\tAftersetting:"<<std::endl;
adlx_intminFreq=0,maxFreq=0,volt=0;
manualGFXTuning2->GetGPUMinFrequency(&minFreq);
manualGFXTuning2->GetGPUMaxFrequency(&maxFreq);
manualGFXTuning2->GetGPUVoltage(&volt);
std::cout<<"\tCurrentGPUminfrequency:"<<minFreq<<std::endl;
std::cout<<"\tCurrentGPUmaxfrequency:"<<maxFreq<<std::endl;
std::cout<<"\tCurrentGPUclockvoltage:"<<volt<<std::endl;
}
