#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IGPUManualPowerTuning.h"
#include"SDK/Include/IGPUTuning.h"
#include<iostream>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXManualPowerTuningPtrmanualPowerTuning);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//Displaypowerlimitrange
voidShowGetPowerLimitRange(IADLXManualPowerTuningPtrmanualPowerTuning);

//Displaycurrentpowerlimit
voidShowGetPowerLimit(IADLXManualPowerTuningPtrmanualPowerTuning);

//Setpowerlimit
voidShowSetPowerLimit(IADLXManualPowerTuningPtrmanualPowerTuning);

//ShowtocheckTDClimitissupported
voidShowTDCLimitSupported(IADLXManualPowerTuningPtrmanualPowerTuning);

//ShowhowtogetTDClimitrange.
voidShowGetTDCLimitRange(IADLXManualPowerTuningPtrmanualPowerTuning);

//ShowhowtogetcurrentTDClimit.
voidShowGetTDCLimit(IADLXManualPowerTuningPtrmanualPowerTuning);

//ShowhowtosetTDClimit.
voidShowSetTDCLimit(IADLXManualPowerTuningPtrmanualPowerTuning);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXGPUTuningServicesPtrgpuTuningService;
res=g_ADLXHelp.GetSystemServices()->GetGPUTuningServices(&gpuTuningService);
if(ADLX_FAILED(res))
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUtuningservicesfailed",0);
}
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_FAILED(res))
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUlistfailed",0);
}
IADLXGPUPtroneGPU;
res=gpus->At(0,&oneGPU);
if(ADLX_FAILED(res)||oneGPU==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetGPUfailed",0);
}
adlx_boolsupported=false;
res=gpuTuningService->IsSupportedManualPowerTuning(oneGPU,&supported);
if(ADLX_FAILED(res)||supported==false)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tThisGPUdoesnotsupportmanualpowertuning",0);
}
IADLXInterfacePtrmanualPowerTuningIfc;
res=gpuTuningService->GetManualPowerTuning(oneGPU,&manualPowerTuningIfc);
if(ADLX_FAILED(res)||manualPowerTuningIfc==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetmanualpowertuninginterfacefailed",0);
}
IADLXManualPowerTuningPtrmanualPowerTuning(manualPowerTuningIfc);
if(manualPowerTuning==nullptr)
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("\tGetmanualpowertuningfailed",0);
}
//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(manualPowerTuning);
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1toshowhowtogetpowerlimitrange"<<std::endl;
std::cout<<"\t->Press2toshowhowtogetcurrentpowerlimit"<<std::endl;
std::cout<<"\t->Press3toshowhowtosetpowerlimit"<<std::endl;
std::cout<<"\t->Press4toshowTDCLimitissupported"<<std::endl;
std::cout<<"\t->Press5toshowhowtogetTDClimitrange"<<std::endl;
std::cout<<"\t->Press6toshowhowtogetcurrentTDClimit"<<std::endl;
std::cout<<"\t->Press7toshowhowtosetTDClimit"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(IADLXManualPowerTuningPtrmanualPowerTuning)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displaypowerlimitrange
case'1':
ShowGetPowerLimitRange(manualPowerTuning);
break;

//Displaycurrentpowerlimit
case'2':
ShowGetPowerLimit(manualPowerTuning);
break;

//Setpowerlimit
case'3':
ShowSetPowerLimit(manualPowerTuning);
break;
//ShowtocheckifTDCLimitissupported
case'4':
ShowTDCLimitSupported(manualPowerTuning);
break;
//ShowhowtogetTDClimitrange.
case'5':
ShowGetTDCLimitRange(manualPowerTuning);
break;

//ShowhowtogetcurrentTDClimit.
case'6':
ShowGetTDCLimit(manualPowerTuning);
break;

//ShowhowtosetTDClimit.
case'7':
ShowSetTDCLimit(manualPowerTuning);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//Displaypowerlimitrange
voidShowGetPowerLimitRange(IADLXManualPowerTuningPtrmanualPowerTuning)
{
ADLX_IntRangepowerRange;
ADLX_RESULTres=manualPowerTuning->GetPowerLimitRange(&powerRange);
std::cout<<"\tPowerlimitrange:("<<powerRange.minValue
<<","<<powerRange.maxValue<<")"<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}

//Displaycurrentpowerlimit
voidShowGetPowerLimit(IADLXManualPowerTuningPtrmanualPowerTuning)
{
adlx_intpowerLimit;
ADLX_RESULTres=manualPowerTuning->GetPowerLimit(&powerLimit);
std::cout<<"\tCurrentpowerlimit:"<<powerLimit<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}

//Setpowerlimit
voidShowSetPowerLimit(IADLXManualPowerTuningPtrmanualPowerTuning)
{
ADLX_IntRangepowerRange;
ADLX_RESULTres=manualPowerTuning->GetPowerLimitRange(&powerRange);
res=manualPowerTuning->SetPowerLimit(powerRange.step+powerRange.minValue+(powerRange.maxValue-powerRange.minValue)/2);
std::cout<<"\tSetpowerlimit"<<(ADLX_SUCCEEDED(res)?"succeeded":"failed")<<std::endl;
adlx_intpowerLimit;
res=manualPowerTuning->GetPowerLimit(&powerLimit);
std::cout<<"\tSetcurrentpowerlimitto:"<<powerLimit<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}

//ShowTDCLimitissupported.
voidShowTDCLimitSupported(IADLXManualPowerTuningPtrmanualPowerTuning)
{
adlx_boolsupportedTDC;
ADLX_RESULTres=manualPowerTuning->IsSupportedTDCLimit(&supportedTDC);
std::cout<<"\tTDClimitissupported:"<<supportedTDC<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}

//Showhowtogettdclimitrange.
voidShowGetTDCLimitRange(IADLXManualPowerTuningPtrmanualPowerTuning)
{
ADLX_IntRangetdcRange;
ADLX_RESULTres=manualPowerTuning->GetTDCLimitRange(&tdcRange);
std::cout<<"\tGetTDClimitrangeis:("<<tdcRange.minValue
<<","<<tdcRange.maxValue<<"),returncode(0isSuccess)is:"<<res<<std::endl;
}

//ShowhowtogetcurrentTDClimit.
voidShowGetTDCLimit(IADLXManualPowerTuningPtrmanualPowerTuning)
{
adlx_inttdcLimit;
ADLX_RESULTres=manualPowerTuning->GetTDCLimit(&tdcLimit);
std::cout<<"\tThecurrentTDClimitis:"<<tdcLimit<<",returncode(0isSuccess)is:"<<res<<std::endl;
}

//ShowhowtosetTDClimit.
voidShowSetTDCLimit(IADLXManualPowerTuningPtrmanualPowerTuning)
{
ADLX_IntRangetdcRange;
ADLX_RESULTres=manualPowerTuning->GetTDCLimitRange(&tdcRange);

res=manualPowerTuning->SetTDCLimit(tdcRange.step+tdcRange.minValue+(tdcRange.maxValue-tdcRange.minValue)/2);
adlx_inttdcLimit;
res=manualPowerTuning->GetTDCLimit(&tdcLimit);
std::cout<<"\tSetcurrentTDClimitto:"<<tdcLimit<<",returncode(0isSuccess)is:"<<res<<std::endl;
}
