#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IGPUManualVRAMTuning.h"
#include"SDK/Include/IGPUTuning.h"
#include<iostream>
#include<map>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//DisplayMemoryTimingsupport
voidShowSupport(IADLXInterfacePtr&vramTuningIfc);

//GetVRAMstate
voidGetState(IADLXInterfacePtr&vramTuningIfc);

//SetMemoryTimingLevel
voidSetTimingLevel(IADLXInterfacePtr&vramTuningIfc);

//SetVRAMstate
voidSetState(IADLXInterfacePtr&vramTuningIfc);

//Mainmenu
voidMainMenu(IADLXInterfacePtr&vramTuningIfc);

//Menuactioncontrol
voidMenuControl(IADLXInterfacePtrvramTuningIfc);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
//GetGPUs
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_SUCCEEDED(res)&&!gpus->Empty())
{
//GetGPUtuningservice
IADLXGPUTuningServicesPtrgpuTuningService;
res=g_ADLXHelp.GetSystemServices()->GetGPUTuningServices(&gpuTuningService);
if(ADLX_SUCCEEDED(res))
{
//GetGPUinterface
IADLXGPUPtrgpu;
adlx_uintindex=0;
res=gpus->At(index,&gpu);
if(ADLX_SUCCEEDED(res))
{
//GetmanualVRAMtuningsupport
adlx_boolsupported;
res=gpuTuningService->IsSupportedManualVRAMTuning(gpu,&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tGPUmanualVRAMtuningsupportstatus:"<<supported<<std::endl;
if(supported)
{
//GetmanualVRAMtuninginterface
IADLXInterfacePtrvramTuningIfc;
res=gpuTuningService->GetManualVRAMTuning(gpu,&vramTuningIfc);
if(ADLX_SUCCEEDED(res))
{
MainMenu(vramTuningIfc);
MenuControl(vramTuningIfc);
}
}
else
{
returnWaitAndExit("\nThisGPUDoesn'tsupportManualVRAMTuning",0);
}
}
}
else
std::cout<<"FailedtogetGPUtuningservices"<<std::endl;
}
else
std::cout<<"FailedtogetGPU"<<std::endl;
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

staticstd::map<ADLX_MEMORYTIMING_DESCRIPTION,constchar*>descMap=
{
{MEMORYTIMING_DEFAULT,"Default"},
{MEMORYTIMING_FAST_TIMING,"FastTiming"},
{MEMORYTIMING_FAST_TIMING_LEVEL_2,"FastTimingLevel2"},
{MEMORYTIMING_AUTOMATIC,"Automatic"},
{MEMORYTIMING_MEMORYTIMING_LEVEL_1,"MemoryTimingLevel1"},
{MEMORYTIMING_MEMORYTIMING_LEVEL_2,"MemoryTimingLevel2"}};

voidShowSupport(IADLXInterfacePtr&vramTuningIfc)
{
adlx_boolsupported=false;
IADLXManualVRAMTuning2PtrvramTuning2(vramTuningIfc);
IADLXManualVRAMTuning1PtrvramTuning1(vramTuningIfc);
ADLX_RESULTres=ADLX_FAIL;
if(vramTuning2)
{
res=vramTuning2->IsSupportedMemoryTiming(&supported);
}
elseif(vramTuning1)
{
res=vramTuning1->IsSupportedMemoryTiming(&supported);
}
std::cout<<"\tIsSupported:"<<supported<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}

voidGetState(IADLXInterfacePtr&vramTuningIfc)
{
IADLXManualVRAMTuning2PtrvramTuning2(vramTuningIfc);
IADLXManualVRAMTuning1PtrvramTuning1(vramTuningIfc);
ADLX_MEMORYTIMING_DESCRIPTIONdesc;
IADLXMemoryTimingDescriptionListPtrdescList;
if(vramTuning2)
{
vramTuning2->GetSupportedMemoryTimingDescriptionList(&descList);
vramTuning2->GetMemoryTimingDescription(&desc);

adlx_intfreq;
ADLX_IntRangerang;
ADLX_RESULTres=vramTuning2->GetMaxVRAMFrequency(&freq);
std::cout<<"\tMaxVRAMfrequency:"<<freq<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
res=vramTuning2->GetMaxVRAMFrequencyRange(&rang);
std::cout<<"\tMaxVRAMfrequencyrange:["<<rang.minValue<<","<<rang.maxValue<<"],step:"<<rang.step
<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}
elseif(vramTuning1)
{
vramTuning1->GetSupportedMemoryTimingDescriptionList(&descList);
vramTuning1->GetMemoryTimingDescription(&desc);

IADLXManualTuningStateListPtrstates;
vramTuning1->GetVRAMTuningStates(&states);
adlx_uints=states->Begin();
std::cout<<"\tStateis:"<<std::endl;
for(;s!=states->End();s++)
{
IADLXManualTuningStatePtrstate;
adlx_intfrequency;
adlx_intvoltage;
states->At(s,&state);
state->GetFrequency(&frequency);
state->GetVoltage(&voltage);
std::cout<<"\t\tfrequency:"<<frequency<<"voltage:"<<voltage<<std::endl;
}

ADLX_IntRangefrequencyRange;
ADLX_IntRangevoltageRange;
ADLX_RESULTres=vramTuning1->GetVRAMTuningRanges(&frequencyRange,&voltageRange);
std::cout<<"\tFrequencyrange:["<<frequencyRange.minValue<<","<<frequencyRange.maxValue<<"],step:"<<frequencyRange.step
<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
std::cout<<"\tVoltagerange:["<<voltageRange.minValue<<","<<voltageRange.maxValue<<"],step:"<<voltageRange.step
<<",returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}
if(descList)
{
std::cout<<"\tCurrenttimingdesc:"<<descMap[desc]<<std::endl;
std::cout<<"\tSupportedMemoryTimingdesclist:"<<std::endl;
for(adlx_uints=0;s!=descList->End();s++)
{
IADLXMemoryTimingDescriptionPtritem;
descList->At(s,&item);
item->GetDescription(&desc);
std::cout<<"\t"<<descMap[desc]<<std::endl;
}
}
std::cout<<std::endl;
}

voidSetTimingLevel(IADLXInterfacePtr&vramTuningIfc)
{
ADLX_RESULTres;
IADLXManualVRAMTuning2PtrvramTuning2(vramTuningIfc);
IADLXManualVRAMTuning1PtrvramTuning1(vramTuningIfc);
ADLX_MEMORYTIMING_DESCRIPTIONdesc,currentDesc;
IADLXMemoryTimingDescriptionListPtrdescList;
boolsupported=false;
if(vramTuning2)
{
//IfMemoryTimingissupported,thenonlywillgettheMemoryTimingDescriptionList
res=vramTuning2->IsSupportedMemoryTiming(&supported);
if(supported)
{
vramTuning2->GetSupportedMemoryTimingDescriptionList(&descList);
if(descList)
{
vramTuning2->GetMemoryTimingDescription(&currentDesc);
for(adlx_uints=0;s!=descList->End();s++)
{
IADLXMemoryTimingDescriptionPtritem;
descList->At(s,&item);
item->GetDescription(&desc);
if(currentDesc!=desc)
{
res=vramTuning2->SetMemoryTimingDescription(desc);
std::cout<<"\tSetMemoryTimingdescriptionto"<<descMap[desc]<<":returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
break;
}
}
}
else
{
std::cout<<"\tFailedtogettheSupportedMemoryTimingDescriptionList."<<std::endl;
}
}
else
{
std::cout<<"\tMemoryTimingLevelnotSupported,socannotsetMemoryTimingDescriptionList."<<std::endl;
}
}
elseif(vramTuning1)
{
//IfMemoryTimingissupported,thenonlywillgettheMemoryTimingDescriptionList
res=vramTuning1->IsSupportedMemoryTiming(&supported);
if(supported)
{
vramTuning1->GetSupportedMemoryTimingDescriptionList(&descList);
if(descList)
{
vramTuning1->GetMemoryTimingDescription(&currentDesc);
for(adlx_uints=0;s!=descList->End();s++)
{
IADLXMemoryTimingDescriptionPtritem;
descList->At(s,&item);
item->GetDescription(&desc);
if(currentDesc!=desc)
{
res=vramTuning1->SetMemoryTimingDescription(desc);
std::cout<<"\tSetMemoryTimingdescriptionto"<<descMap[desc]<<":returncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
break;
}
}
}
else
{
std::cout<<"\tFailedtogettheSupportedMemoryTimingDescriptionList."<<std::endl;
}
}
else
{
std::cout<<"\tMemoryTimingLevelnotSupported,socannotsetMemoryTimingDescriptionList."<<std::endl;
}
}
}

voidSetState(IADLXInterfacePtr&vramTuningIfc)
{
ADLX_RESULTres;
IADLXManualVRAMTuning2PtrvramTuning2(vramTuningIfc);
IADLXManualVRAMTuning1PtrvramTuning1(vramTuningIfc);
boolsupported=false;
if(vramTuning2)
{
//IfMemoryTimingissupported,thenonlywillsettheMemoryTimingState
res=vramTuning2->IsSupportedMemoryTiming(&supported);
if(supported)
{
adlx_intfreq;
ADLX_IntRangerang;
vramTuning2->GetMaxVRAMFrequency(&freq);
vramTuning2->GetMaxVRAMFrequencyRange(&rang);
if(freq!=rang.minValue)
{
res=vramTuning2->SetMaxVRAMFrequency(rang.minValue);
std::cout<<"\tUseminFrequencytoset,returncode(0isSuccess)is:"<<res<<std::endl;
}
else
{
res=vramTuning2->SetMaxVRAMFrequency(rang.minValue+rang.step*2);
std::cout<<"\tUsemaxFrequencytoset,returncode(0isSuccess)is:"<<res<<std::endl;
}
}
else
{
std::cout<<"\tTuningnotSupported,socannotchangetheState."<<std::endl;
}
}
elseif(vramTuning1)
{
//IfMemoryTimingissupported,thenonlywillsettheMemoryTimingState
res=vramTuning1->IsSupportedMemoryTiming(&supported);
if(supported)
{
ADLX_IntRangefrequencyRange;
ADLX_IntRangevoltageRange;
vramTuning1->GetVRAMTuningRanges(&frequencyRange,&voltageRange);
IADLXManualTuningStateListPtrstates;
vramTuning1->GetVRAMTuningStates(&states);
adlx_uints=states->Size()-1;
for(;s!=states->End();s++)
{
IADLXManualTuningStatePtrstate;
adlx_intfrequency;
adlx_intvoltage;
states->At(s,&state);
state->GetFrequency(&frequency);
state->GetVoltage(&voltage);
//Onlychangethelast
res=state->SetFrequency(frequencyRange.maxValue-frequencyRange.step*10);
res=state->SetVoltage(voltageRange.maxValue-voltageRange.step*10);
break;
}
res=vramTuning1->SetVRAMTuningStates(states);
std::cout<<"\tChangeVRAMtuningstates,returncode(0isSuccess)is:"<<res<<std::endl;
}
else
{
std::cout<<"\tTuningnotSupported,socannotchangetheState."<<std::endl;
}
}
}

voidMainMenu(IADLXInterfacePtr&vramTuningIfc)
{
//AcquiretoManualVRAMTuning1
IADLXManualVRAMTuning1PtrvramTuning1(vramTuningIfc);
//AcquiretoManualVRAMTuning2
IADLXManualVRAMTuning2PtrvramTuning2(vramTuningIfc);

std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1todisplayMemoryTimingsupport"<<std::endl;
if(vramTuning2)
std::cout<<"\t->Press2togetMemoryTiminglevelandfrequencyrange"<<std::endl;
elseif(vramTuning1)
std::cout<<"\t->Press2togetMemoryTiminglevel,VRAMfrequency,andthevoltageglobalrangeforallstates"<<std::endl;

std::cout<<"\t->Press3tochangecurrentMemoryTimingLevel"<<std::endl;

if(vramTuning2)
std::cout<<"\t->Press4tochangecurrentVRAMmaxfrequencyvalue"<<std::endl;
elseif(vramTuning1)
std::cout<<"\t->Press4tochangeVRAMstates"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

voidMenuControl(IADLXInterfacePtrvramTuningIfc)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//GetMemoryTimingsupport
case'1':
ShowSupport(vramTuningIfc);
break;

//GetVRAMstate
case'2':
GetState(vramTuningIfc);
break;

//Settiminglevel
case'3':
SetTimingLevel(vramTuningIfc);
break;

//SetVRAMstate
case'4':
SetState(vramTuningIfc);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu(vramTuningIfc);
break;
default:
break;
}
}
}

intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}
