//Showcurrentallmetrics
voidShowCurrentAllMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU)
{
//Getsystemmetricssupport
IADLXSystemMetricsSupportPtrsystemMetricsSupport;
ADLX_RESULTres2=perfMonitoringServices->GetSupportedSystemMetrics(&systemMetricsSupport);
//GetGPUmetricssupport
IADLXGPUMetricsSupportPtrgpuMetricsSupport;
res2=perfMonitoringServices->GetSupportedGPUMetrics(oneGPU,&gpuMetricsSupport);

//Loop10timetoaccumulatedataandshowthecurrentmetricsineachloop
IADLXAllMetricsPtrallMetrics;
IADLXSystemMetricsPtrsystemMetrics;
IADLXGPUMetricsPtrgpuMetrics;
IADLXFPSPtroneFPS;
for(inti=0;i<10;++i)
{
//clearscreenwhenshowcurrentmetrics
system("cls");
//GetcurrentAllmetrics
ADLX_RESULTres1=perfMonitoringServices->GetCurrentAllMetrics(&allMetrics);
if(ADLX_SUCCEEDED(res1))
{
std::cout<<"Thecurrentallmetrics:"<<std::endl;
//Getcurrentsystemmetrics.
res1=allMetrics->GetSystemMetrics(&systemMetrics);
//Showtimestamp,CPUusage,systemRAMandsmartshift
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
std::cout<<std::boolalpha;//displayboolvariableastrueoffalse
GetTimeStamp(systemMetrics);
ShowCPUUsage(systemMetricsSupport,systemMetrics);
ShowSystemRAM(systemMetricsSupport,systemMetrics);
ShowSmartShift(systemMetricsSupport,systemMetrics);
std::cout<<std::noboolalpha;
}
//GetcurrentGPUmetrics
res1=allMetrics->GetGPUMetrics(oneGPU,&gpuMetrics);
//ShowtimestampandGPUmetrics
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
std::cout<<std::boolalpha;//displayboolvariableastrueoffalse
GetTimeStamp(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUMemoryTemperature(gpuMetricsSupport,gpuMetrics);
ShowNPUActivityLevel(gpuMetricsSupport,gpuMetrics);
ShowNPUFrequency(gpuMetricsSupport,gpuMetrics);
std::cout<<std::noboolalpha;
}
//GetcurrentFPSmetrics
res1=allMetrics->GetFPS(&oneFPS);
if(ADLX_SUCCEEDED(res1))
{
adlx_int64timeStamp=0;
res1=oneFPS->TimeStamp(&timeStamp);
if(ADLX_SUCCEEDED(res1))
std::cout<<"Thecurrentmetrictimestampis:"<<timeStamp<<"ms\n";
adlx_intfpsData=0;
res1=oneFPS->FPS(&fpsData);
if(ADLX_SUCCEEDED(res1))
std::cout<<"ThecurrentmetricFPSis:"<<fpsData<<std::endl;
elseif(res1==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportFPS"<<std::endl;
}
}
Sleep(1000);
}

MainMenu();
}

//Showhistoricalallmetrics
voidShowHistoricalAllMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU)
{
//Clearhistoricalperformancemetricsdata
ADLX_RESULTres=perfMonitoringServices->ClearPerformanceMetricsHistory();
if(ADLX_FAILED(res))
{
std::cout<<"Clearhistoricaldatafailed"<<std::endl;
return;
}

//Starttrackingperformancemetrics
res=perfMonitoringServices->StartPerformanceMetricsTracking();
if(ADLX_FAILED(res))
{
std::cout<<"Starttrackingperformancemetricsfailed"<<std::endl;
return;
}

//Waitfor10secondstoaccumulatemetrics
std::cout<<"Waitfor10secondstoaccumulatemetrics..."<<std::endl;
Sleep(10000);

//Grabtheallmetricshistoryfrom10secondsago(10000ms:thefirstparameter)tothepresenttime(0ms:thesecondparameter)
IADLXAllMetricsListPtrallMetricsList;
res=perfMonitoringServices->GetAllMetricsHistory(10000,0,&allMetricsList);
if(ADLX_SUCCEEDED(res))
{
//Getsystemmetricssupport
IADLXSystemMetricsSupportPtrsystemMetricsSupport;
ADLX_RESULTsystemRes=perfMonitoringServices->GetSupportedSystemMetrics(&systemMetricsSupport);

//GetGPUmetricssupport
IADLXGPUMetricsSupportPtrgpuMetricsSupport;
ADLX_RESULTgpuRes=perfMonitoringServices->GetSupportedGPUMetrics(oneGPU,&gpuMetricsSupport);

//Displayeachallmetricsinthelist
IADLXAllMetricsPtrallMetrics;
IADLXSystemMetricsPtrsystemMetrics;
IADLXGPUMetricsPtrgpuMetrics;
IADLXFPSPtroneFPS;
for(inti=allMetricsList->Begin();i!=allMetricsList->End();++i)
{
std::cout<<"**********historicalsystemmetrics"<<i+1<<":**********"<<std::endl;
res=allMetricsList->At(i,&allMetrics);
if(ADLX_SUCCEEDED(res))
{
res=allMetrics->GetSystemMetrics(&systemMetrics);
//Showtimestamp,CPUusage,systemRAM,smartshiftoffirstsystemmetrics
if(ADLX_SUCCEEDED(systemRes)&&ADLX_SUCCEEDED(res))
{
std::cout<<std::boolalpha;//displayboolvariableastrueoffalse
GetTimeStamp(systemMetrics);
ShowCPUUsage(systemMetricsSupport,systemMetrics);
ShowSystemRAM(systemMetricsSupport,systemMetrics);
ShowSmartShift(systemMetricsSupport,systemMetrics);
std::cout<<std::noboolalpha;
}
res=allMetrics->GetGPUMetrics(oneGPU,&gpuMetrics);
//ShowtimestampandGPUmetrics
if(ADLX_SUCCEEDED(gpuRes)&&ADLX_SUCCEEDED(res))
{
std::cout<<std::boolalpha;//displayboolvariableastrueoffalse
GetTimeStamp(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUMemoryTemperature(gpuMetricsSupport,gpuMetrics);
ShowNPUActivityLevel(gpuMetricsSupport,gpuMetrics);
ShowNPUFrequency(gpuMetricsSupport,gpuMetrics);
std::cout<<std::noboolalpha;
}
res=allMetrics->GetFPS(&oneFPS);
if(ADLX_SUCCEEDED(res))
{
adlx_int64timeStamp=0;
res=oneFPS->TimeStamp(&timeStamp);
if(ADLX_SUCCEEDED(res))
std::cout<<"Thetimestampis:"<<timeStamp<<"ms"<<std::endl;
adlx_intfpsData=0;
res=oneFPS->FPS(&fpsData);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheFPSis:"<<fpsData<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportFPS"<<std::endl;
}
std::cout<<std::endl;
}
}
}

//Stoptrackingperformancemetrics
res=perfMonitoringServices->StopPerformanceMetricsTracking();
if(ADLX_FAILED(res))
{
std::cout<<"Stoptrackingperformancemetricsfailed"<<std::endl;
return;
}
}
