#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IPerformanceMonitoring.h"
#include<iostream>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//ASCIIÂ°
staticconstsignedcharg_degree=248;

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//ShowGPUmetricsrange
voidShowGPUMetricsRange(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU);

/*
*Iftheapponlyrequiresthecurrentmetricset,metricstrackingcanbeomittedinfavorofcallingthemoreefficientGetCurrentfunction.
*Iftheappalsorequiresmetricshistory,GetHistoryretrievestherequiredaccumulatedhistorybuffer,thatmayalsoincludethecurrentmetricset.
*/
//ShowcurrentGPUmetrics
voidShowCurrentGPUMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU);
voidShowCurrentGPUMetricsFromHistorical(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU);

//ShowhistoricalGPUmetrics
voidShowHistoricalGPUMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
//GetPerformanceMonitoringservices
IADLXPerformanceMonitoringServicesPtrperfMonitoringService;
res=g_ADLXHelp.GetSystemServices()->GetPerformanceMonitoringServices(&perfMonitoringService);
if(ADLX_SUCCEEDED(res))
{
IADLXGPUListPtrgpus;
//GetGPUlist
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_SUCCEEDED(res))
{
//UsethefirstGPUinthelist
IADLXGPUPtroneGPU;
res=gpus->At(gpus->Begin(),&oneGPU);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(perfMonitoringService,oneGPU);
}
else
std::cout<<"\tGetparticularGPUfailed"<<std::endl;
}
else
std::cout<<"\tGetGPUlistfailed"<<std::endl;
}
else
std::cout<<"\tGetperformancemonitoringservicesfailed"<<std::endl;
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
std::cout<<"\tChooseonefromthefollowingoptions"<<std::endl;

std::cout<<"\t->Press1todisplayGPUmetricsrange"<<std::endl;
std::cout<<"\t->Press2todisplaycurrentGPUmetrics"<<std::endl;
std::cout<<"\t->Press3todisplaycurrentGPUmetricsfromhistoricaldata"<<std::endl;
std::cout<<"\t->Press4todisplayhistoricalGPUmetrics"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaythemainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//ShowGPUmetricsrange
case'1':
ShowGPUMetricsRange(perfMonitoringServices,oneGPU);
break;

//DisplaycurrentGPUmetrics
case'2':
ShowCurrentGPUMetrics(perfMonitoringServices,oneGPU);
break;

//DisplaycurrentGPUmetricsfromhistoricaldata
case'3':
ShowCurrentGPUMetricsFromHistorical(perfMonitoringServices,oneGPU);
break;

//DisplayhistoricalGPUmetrics
case'4':
ShowHistoricalGPUMetrics(perfMonitoringServices,oneGPU);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//DisplayGPUmetricsrange
voidShowGPUMetricsRange(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU)
{
//GetGPUmetricssupport
IADLXGPUMetricsSupportPtrgpuMetricsSupport;
ADLX_RESULTres=perfMonitoringServices->GetSupportedGPUMetrics(oneGPU,&gpuMetricsSupport);
if(ADLX_SUCCEEDED(res))
{
adlx_intminValue=0,maxValue=0;

//GetGPUusagerange
res=gpuMetricsSupport->GetGPUUsageRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUusagerangebetween"<<minValue<<"%and"<<maxValue<<"%"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUusagerange"<<std::endl;

//GetGPUclockspeedrange
res=gpuMetricsSupport->GetGPUClockSpeedRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUclockspeedrangebetween"<<minValue<<"MHzand"<<maxValue<<"MHz"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUclockspeedrange"<<std::endl;

//GetGPUVRAMclockspeedrange
res=gpuMetricsSupport->GetGPUVRAMClockSpeedRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUVRAMclockspeedrangebetween"<<minValue<<"MHzand"<<maxValue<<"MHz"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUVRAMclockspeedrange"<<std::endl;

//GetGPUtemperaturerange
res=gpuMetricsSupport->GetGPUTemperatureRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUtemperaturerangebetween"<<minValue<<g_degree<<"Cand"<<maxValue<<g_degree<<"C"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUtemperaturerange"<<std::endl;

//GetGPUhotspottemperaturerange
res=gpuMetricsSupport->GetGPUHotspotTemperatureRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUhotspottemperaturerangebetween"<<minValue<<g_degree<<"Cand"<<maxValue<<g_degree<<"C"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUhotspottemperaturerange"<<std::endl;

//GetGPUpowerrange
res=gpuMetricsSupport->GetGPUPowerRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUpowerrangebetween"<<minValue<<"Wand"<<maxValue<<"W"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUpowerrange"<<std::endl;

//GetGPUfanspeedrange
res=gpuMetricsSupport->GetGPUFanSpeedRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUfanspeedrangebetween"<<minValue<<"RPMand"<<maxValue<<"RPM"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUfanspeedrange"<<std::endl;

//GetGPUVRAMrange
res=gpuMetricsSupport->GetGPUVRAMRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUVRAMrangebetween"<<minValue<<"MBand"<<maxValue<<"MB"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUVRAMrange"<<std::endl;

//GetGPUvoltagerange
res=gpuMetricsSupport->GetGPUVoltageRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUvoltagerangebetween"<<minValue<<"mVand"<<maxValue<<"mV"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUvoltagerange"<<std::endl;

//GetGPUtotalboardpowerrange
res=gpuMetricsSupport->GetGPUTotalBoardPowerRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUtotalboardpowerrangebetween"<<minValue<<"Wand"<<maxValue<<"W"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUtotalboardpowerrange"<<std::endl;

//GetGPUintaketemperaturerange
res=gpuMetricsSupport->GetGPUIntakeTemperatureRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUintaketemperaturerangebetween"<<minValue<<g_degree<<"Cand"<<maxValue<<g_degree<<"C"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportGPUintaketemperaturerange"<<std::endl;
}
}

//Displaythesystemtimestamp(inms)
voidGetTimeStamp(IADLXGPUMetricsPtrgpuMetrics)
{
adlx_int64timeStamp=0;
ADLX_RESULTres=gpuMetrics->TimeStamp(&timeStamp);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUtimpstampis:"<<timeStamp<<"ms"<<std::endl;
}

//DisplayGPUusage(in%)
voidShowGPUUsage(IADLXGPUMetricsSupportPtrgpuMetricsSupport,IADLXGPUMetricsPtrgpuMetrics)
{
adlx_boolsupported=false;
//DisplayGPUusagesupportstatus
ADLX_RESULTres=gpuMetricsSupport->IsSupportedGPUUsage(&supported);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"GPUusagesupportstatus:"<<supported<<std::endl;
if(supported)
{
adlx_doubleusage=0;
res=gpuMetrics->GPUUsage(&usage);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheGPUusageis:"<<usage<<"%"<<std::endl;
}
}
}

