//DisplaycurrentGPUmetrics
voidShowCurrentGPUMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU)
{
//GetGPUmetricssupport
IADLXGPUMetricsSupportPtrgpuMetricsSupport;
ADLX_RESULTres1=perfMonitoringServices->GetSupportedGPUMetrics(oneGPU,&gpuMetricsSupport);

//Accumulateanddisplaycurrentmetricsforeachof10loops
IADLXGPUMetricsPtrgpuMetrics;
for(inti=0;i<10;++i)
{
//Clearscreen
system("cls");
//GetcurrentGPUmetrics
ADLX_RESULTres2=perfMonitoringServices->GetCurrentGPUMetrics(oneGPU,&gpuMetrics);

//DisplaytimestampandGPUmetrics
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
std::cout<<"ThecurrentGPUmetrics:"<<std::endl;
std::cout<<std::boolalpha;//Displaybooleanvariableastrueorfalse
GetTimeStamp(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUTotalBoardPower(gpuMetricsSupport,gpuMetrics);
ShowGPUIntakeTemperature(gpuMetricsSupport,gpuMetrics);
std::cout<<std::noboolalpha;
}
Sleep(1000);
}

MainMenu();
}

voidShowCurrentGPUMetricsFromHistorical(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU)
{
//Clearhistoricalperformancemetricsdata
ADLX_RESULTres=perfMonitoringServices->ClearPerformanceMetricsHistory();
if(ADLX_FAILED(res))
{
std::cout<<"Clearhistoricaldatafailed"<<std::endl;
return;
}

//Starttrackingperformancemetrics
res=perfMonitoringServices->StartPerformanceMetricsTracking();
if(ADLX_FAILED(res))
{
std::cout<<"Starttrackingperformancemetricsfailed"<<std::endl;
return;
}

//GetGPUmetricssupport
IADLXGPUMetricsSupportPtrgpuMetricsSupport;
ADLX_RESULTmetricsSupportRet=perfMonitoringServices->GetSupportedGPUMetrics(oneGPU,&gpuMetricsSupport);

//Accumulateanddisplaycurrentmetricsforeachof10loops
adlx_intstartMs=0;
adlx_intstopMs=0;
for(intit=0;it<10;++it)
{
//Clearscreen
system("cls");

IADLXGPUMetricsListPtrgpuMetricsList;
res=perfMonitoringServices->GetGPUMetricsHistory(oneGPU,startMs,stopMs,&gpuMetricsList);

//DisplayalltheGPUmetricsinthelist
IADLXGPUMetricsPtrgpuMetrics;
for(inti=gpuMetricsList->Begin();i!=gpuMetricsList->End();++i)
{
res=gpuMetricsList->At(i,&gpuMetrics);
//DisplaytimestampandGPUmetrics
if(ADLX_SUCCEEDED(metricsSupportRet)&&ADLX_SUCCEEDED(res))
{
std::cout<<"ThecurrentGPUmetrics:"<<std::endl;
std::cout<<std::boolalpha;//Displaybooleanvariableastrueorfalse
GetTimeStamp(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUTotalBoardPower(gpuMetricsSupport,gpuMetrics);
ShowGPUIntakeTemperature(gpuMetricsSupport,gpuMetrics);
std::cout<<std::noboolalpha;
}
std::cout<<std::endl;
}

Sleep(1000);
}

//Stoptrackingperformancemetrics
res=perfMonitoringServices->StopPerformanceMetricsTracking();
if(ADLX_FAILED(res))
{
std::cout<<"Stoptrackingperformancemetricsfailed"<<std::endl;
}

MainMenu();
}

//DisplayhistoricalGPUMetrics
voidShowHistoricalGPUMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices,IADLXGPUPtroneGPU)
{
//Clearhistoricalperformancemetricsdata
ADLX_RESULTres=perfMonitoringServices->ClearPerformanceMetricsHistory();
if(ADLX_FAILED(res))
{
std::cout<<"Clearhistoricaldatafailed"<<std::endl;
return;
}

//Starttrackingperformancemetrics
res=perfMonitoringServices->StartPerformanceMetricsTracking();
if(ADLX_FAILED(res))
{
std::cout<<"Starttrackingperformancemetricsfailed"<<std::endl;
return;
}

//Waitfor10secondstoaccumulatemetrics
std::cout<<"Waitfor10secondstoaccumulatemetrics..."<<std::endl;
Sleep(10000);

//GetGPUmetricshistoryfrom10secondsago(10000ms:thesecondparameter)tothepresenttime(0ms:thethirdparameter)
IADLXGPUMetricsListPtrgpuMetricsList;
res=perfMonitoringServices->GetGPUMetricsHistory(oneGPU,10000,0,&gpuMetricsList);
if(ADLX_SUCCEEDED(res))
{
//GetGPUmetricssupport
IADLXGPUMetricsSupportPtrgpuMetricsSupport;
ADLX_RESULTres1=perfMonitoringServices->GetSupportedGPUMetrics(oneGPU,&gpuMetricsSupport);

//DisplayalltheGPUmetricsinthelist
IADLXGPUMetricsPtrgpuMetrics;
for(inti=gpuMetricsList->Begin();i!=gpuMetricsList->End();++i)
{
std::cout<<"**********historicalGPUmetrics"<<i+1<<":**********"<<std::endl;
ADLX_RESULTres2=gpuMetricsList->At(i,&gpuMetrics);
//DisplaytimestampandGPUmetrics
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
std::cout<<std::boolalpha;//Displaybooleanvariableastrueorfalse
GetTimeStamp(gpuMetrics);
ShowGPUUsage(gpuMetricsSupport,gpuMetrics);
ShowGPUClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAMClockSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUHotspotTemperature(gpuMetricsSupport,gpuMetrics);
ShowGPUPower(gpuMetricsSupport,gpuMetrics);
ShowGPUFanSpeed(gpuMetricsSupport,gpuMetrics);
ShowGPUVRAM(gpuMetricsSupport,gpuMetrics);
ShowGPUVoltage(gpuMetricsSupport,gpuMetrics);
ShowGPUTotalBoardPower(gpuMetricsSupport,gpuMetrics);
ShowGPUIntakeTemperature(gpuMetricsSupport,gpuMetrics);
std::cout<<std::noboolalpha;
}
std::cout<<std::endl;
}
}

//Stoptrackingperformancemetrics
res=perfMonitoringServices->StopPerformanceMetricsTracking();
if(ADLX_FAILED(res))
{
std::cout<<"Stoptrackingperformancemetricsfailed"<<std::endl;
return;
}
}
