#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IPerformanceMonitoring.h"
#include<iostream>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//Displaysystemmetricsrange
voidShowSystemMetricsRange(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices);

/*
*Iftheapponlyrequiresthecurrentmetricset,metricstrackingcanbeomittedinfavorofcallingthemoreefficientGetCurrentfunction.
*Iftheappalsorequiresmetricshistory,GetHistoryretrievestherequiredaccumulatedhistorybuffer,thatmayalsoincludethecurrentmetricset.
*/
//Displaycurrentsystemmetrics
voidShowCurrentSystemMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices);

//Displayhistoricalsystemmetrics
voidShowHistoricalSystemMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
//GetPerformanceMonitoringservices
IADLXPerformanceMonitoringServicesPtrperfMonitoringServices;
res=g_ADLXHelp.GetSystemServices()->GetPerformanceMonitoringServices(&perfMonitoringServices);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(perfMonitoringServices);
}
else
std::cout<<"\tGetperformancemonitoringservicesfailed"<<std::endl;
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
std::cout<<"\tChooseonefromthefollowingoptions"<<std::endl;

std::cout<<"\t->Press1todisplaythesystemmetricsrange"<<std::endl;
std::cout<<"\t->Press2todisplaythecurrentsystemmetrics"<<std::endl;
std::cout<<"\t->Press3todisplaythehistoricalsystemmetrics"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaythemainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displaysystemmetricsrange
case'1':
ShowSystemMetricsRange(perfMonitoringServices);
break;

//Displaycurrentmetrics
case'2':
ShowCurrentSystemMetrics(perfMonitoringServices);
break;

//Displayhistoricalmetrics
case'3':
ShowHistoricalSystemMetrics(perfMonitoringServices);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//Displaysystemmetricsrange
voidShowSystemMetricsRange(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices)
{
//Getsystemmetricssupport
IADLXSystemMetricsSupportPtrsystemMetricsSupport;
ADLX_RESULTres=perfMonitoringServices->GetSupportedSystemMetrics(&systemMetricsSupport);
if(ADLX_SUCCEEDED(res))
{
adlx_intminValue=0,maxValue=0;

//GetCPUusagerange
res=systemMetricsSupport->GetCPUUsageRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheCPUusagerangebetween"<<minValue<<"%and"<<maxValue<<"%"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportCPUusagerange"<<std::endl;

//GetsystemRAMrange
res=systemMetricsSupport->GetSystemRAMRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"ThesystemRAMrangebetween"<<minValue<<"MBand"<<maxValue<<"MB"<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportsystemRAMrange"<<std::endl;

//GetSmartShiftrange
res=systemMetricsSupport->GetSmartShiftRange(&minValue,&maxValue);
if(ADLX_SUCCEEDED(res))
std::cout<<"Thesmartshiftrangebetween"<<minValue<<"and"<<maxValue<<std::endl;
elseif(res==ADLX_NOT_SUPPORTED)
std::cout<<"Don'tsupportsmartshiftrange"<<std::endl;
}
}

//Displaythesystemtimestamp(inms)
voidGetTimeStamp(IADLXSystemMetricsPtrsystemMetrics)
{
adlx_int64timeStamp=0;
ADLX_RESULTres=systemMetrics->TimeStamp(&timeStamp);
if(ADLX_SUCCEEDED(res))
std::cout<<"Thesystemtimestampis:"<<timeStamp<<"ms"<<std::endl;
}

//ShowCPUusage(in%)
voidShowCPUUsage(IADLXSystemMetricsSupportPtrsystemMetricsSupport,IADLXSystemMetricsPtrsystemMetrics)
{
adlx_boolsupported=false;
//DisplayCPUusagesupportstatus
ADLX_RESULTres=systemMetricsSupport->IsSupportedCPUUsage(&supported);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"CPUusagesupportstatus:"<<supported<<std::endl;
if(supported)
{
adlx_doublecpuUsage=0;
res=systemMetrics->CPUUsage(&cpuUsage);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheCPUusageis:"<<cpuUsage<<"%"<<std::endl;
}
}
}

//DisplaysystemRAM(inMB)
voidShowSystemRAM(IADLXSystemMetricsSupportPtrsystemMetricsSupport,IADLXSystemMetricsPtrsystemMetrics)
{
adlx_boolsupported=false;
//DisplaysystemRAMusagesupportstatus
ADLX_RESULTres=systemMetricsSupport->IsSupportedSystemRAM(&supported);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"SystemRAMusagesupportstatus:"<<supported<<std::endl;
if(supported)
{
adlx_intsystemRAM=0;
res=systemMetrics->SystemRAM(&systemRAM);
if(ADLX_SUCCEEDED(res))
std::cout<<"ThesystemRAMis:"<<systemRAM<<"MB"<<std::endl;
}
}
}

//ShowSmartShift
voidShowSmartShift(IADLXSystemMetricsSupportPtrsystemMetricsSupport,IADLXSystemMetricsPtrsystemMetrics)
{
adlx_boolsupported=false;
//DisplaySmartShiftsupportstatus
ADLX_RESULTres=systemMetricsSupport->IsSupportedSmartShift(&supported);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"SmartShiftsupportstatus:"<<supported<<std::endl;
if(supported)
{
adlx_intsmartShift;
res=systemMetrics->SmartShift(&smartShift);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheSmartShiftis:"<<smartShift<<std::endl;
}
}
}

//Displaycurrentsystemmetrics
voidShowCurrentSystemMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices)
{
//Getsystemmetricssupport
IADLXSystemMetricsSupportPtrsystemMetricsSupport;
ADLX_RESULTres1=perfMonitoringServices->GetSupportedSystemMetrics(&systemMetricsSupport);

//Accumulateanddisplaycurrentmetricsforeachofthe10loops
IADLXSystemMetricsPtrsystemMetrics;
for(inti=0;i<10;++i)
{
//Clearscreen
system("cls");
//Getcurrentsystemmetrics
ADLX_RESULTres2=perfMonitoringServices->GetCurrentSystemMetrics(&systemMetrics);

//Displaytimestamp,CPUusage,systemRAM,andSmartShift
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
std::cout<<"Thecurrentsystemmetrics:"<<std::endl;
std::cout<<std::boolalpha;//Displaybooleanvariableastrueorfalse
GetTimeStamp(systemMetrics);
ShowCPUUsage(systemMetricsSupport,systemMetrics);
ShowSystemRAM(systemMetricsSupport,systemMetrics);
ShowSmartShift(systemMetricsSupport,systemMetrics);
std::cout<<std::noboolalpha;
}
Sleep(1000);
}

MainMenu();
}

//Displayhistoricalsystemmetrics
voidShowHistoricalSystemMetrics(IADLXPerformanceMonitoringServicesPtrperfMonitoringServices)
{
//Clearhistoricalperformancemetricsdata
ADLX_RESULTres=perfMonitoringServices->ClearPerformanceMetricsHistory();
if(ADLX_FAILED(res))
{
std::cout<<"Clearhistoricaldatafailed"<<std::endl;
return;
}

//Getcurrentmaxhistoricalsize(ins)
adlx_intmaxHistorySize=0;
res=perfMonitoringServices->GetMaxPerformanceMetricsHistorySize(&maxHistorySize);
if(ADLX_SUCCEEDED(res))
std::cout<<"Thecurrentmaxhistoricalsizeis:"<<maxHistorySize<<"s"<<std::endl;
//Gettheconfigurablemaximumhistoricalsizerange
ADLX_IntRangerange;
res=perfMonitoringServices->GetMaxPerformanceMetricsHistorySizeRange(&range);
if(ADLX_SUCCEEDED(res))
std::cout<<"Therangeofmaxhistoricalsizeis:("<<range.minValue<<","<<range.maxValue<<")s"<<std::endl;
//Changemaxhistoricalsizetominimumvalue
res=perfMonitoringServices->SetMaxPerformanceMetricsHistorySize(range.minValue);
if(ADLX_SUCCEEDED(res))
{
perfMonitoringServices->GetMaxPerformanceMetricsHistorySize(&maxHistorySize);
std::cout<<"Setmaxhistoricalsizetominimumvaluesuccessfully,thecurrentmaxhistoricalis:"<<maxHistorySize<<"s"<<std::endl;
}

//Getcurrentsamplinginterval(inms)
adlx_intsamplingInterval=0;
res=perfMonitoringServices->GetSamplingInterval(&samplingInterval);
if(ADLX_SUCCEEDED(res))
std::cout<<"Thecurrentsamplingintervalis:"<<samplingInterval<<"ms"<<std::endl;
//Getthesamplingintervalrange.Thesamplingintervalmustbesetwithinthisrange.
res=perfMonitoringServices->GetSamplingIntervalRange(&range);
if(ADLX_SUCCEEDED(res))
std::cout<<"Therangeofsamplingintervalis:("<<range.minValue<<","<<range.maxValue<<")ms"<<std::endl;
//Changesamplingintervaltominimumvalue
res=perfMonitoringServices->SetSamplingInterval(range.minValue);
if(ADLX_SUCCEEDED(res))
{
perfMonitoringServices->GetSamplingInterval(&samplingInterval);
std::cout<<"Setsamplingintervaltominimumvaluesuccessfully,thecurrentsamplingintervalis:"<<samplingInterval<<"ms"<<std::endl;
}

//Starttrackingperformancemetrics
res=perfMonitoringServices->StartPerformanceMetricsTracking();
if(ADLX_FAILED(res))
{
std::cout<<"Starttrackingperformancemetricsfailed"<<std::endl;
return;
}

//Waitfor10secondstoaccumulatemetrics
std::cout<<"Waitfor10secondstoaccumulatemetrics..."<<std::endl;
Sleep(10000);

//Checkthecurrentperformancemetricshistoricalsize(ins)uponaccumulation
adlx_intcurrentHistorySize=0;
res=perfMonitoringServices->GetCurrentPerformanceMetricsHistorySize(&currentHistorySize);
if(ADLX_SUCCEEDED(res))
std::cout<<"Thecurrenthistoricalsizeis:"<<currentHistorySize<<"s"<<std::endl;

//Getsystemmetricshistoryfrom10secondsago(10000ms:thefirstparameter)tothepresenttime(0ms:thesecondparameter)
IADLXSystemMetricsListPtrsystemMetricsList;
res=perfMonitoringServices->GetSystemMetricsHistory(10000,0,&systemMetricsList);
if(ADLX_SUCCEEDED(res))
{
//Getthesystemmetricssupport
IADLXSystemMetricsSupportPtrsystemMetricsSupport;
ADLX_RESULTres1=perfMonitoringServices->GetSupportedSystemMetrics(&systemMetricsSupport);

//Displayallthesystemmetricsinthelist
IADLXSystemMetricsPtrsystemMetrics;
for(inti=systemMetricsList->Begin();i!=systemMetricsList->End();++i)
{
std::cout<<"**********historicalsystemmetrics"<<i+1<<":**********"<<std::endl;
ADLX_RESULTres2=systemMetricsList->At(i,&systemMetrics);
//Displayfirstsystemmetricstimestamp,CPUusage,systemRAM,andSmartShift
if(ADLX_SUCCEEDED(res1)&&ADLX_SUCCEEDED(res2))
{
std::cout<<std::boolalpha;//Displaybooleanvariableastrueorfalse
GetTimeStamp(systemMetrics);
ShowCPUUsage(systemMetricsSupport,systemMetrics);
ShowSystemRAM(systemMetricsSupport,systemMetrics);
ShowSmartShift(systemMetricsSupport,systemMetrics);
std::cout<<std::noboolalpha;
}
std::cout<<std::endl;
}
}

//Stoptrackingperformancemetrics
res=perfMonitoringServices->StopPerformanceMetricsTracking();
if(ADLX_FAILED(res))
{
std::cout<<"Stoptrackingperformancemetricsfailed"<<std::endl;
return;
}
}
