{
{WFVR_ALWAYS_OFF,"Alwaysoff"},
{WFVR_OFF_UNLESS_APP_SPECIFIES,"Off,unlessapplicationspecifies"},
{WFVR_ON_UNLESS_APP_SPECIFIES,"On,unlessapplicationspecifies"},
{WFVR_ALWAYS_ON,"Alwayson"}};

//antiAliasingmode
staticconststd::map<ADLX_ANTI_ALIASING_MODE,constchar*>antiAliasingMode=
