{AA_MODE_USE_APP_SETTINGS,"Useapplicationsettings"},
{AA_MODE_ENHANCE_APP_SETTINGS,"Enhanceapplicationsettings"},
{AA_MODE_OVERRIDE_APP_SETTINGS,"Overrideapplicationsettings"}};

